css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Zen Familiar*/
/* i really want this to be global */
/*Images - pictures*/

#pet-headerPortrait-base {
    background: url("https://i.pinimg.com/originals/34/f3/e1/34f3e1ddcf3ef953be2f54b805e178a7.jpg") -15px top no-repeat !important;
    background-size: auto 125px !important;
}
#pet-portrait {
    background: url("https://68.media.tumblr.com/7795826f43d4d77b893d96343eae6252/tumblr_inline_ocoxumJwEx1qj7dh5_500.gif") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Infos*/

#pet-info-name:before {
    content: "Hyun Ryu" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: " Zen se a enamorado mas de ti";
}
#pet-info-racegender:before {
    content: "Humano" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.es/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 0px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Comida - food*/

#pet-info-food > img {
    background: url("https://fotos.subefotos.com/b0154183b50524f5717e393fc6ce4c01o.png") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
}
#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food:before {
    content: " Kebab de Cordero" !important;
    font-size: 16px !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}
/*Zen en tu perfil*/

#player-display-pet {
    background: url("https://1.bp.blogspot.com/-sURXoCJ5-Uk/WRDPVEzeE0I/AAAAAAAADBk/cqdgJKzNsqo-yzNA_liN1q-zMtEx7X5LwCLcB/s1600/zen%2Brender.png") no-repeat;
    background-size: auto 100%;
    height: 560px !important;
    width: 200px !important;
}
#player-display-pet > img {
    display: none !important;
}

img[src="/i18n/static/fr/logo.png"] {
    width: 0;
    height: 0;
    background: url("https://fotos.subefotos.com/fc5b6ed9f5a67436991df5cb90df39c2o.png");
    padding-left: 500px;
    padding-top: 205px;
    position: absolute;
    top: -0px;
    left: -70px;
}
