css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zhidao.baidu.com") {
/*百度知道
  横幅，右侧滚动栏，签到，知道日报，快看，分割线，右上角商城，分享五角星，底栏帮助,
  分割线话题，分割线标题，等你来答*/
  .nav-show-control,
  .jump-top-box,
  .line.user-info,
  .wgt-daily.mod-shadow,
  .cms-slide.mod-noshadow,
  .qbside-top-line,
  .shop-entrance,
  .others-share,
  .iknow-qb_home_icons.ad-leftdown-icon,
  .footer-wp,
  .text-chain-content,
  .text-chain-title,
  .wgt-push
  { display: none !important;}
  /*页面布局调整 */
  .line.qb-section + DIV
  { height:20px !important;}
  .qb-content 
  {width: 90% !important;
   padding: 50px !important; 
   margin-bottom: 20px !important;
   border-radius: 15px !important;
   box-shadow: 0 0 10px 0px rgba(0, 0, 0, .1) !important;}
}

@-moz-document url-prefix("https://zhidao.baidu.com/search") {
/*知道搜索页面 */ 
  .aside
  { display: none !important;}
  .newsearch .page-main-inner 
  { margin-left:10% !important;}
  .page-main-inner .list-wraper
  { margin-right:14% !important;
   float: none !important;}
  .list-inner,.list-header 
  { width: 100% !important;}
  .list-wraper .dt 
  {font-size: 1.6em !important;} 
  .list-wraper .dd 
  {font-size: 1.25em !important;
   line-height: 26px !important;}
  .widget-pager .pager
  { padding-left: 58px !important;
   transform:scale(1.3) !important;}
}

@-moz-document regexp("https?://zhidao.baidu.com/([?].*)?") {
/*知道首页 */
  #body,
  .nav-menu-container
  { display: none !important;}
  .search-box-new .logo 
  { float: none !important;
   margin:40px 0 40px 40% !important;
   transform:scale(1.3) !important;}
  .wgt-footer-new
  {position:fixed;
   bottom: 10px !important;
   width: 100% !important;}
}

@-moz-document domain("jingyan.baidu.com") {
/*百度经验
  横幅，右侧分享，aside，左侧点赞，热门杂志，帮助 */
  .wgt-common-nav,
  .wrp-exp-share,
  #aside,
  #wgt-like,
  .magzine-list,
  .s-tools
  { display: none !important;}
  /*页面布局调整 */
   #main-content
  { width: 90% !important;
    padding: 50px !important;
    margin: 30px 0 !important;
    border-radius: 10px !important;
    box-shadow: 0 0 10px 0px rgba(0, 0, 0, .1) !important;}
}

@-moz-document url-prefix("https://jingyan.baidu.com/search") {
/*经验搜索页面 */
  .top-search-box
  {padding-left: 80px !important;}
  .wgt-bread
  {padding-left: 130px !important;}
  .l-main-col 
  {padding-left: 80px !important;
   width: 86% !important;}
  .s-nav-tab 
  {margin-left: 0 !important;
   padding-left: 10px !important;}
  .search-list 
  {width: 100% !important;}
  .search-list dt a
  {font-size: 1.6em !important;} 
  .search-list .abstract,
  .search-list dd .cate,
  .search-list dt span.succ-times
  {font-size: 1.25em !important;
   line-height: 26px !important;}
  .search-pager .pg
  {padding-left: 52px !important;
   transform:scale(1.2) !important;}
}

@-moz-document regexp("https?://jingyan.baidu.com/([?].*)?") {
/*经验首页*/
  .nav,
  #wgt-banner,
  #exp-container,
  #wgt-hot-exp, 
  #wgt-aside,
  #wgt-magazine,
  #category-list,
  .wgt-category
  { display: none !important;} 
  .search-box ul.channel 
  {padding:5px 0 0 100px !important;} 
  .top-search-form 
  {height: 200px !important;
   padding:160px 0 0 100px !important;}
  .search-box a.logo
  { float: none !important;
    left: 500px !important;
    bottom: 250px !important;
    transform:scale(1.3) !important;}
  .home-body
  {position:fixed !important;
   bottom: 0px !important;
   width: 100% !important;}
  .page-home .main-wrapper 
  {margin-top:0px !important;}
}

@-moz-document domain("www.zybang.com") {
/*百度作业帮
  app下载，下载，右侧栏，出错页面下载*/
  .app-download,
  #QRinside,
  .question-view .aside,
  #downBtn
  { display: none !important;}
  /*页面布局调整 */
  .qb_wgt-navbar .user-center 
  { position: static !important;}
  .question-view .main-con 
  {width: 90% !important;
   padding: 40px !important;
   margin: 0 !important;
   border-radius: 10px !important;
   box-shadow: 0 0 10px 0 rgba(0, 0, 0, .1) !important;}
}
