css:
@-moz-document domain("soundcloud.com") {
/* variables */
:root {
    --black: #051519;
    --white: #fff;
    --grey: #0b2e37;
    --light-grey: #d9e1e3;
    --a1: #92d3a2;
    --a2: #f8818e;
    --a2s: #f55165;
    --a3: #31658C;
    --a4: #FFBB6F;
    --shadows: 0px 2px 10px 1px rgba(0, 0, 0, 0.25);
}


/* hide */
.dashbox__wrapper, .mobileApps, .l-footer.standard, .stream__header, .headerSearch__submit {
    display: none;
}


/* selection colors */
::selection {
    background: var(--a4);
}

body, .sc-button-small, .sc-button-small:not(.sc-button-cta), .soundContext__line, .streamExploreTabs {
	font-family: 'Proxima Nova Medium', sans-serif;
}

.sc-button {
	border: 0;
}

a.sc-link-light, .soundTitle.streamContext .soundTitle__title, .sound__waveform, .sc-type-light, .sc-ministats, .playbackSoundBadge__titleLink, .playbackSoundBadge__titleLink:visited {
	color: var(--black);
}

.sc-tag, .sc-tag:visited, .sc-ministats-small, a.sc-ministats {
	background: none;
    color: var(--grey);
    border: 0;
}

.playControls__inner, .playControls__bg {
	background: var(--black);
    border-color: var(--black);
}

.skipControl__previous {
	background-image: url('https://x.eti.tf/panY7.svg');
}

.playControl.playing {
	background-image: url('https://x.eti.tf/nRnB4.svg');
}

.skipControl__next {
	background-image: url('https://x.eti.tf/TPlST.svg');
}

.shuffleControl::before {
	background-image: url('https://x.eti.tf/O1a0U.svg');
}

.shuffleControl::after {
	background-image: url('https://x.eti.tf/kQ3by.svg');
}

.repeatControl::after {
	background-image: url('https://x.eti.tf/Bnt1g.svg');
}

.repeatControl.m-none {
    background-image: url('https://x.eti.tf/Dmv2X.svg');
}

.repeatControl::before {
    background-image: url('https://x.eti.tf/2rA6P.svg');
}

.playbackTimeline__progressBackground {
	background: var(--grey);
}

.playControl {
    background-image: url('https://x.eti.tf/QL4IX.svg');
}

.sc-button-active:hover, .sc-button-active:focus, .sc-button-active.sc-button-focus, .sc-button-selected:hover, .sc-button-selected:focus, .sc-button-selected.sc-button-focus {
	color: var(--a2);
    border: 0;
}

.g-tabs-large {
    text-transform: uppercase;
}

div#app {
	background-image: url('https://x.eti.tf/wA9wA.png');
}

div#content {
	background: var(--white);
}

.header {
    background: none;
    font: 600 14px/1.7 "Proxima Nova", sans-serif;
}

.header__navMenu>li>a, .header__link, .userNav__usernameButton {
	padding-top: 15px;
}

.userNav__activitiesButton, .userNav__messagesButton {
	padding-top: 4px;
}

#app > header > div > div.header__right.sc-clearfix > ul {
	padding-top: 2px;
    padding-right: 3px;
}

.headerSearch {
	padding-top: 11px;
}

.header__inner {
    background: var(--black);
}

.sc-input {
	border-radius: 0;
}

.header__logoLink {
	height: 50px;
    background-position: center;
}

.sc-button-active, .playbackTimeline__timePassed, .sc-button-active, .sc-button-selected, .moreMenu__link.moreMenu__goUpsell, .g-required-label:after, .soundBadge.active .soundTitle__title {
    color: var(--a2);
}

.header__logo, .playButton.m-stretch, .playButton.m-stretch:hover, .playButton.m-stretch:focus, .sc-button-play, .sc-button-pause, .sc-button-prev, .sc-button-next, .sc-button.sc-button-blocked, .sc-button.sc-button-play:disabled, .sc-button.sc-button-play:disabled:hover, .sc-button.sc-button-play.sc-button-disabled:disabled, .sc-toggle:before, .sc-button-cta, .sc-button-cta:hover, .sc-button-cta:focus, .sc-button-cta:visited, .sc-button.sc-button-white:hover, .sc-button.sc-button-white:focus, .playbackTimeline__progressBar {
    background: var(--a2);
}

.inboxItem.active {
	border-color: var(--a2);
}

.sc-button-play:hover, .sc-button-play:focus, .sc-button-play.sc-button-focus, .sc-button-pause:hover, .sc-button-pause:focus, .sc-button-pause.sc-button-focus, .sc-button-prev:hover, .sc-button-prev:focus, .sc-button-prev.sc-button-focus, .sc-button-next:hover, .sc-button-next:focus, .sc-button-next.sc-button-focus {
	background: var(--a2s);
}

.g-tabs-link.active {
    color: var(--a2);
    border-color: var(--a2);
}

.sc-status-icon {
    background-image: url('https://x.eti.tf/YREch.svg');
}

.sc-button-small.sc-button-selected.sc-button-like:before, .sc-button-small.sc-button-selected.sc-button-like.sc-button-lightfg:before, .sc-button-small.sc-button-selected.sc-button-like.sc-button-visual:before, .sc-button-selected.sc-button-medium.sc-button-like:before {
    background-image: url('https://x.eti.tf/IFxK4.svg')
}

.sc-icon-large.sc-icon-follower {
	background-image: url('https://x.eti.tf/hBSSC.svg');
}

.whoToFollowModule .sidebarHeader__more {
	background-image: url('https://x.eti.tf/jsemc.png');
}

.sc-button-small.sc-button-selected.sc-button-more:before, .sc-button-small.sc-button-active.sc-button-more:before {
	background-image: url('https://x.eti.tf/FVr3L.svg');
}

.sc-ministats-medium.sc-ministats-plays:before {
	background-image: url('https://x.eti.tf/eJVbT.svg');
}

.sc-ministats-medium.sc-ministats-likes:before {
    background-image: url('https://x.eti.tf/9gw3L.svg');
}

.sc-ministats-medium.sc-ministats-reposts:before {
    background-image: url('https://x.eti.tf/Hx5Ez.svg');
}

.sc-icon-large.sc-icon-like, .sc-button-selected.sc-button-medium.sc-button-like:active:before, .sc-button-selected.sc-button-medium.sc-button-like:focus:before {
	background-image: url('https://x.eti.tf/IFxK4.svg');
}

.sc-icon-large.sc-icon-calendar {
    background-image: url('https://x.eti.tf/0LG8G.svg');
}

.sc-ministats-small.sc-ministats-followers:before {
    background-image: url('https://x.eti.tf/7fzv1.svg');
}

.sc-ministats-small.sc-ministats-sounds:before {
    background-image: url('https://x.eti.tf/MV4eg.svg');
}

.sc-ministats-small.sc-ministats-plays:before {
    background-image: url('https://x.eti.tf/QCtI8.svg');
}

.sc-ministats-small.sc-ministats-likes:before {
    background-image: url('https://x.eti.tf/seNtq.svg');
}

.sc-ministats-small.sc-ministats-reposts:before {
    background-image: url('https://x.eti.tf/dV4uW.svg');
}

.sc-ministats-small.sc-ministats-comments:before {
    background-image: url('https://x.eti.tf/eW993.svg');
}

.g-type-shrinkwrap-inline, .g-type-shrinkwrap-inline:hover, .g-type-shrinkwrap-inline:visited, .g-type-shrinkwrap-block, .g-type-shrinkwrap-block:hover, .g-type-shrinkwrap-block:visited {
	background: var(--black);
}

.sc-button-selected.sc-button-medium.sc-button-more:before, .sc-button-active.sc-button-medium.sc-button-more:before {
    background-image: url('https://x.eti.tf/FVr3L.svg');
}

.sc-button-dropdown:not(.sc-button-disabled):not(:disabled), .linkMenu__activeItem, .linkMenu__activeItem>a, .linkMenu__item:hover, .linkMenu__item>a:hover {
	border: 0;
    color: var(--a2);
}

.sc-button-dropdown.sc-button-active:not(.sc-button-dropdown-plain), .sc-button-dropdown:hover:not(.sc-button-dropdown-plain) {
	background: var(--a2);
    color: var(--white);
}

.sceneLayer {
    filter: grayscale(1) brightness(1);
}

.sc-button-dropdown:after, .soundGallery__sliderPeek:hover .soundGallery__sliderButton, .soundGallery__sliderButton:focus, .soundGallery__sliderButton:active, .soundGallery__sliderPeek:hover .soundGallery__sliderButton:after, .soundGallery__sliderButton:focus:after, .soundGallery__sliderButton:active:after {
	border-color: var(--a2);
}

.playbackTimeline__progressHandle {
	border-color: var(--a2);
    background-color: var(--a2);
}

.playbackTimeline__duration {
	color: var(--white);
}

.volume[data-level='6'] .volume__button, .volume[data-level='7'] .volume__button, .volume[data-level='8'] .volume__button, .volume[data-level='9'] .volume__button, .volume[data-level='10'] .volume__button {
	background-image: url('https://x.eti.tf/Nywzx.svg');
}

.volume[data-level='1'] .volume__button, .volume[data-level='2'] .volume__button, .volume[data-level='3'] .volume__button, .volume[data-level='4'] .volume__button, .volume[data-level='5'] .volume__button {
	background-image: url('https://x.eti.tf/AeaaL.svg');
}

.volume.muted .volume__button {
	background-image: url('https://x.eti.tf/TrTpi.svg');
}

.volume__sliderWrapper {
	border: 0;
}

#app > div.playControls.g-z-index-header.m-visible > div > div > div.playControls__elements > div.playControls__soundBadge > div > div.playbackSoundBadge__titleContextContainer > a, #app > div.playControls.g-z-index-header.m-visible > div > div > div.playControls__elements > div.playControls__soundBadge > div > div.playbackSoundBadge__titleContextContainer > div > a {
	color: var(--white) !important;
}

.sc-button-small.sc-button-like:before {
	background-image: url('https://x.eti.tf/K4tXH.svg');
}

.playbackSoundBadge__showQueue {
	content: url('https://x.eti.tf/txLyd.svg');
}

.commentPopover__username, a.commentPopover__username:hover, a.commentPopover__username:visited {
	color: var(--a2);
}

.g-dark a, .g-dark .sc-type-light {
	color: var(--light-grey)
}

.sc-icon-large.sc-icon-sound {
	background-image: url('https://x.eti.tf/BSOja.svg');
}

.sc-icon-large.sc-icon-set {
	background-image: url('https://x.eti.tf/CZGK3.svg');
}

.commentPopover__username:before {
	background-image: linear-gradient(var(--a2),rgba(255,255,255,0));
}

.trackItem:not(.m-disabled).active .trackItem__trackTitle, .trackItem:not(.m-disabled).active .trackItem__content {
	color: var(--a2);
}

.soundTitle.m-visualSound.streamContext .soundTitle__title {
	color: var(--white);
}

.soundTitle__uploadTime {
	color: var(--a2);
}

.chartTrack__missingScore, .chartTrack__scoreAllPlays {
	color: var(--black);
}

.sc-button-small.sc-button-icon:not(.sc-button-stretch-icon), .sc-button-small.sc-button-responsive:not(.sc-button-stretch-icon) {
	font-weight: normal;
}

.sc-orange {
	color: var(--a2);
}

.compactTrackListItem.active, .compactTrackListItem.active .compactTrackListItem__number, .compactTrackListItem.active .compactTrackListItem__content, .compactTrackListItem.active .compactTrackListItem__trackTitle, .compactTrackListItem.active .compactTrackListItem__user, .compactTrackListItem.active .compactTrackListItem__plays {
	color: var(--a2);
}

.header__logoLink:focus {
	background-color: var(--black);
}

.volume__sliderProgress, .volume__sliderHandle {
	background: var(--a2);
}
}
