body.hp { background-color:#eee; }
body.hp td, body.hp a, body.hp p, body.hp .h { font-family: "Yantramanav"; }
body.hp #lga { display: none; }
body.hp #gs_st0 { filter: grayscale(100%); }
body.hp .sbibod { border-radius: 40px; transition: .2s; }
body.hp .sfibbbc { z-index: 1; }
body.hp .lst-c { border-radius: 40px; padding-left: 6px; }
body.hp div.sfibbbc::before { content: "Google"; font-family: "Pacifico"; font-size: 20px; color: #ccc; position: absolute; z-index: 2; margin-left: 18px; transition: .2s; }
body.hp div.sfibbbc:hover::before { color: #999; }
body.hp input#lst-ib.gsfi { font-family: "Yantramanav"; font-size: 1.3em; border-radius: 10px; text-indent: 70px; }
body.hp .sbhcn { transform: scale(1.01); transition: .2s; }
body.hp .sbfcn { transform: scale(1.01); }
body.hp li.sbsb_c.gsfs { font-family: "Yantramanav"; }
body.hp div.sbdd_b { border-radius: 5px; }
body.hp div.sbsb_a { padding: 35px 0 10px 0; border-radius: 10px; }
body.hp div.gstl_0.sbdd_a { z-index: -1; margin-top: -30px; transform: scale(.95); }
body.hp .sbsb_g { display: none; }
body.hp #sbfblt { display: none; }
body.hp div.jsb { display: none; }
body.hp #gb { max-width: 160px; background-color: #fff; padding: 6px; border-top-left-radius: 40px; border-bottom-left-radius: 40px; border: 1px solid #ddd; box-shadow: 0 1px 1px rgba(0, 0, 0, 0.12), 0 1px 1px rgba(0, 0, 0, 0.24); }
body.hp div.gb_ce.gb_R.gb_tg.gb_kg { display: none; }
body.hp #gb#gb a.gb_P, #gb#gb span.gb_P { font-size: 15px; font-weight: 700; transition: .2s; }
body.hp #gb#gb a.gb_P:hover, #gb#gb span.gb_P:hover { text-decoration: none; font-weight: 900; transition: .2s; }
body.hp div.fbar { font-style: italic; }
