css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("cv.uoc.edu") {

/* Módulo avisos */
  #announcement_modul {
    margin-bottom: 5px !important;
  }

  /* Widgets */
  /*************************************
  Cambio tipografía a Arial 
  *************************************/
  .card--announcement p, 
  h4.aula-tit-secc,
  .flexbox-layout h2.modul__title,
  ul.list--unstyled a.link,
  #widgetHeading a.title,
  h4.widget__title,
  h4 a.btnlink,
  .widget__marcadors h5,
  .aula-item a.link,
  .aula-data span,
  #widget a.btn{
    font-family: Arial!important;
  }
  
  
  /**************************************
  Cambio tamaño de texto 
  **************************************/
  .card--announcement p,
  .flexbox-layout h2.modul__title
  {
    font-size: 12px!important;
  }
  
  .widget__marcadors h5,
  .list--unstyled a.link,
  h4.aula-tit-secc,
  h4.aula-tit-secc a.link,
  h5.aula-tit-col a.link,
  .aula-data span span,
  .widget a.btn,
  .widget a.btn span.icon,
  .flexbox-layout ul.modul__actions span.icon,
  #widgetHeading a.title, h4.widget__title, h4 a.btnlink,
  span.aula-profile-links a.btnlink span.icon--send-mail{
    font-size: 12px!important;
  } 
  .flexbox-layout .modul__actions a span icon{
    font-size: 5px!important;
  }
  .widget__marcadors .marcadors span,
  ul.list--unstyled .marcadors span{
    font-size: 10px;
  }
  .flexbox-layout button.btn--primary > span.icon,
  #widgetToggle.btn span.icon{
    font-size: 7px!important;
  }
  dt.accordion__heading > a {
  	font-size: 14px;                     
  }
  dt.accordion__heading > a > span.marcadors span, dt.accordion__heading > a.accordion__toggle > span.icon {
    font-size: 10px;  
  }

  /**************************************
  Cambio tamaño de distancias
  **************************************/ 
  .flexbox-layout h2.modul__title{
    padding-left: 5px!important;
    width: 81.5%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
  .flexbox-layout button.btn--primary {
    height: 5px!important;
  } 
  .flexbox-layout ul.modul__actions{
    top: 0 !important;
    padding-bottom: 0px;
  } 
  .flexbox-layout .modul__header {
    margin-bottom: 0px;
  }
  #widgetHeading {
    padding: 0!important;
  }
  #widgetHeading a.title, h4.widget__title, h4 a.btnlink {
    padding-bottom: 0px;
  }
  .widget__header {
    padding: 0 5px 0 10px!important;
  }
  .widget__header .media {
    padding: 5px 0 0 0;
  }
  .list--tablist li > a{
    padding:2px 0;
  }
  .list--tablist li .marcadors {
    top:3px;
  }
  h4.aula-tit-secc{
    padding: 4px 10px!important;
  }
  #widget.widget a.btn.p-y {
    padding-top: 0!important;
    padding-bottom: 0!important;
  }
  #widget.widget a.btn{
    min-height: 0px!important;
  }
  #divMaximizedPart .aula-item {
    margin-bottom: 0;
  }
  #widgetToggle.btn--xsmall {
    min-height: 16px;
    overflow: hidden;
    background-color: white;
    color: #000078;
    border-top: 1px solid #000078;
  }
  .widget__toggle {
    margin-top: 0;
  }
  h5.aula-tit-col {
    padding: 0 10px 0 10px;
  }
  .aula-data, h5.aula-tit-col {
    padding:0 10px!important;
  }
  #tab_favorites.tab, #tab_all.tab {
  	padding: 0 10px;                     
  }
  dt.accordion__heading > a {
  	padding-top: 5px !important;
    padding-bottom: 5px !important;
  }
  dt.accordion__heading > a > span.marcadors span {
    margin: 0 10px; 
  }

  .flexbox-layout .modul {
    border: 1px solid #000078;
    margin-bottom: 5px;
  }
}
