css:
@-moz-document domain("edupoint.com") {
.info_box_icon {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/information.png") !important;
}

/* Home */

img[alt="click to see teacher comment"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/balloon-ellipsis.png") !important;
    margin-right: .2rem;
}

img[alt="tch comment"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/bonus/icons-shadowless-24/balloon.png") !important;
}

.mail {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/mail.png") !important;
}

.mail[alt="click to send email"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/mail--pencil.png") !important;
}

/* Messages */

.MsgUnreadIcon {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/bonus/icons-shadowless-24/mail.png") !important;
}

.MsgReadIcon {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/bonus/icons-shadowless-24/mail-open.png") !important;
}

.MsgDeleteIcon {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/bonus/icons-shadowless-24/cross.png") !important;
}

/* Calendar */

img[alt="Today's Calendar Icon"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/calendar-day.png") !important;
}

img[alt="Left Arrow"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/arrow-180.png") !important;
    margin-right: .2rem;
}

img[alt="Right Arrow"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/arrow.png") !important;
    margin-left: .2rem;
}

img[alt="Daily Calendar Icon"],
img[alt="Weekly Calendar Icon"],
img[alt="Monthly Calendar Icon"] {
    margin-left: .1rem !important;
    margin-right: .1rem !important;
}

img[alt="Daily Calendar Icon"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/calendar-select.png") !important;
}

img[alt="Weekly Calendar Icon"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/calendar-select-week.png") !important;
}

img[alt="Monthly Calendar Icon"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/calendar-select-month.png") !important;
}

img[alt="Event Icon"][src="images/PXP/assignment.png"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/report-paper.png") !important;
}

/* Class Websites */

img[alt="PDF Icon"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/document-pdf.png") !important;
}

/* Attendance */

img[alt="Excused"],
img[alt="Total Excused"],
img[alt="Excused Arrival/Dismissal"],
img[alt="Total Excused Arrivals/Dismissals"],
img[alt="Unexcused Arrival/Dismissal"],
img[alt="Total Unexcused Arrivals/Dismissals"],
img[alt="Unexcused"],
img[alt="Total Unexcused"],
img[alt="Activity"],
img[alt="Total Activities"] {
    margin-left: .2rem !important;
    margin-right: .2rem !important;
}

img[alt="Excused"],
img[alt="Total Excused"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/tick-circle.png") !important;
}

img[alt="Excused Arrival/Dismissal"],
img[alt="Total Excused Arrivals/Dismissals"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/clock--plus.png") !important;
}

img[alt="Unexcused Arrival/Dismissal"],
img[alt="Total Unexcused Arrivals/Dismissals"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/clock--minus.png") !important;
}

img[alt="Unexcused"],
img[alt="Total Unexcused"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/cross-circle.png") !important;
}

img[alt="Activity"],
img[alt="Total Activities"] {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/flag-green.png") !important;
}

/* Report Card */

img.REPORT_CARD_IMG {
    content: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/bonus/icons-shadowless-24/document-text.png") !important;
}

/* Tabs, Topnav, Footer, Sidenav */

td[class^="tab_"] a[href="Home_PXP.aspx"]::before,
.topnav[href="Login_Student_PXP.aspx?Logout=1"]::before,
#footer a[href="Login_Student_PXP.aspx?Logout=1"]::before,
.topnav[href="Help_PXP.aspx"]::before,
#footer a[href="Privacy_PXP.aspx"]::before,
#sidenav a[href="PXP_Messages.aspx?AGU=0"]::before,
#sidenav a[href="PXP_Calendar.aspx?AGU=0"]::before,
#sidenav a[href="PXP_Attendance.aspx?AGU=0"]::before,
#sidenav a[href="PXP_ClassSchedule.aspx?AGU=0"]::before,
#sidenav a[href="PXP_ClassWebsite.aspx?AGU=0"]::before,
#sidenav a[href="PXP_Conference.aspx?AGU=0"]::before,
#sidenav a[href="PXP_CourseHistory.aspx?AGU=0"]::before,
#sidenav a[href="PXP_CourseRequest.aspx?AGU=0"]::before,
#sidenav a[href="PXP_Gradebook.aspx?AGU=0"]::before,
#sidenav a[href="PXP_Grades.aspx?AGU=0"]::before,
#sidenav a[href="PXP_SchoolInformation.aspx?AGU=0"]::before,
#sidenav a[href="PXP_Student.aspx?AGU=0"]::before,
#sidenav a[href="PXP_DigitalLocker.aspx?AGU=0"]::before {
    content: "\00a0";
    display: inline-block;
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    width: 1rem;
    height: 1rem;
    margin-right: .2rem;
}

/* Tabs, Topnav, Footer */

td[class^="tab_"] a[href="Home_PXP.aspx"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/home.png");
}

.topnav[href="Login_Student_PXP.aspx?Logout=1"]::before,
#footer a[href="Login_Student_PXP.aspx?Logout=1"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/door-open-out.png");
}

.topnav[href="Help_PXP.aspx"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/question.png");
}

#footer a[href="Privacy_PXP.aspx"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/lock.png");
}

/* Sidenav */

#sidenav a[href="PXP_Messages.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/mail.png");
}

#sidenav a[href="PXP_Calendar.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/calendar.png");
}

#sidenav a[href="PXP_Attendance.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/calendar-task.png");
}

#sidenav a[href="PXP_ClassSchedule.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/clock.png");
}

#sidenav a[href="PXP_ClassWebsite.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/globe.png");
}

#sidenav a[href="PXP_Conference.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/user-business.png");
}

#sidenav a[href="PXP_CourseHistory.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/clock-history.png");
}

#sidenav a[href="PXP_CourseRequest.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/clock--exclamation.png");
}

#sidenav a[href="PXP_Gradebook.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/document-task.png");
}

#sidenav a[href="PXP_Grades.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/document-attribute.png");
}

#sidenav a[href="PXP_SchoolInformation.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/address-book.png");
    width: .9rem;
    height: .9rem;
}

#sidenav a[href="PXP_Student.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/information.png");
}

#sidenav a[href="PXP_DigitalLocker.aspx?AGU=0"]::before {
    background-image: url("https://raw.github.com/yusukekamiyamane/fugue-icons/master/icons-shadowless/safe.png");
}
}
