css:
@-moz-document domain(youtube.com) {

.ytd-page-manager,
.ytd-masthead,
#guide-content.ytd-app {
    background: #222 !important;  
    color: #DDD !important;
}


/* ==== Nav header ==== */
ytd-searchbox[mode="legacy"] #container.ytd-searchbox {
    background-color: transparent;
    box-shadow: none;
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom-color: #666;
}

#search-icon-legacy.ytd-searchbox {
    background-color: #333;
    border-color: #666;
    border-top: none;
    border-left: none;
}

ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:hover {
    background-color: #555;
    border-color: #444;
}

ytd-searchbox #search-input,
ytd-searchbox[mode="legacy"] #container.ytd-searchbox input.ytd-searchbox {
    color: #DDD;
}


/* ==== END Nav header ==== */

/* typeahead results */
.sbdd_b {
    border-color: #222;
}

.sbsb_a {
    background-color: #222;
}

ul.sbsb_b {
    background: #222;
}

.sbqs_c {
    color: #DDD;
}

.sbsb_c.gsfs.sbsb_d {
    color: #AAA;
    background-color: #444;
}


/* ==== END typeahead results ==== */
#label-icon.yt-dropdown-menu, /* 3-dots context menu */
#vote-count.ytd-comment-action-buttons-renderer,
ytd-topbar-menu-button-renderer #button.ytd-topbar-menu-button-renderer, /* topbar menu */
ytd-notification-topbar-button-renderer #button.ytd-notification-topbar-button-renderer, /* notifications */
#guide-icon.ytd-masthead, /* burger icon */
#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox, /* magnifier icon */
yt-icon.ytd-badge-supported-renderer, /* "verified" badge */
ytd-toggle-button-renderer #button.ytd-toggle-button-renderer {
    color: #BBB;
    fill: #EEE;
}

/* thumbs-up active */
ytd-toggle-button-renderer #button.ytd-toggle-button-renderer.style-default-active
{
    color: #2793E6;
    fill: #2793E6;
}

paper-button.ytd-subscribe-button-renderer,

/* subscribe button */
paper-button.ytd-subscribe-button-renderer[subscribed],

/* subscribe button -- subscribed */
paper-button.ytd-subscribe-button-renderer[subscribed] .deemphasize.yt-formatted-string {
    color: #FFF;
    background-color: #2793E6;
}

.ytd-compact-video-renderer,
.ytd-video-meta-block,
.ytd-video-primary-info-renderer,
.ytd-button-renderer,
#button.ytd-menu-renderer yt-icon.ytd-menu-renderer,
ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer,
.date.ytd-video-secondary-info-renderer,
.description.ytd-video-secondary-info-renderer yt-formatted-string.ytd-video-secondary-info-renderer,
yt-formatted-string.ytd-comments-header-renderer,
#simplebox-placeholder.ytd-comment-simplebox-renderer,
#icon-label.yt-dropdown-menu,
#content-text.ytd-comment-renderer,
#metadata-line.ytd-grid-video-renderer,
button.ytd-masthead[is="paper-icon-button-light"],
#video-title.ytd-child-video-renderer,
#length.ytd-child-video-renderer,
span.yt-view-count-renderer,
#byline.ytd-playlist-panel-video-renderer /* playlist thumbnail channel */
{
    color: #DDD;
}


/* ==== Titles, bold text and URLs ==== */
#byline.ytd-video-meta-block,
a.yt-simple-endpoint.yt-formatted-string,
.content.ytd-metadata-row-renderer,
#author-text.yt-simple-endpoint.ytd-comment-renderer,
.align-by-text.ytd-comment-replies-renderer,
span.more-button.ytd-comment-renderer,
#upnext.ytd-compact-autoplay-renderer,
#video-title.yt-simple-endpoint.ytd-grid-video-renderer,
#title.ytd-shelf-renderer,
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer,
#toggle.ytd-grid-renderer,
#video-title.ytd-video-renderer, /* search page video title */
#result-count.ytd-search-sub-menu-renderer, /* search submenu */
h3.ytd-compact-radio-renderer,
h3.ytd-radio-renderer, /* playlist title */
.title.ytd-video-primary-info-renderer,
h4.ytd-playlist-panel-video-renderer /* playlist thumbnail title */
{
    color: #FFF;
}


/* ==== Show more button ==== */
paper-button.yt-next-continuation {
    background-color: #111;
    color: #AAA;
}


/* ==== Playlists ==== */

/* == Sidebar == */
.header.ytd-playlist-panel-renderer {
    background-color: #222;
}

#items.iron-list /* video list */
{
    background-color: #444;
}

.index-message.ytd-playlist-panel-renderer, /* playlist index */
a.yt-simple-endpoint.yt-formatted-string:hover /* playlist channel */
{
    color: #DDD !important;
}

}
