css:
@-moz-document domain("www.youtube.com") {
#page,
#masthead,
#guide-wrapper #guide-content,
#search-form,
#search.style-scope.ytd-masthead,
#container,
button#search-icon-legacy,
#channel-header,
#channel-title,
#subscriber-count,
#tabs-inner-container,
#tabs, 
ytd-browse[page-subtype=home],
ytd-browse[page-subtype=channels],
ytd-playlist-sidebar-renderer,
ytd-browse.ytd-page-manager,
#trigger, #label,
paper-button,
#top,
.view-count,
.date,
#description,
.ytd-metadata-row-renderer,
#contents,
ytd-alert-renderer,
.ytd-badge-supported-renderer,
.yt-dropdown-menu,
yt-confirm-dialog-renderer,
.ytd-active-account-header-renderer,
paper-item,
ytd-simple-menu-header-renderer,
#yt-masthead-container,
#creator-page-sidebar,
.account-container,
select, option,
#body-container,
#footer-container,
#upload-prompt-box,
#upload-hub-footer,
.upload-widget,
button,
#masthead-search-term,
.upload-sidebar-header,
.upload-option-description,
.upload-option-text,
.yt-uix-button,
#yt-masthead-notifications-clickcard,
#yt-masthead-notifications-title,
#yt-masthead-notifications-content .feed-item-container,
.yt-lockup-content,
.yt-uix-tile-link {
    background-color: #212121 !important;
    color: #ccc !important;
}




#text,
#title,
#video-title,
#thumbnail-attribution,
#metadata-line,
#content-text,
yt-icon,
yt-formatted-string,
#author-text,
.count-text,
#icon-label,
#simplebox-placeholder,
.title,
.more-button,
.ytd-badge-supported-renderer,
.yt-formatted-string,
.ytd-sub-feed-option-renderer,
label,
.logo-container,
.yt-uix-button-icon {
    color: #ccc !important;
}

paper-tab.iron-selected {
    color: white !important;
}

#guide-icon, #youtube-paths path {
    fill: #ccc !important;
}

ytd-searchbox[mode="legacy"] #container.ytd-searchbox,
button#search-icon-legacy {
    border: 1px solid #aaa !important;
    box-shadow: none;
}

button#search-icon-legacy yt-icon {
    color: #ccc !important;
}

#search.style-scope.ytd-masthead {
    border: none;
}

#logo * {
    color: white !important;
}

#guide-wrapper #guide-content {
    background-color: #212121;
    color: #ccc;
}

.title.style-scope.ytd-guide-entry-renderer {
    color: #ccc;
}


    
}
