css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vaughnlive.tv")
{

  DIV#chatboxMain.CV3wrapper
  {
    background: #000000 !important;
    color: #ffffff !important;
  }

  body
  {
    background: #000000 !important;
  }

  body#vn_body
  {
    background: #000000 !important;
  }

  p.chatTagCaster
  {
    background: #ffdc00 !important;
    color: #2a2a2a !important;
  }

  p.chatTagMod
  {
    background: #87ff29 !important;
    color: #2a2a2a !important;
  }

  .chat_vaughn_wrapper
  {
    background: #2a2a2a !important;
    color: #ffffff !important;
  }

  span.chatLine
  {
    color: #ffffff !important;
  }

  a.chat_url
  {
    color: #ffffff !important;
    border-bottom: 1px solid 5b5a5a !important;
  }

  a.chat_url:hover
  {
    color: #ffffff !important;
    border-bottom: 1px solid 5b5a5a !important;
  }

  a.chat_url:visited
  {
    color: #ffffff !important;
    border-bottom: 1px solid 5b5a5a !important;
  }

  DIV.CV3setmoney
  {
    display: none !important;
  }

  TEXTAREA#vn_chat_message.CV3input
  {
    background: #2a2a2a !important;
    color: #ffffff !important;
  }

  #vn_chat_box
  {
    color: #ffffff !important;
    background: #2a2a2a !important;
  }

  div.CV3input
  {
    background-color: #2a2a2a !important;
  }

  div.CV3dosend
  {
    background: #87ff29 !important;
  }

  div.noticeLine
  {
    background: #2a2a2a !important;
    color: #ffff00 !important;
  }

  div.CV3pic
  {
    background: #2a2a2a !important;
  }

  div.CV3userboxContainer
  {
    background: #2a2a2a !important;
  }

  div.modalv3_right
  {
    background: #2a2a2a !important;
  }

  div.modalv3_status
  {
    color: #ffffff !important;
    background: #2a2a2a !important;
    border-bottom: 0px solid #ffffff !important;
  }

  div.CV3userbox
  {
    background-color: #2a2a2a !important;
  }

  div.CV3overlayLoading
  {
    background: #2a2a2a !important;
  }

  div.chatOptColLeft
  {
    background: #2a2a2a !important;
  }

  div.chatOptSettings
  {
    background-color: #2a2a2a !important;
  }

  DIV#chatOptBtn_general.chatOptMenuItem.chatOptMenuItemAccentSelected
  {
    background: #2a2a2a !important;
    color: #ffffff !important;
  }

  div.CV3chattersListBox
  {
    background-color: #2a2a2a;
  }

  div.CV3chattersListItem
  {
    background: #2a2a2a !important;
    color: #ffffff !important;
  }

  div.CV3chattersListHeader
  {
    background: #2a2a2a !important;
    color: #ffffff !important;
  }

  div.chatOptSmileyWrapper
  {
    background: #2a2a2a !important;
  }

  div.chatOptHelpWrapper
  {
    background: #2a2a2a !important;
  }

  div.chatOptHelp
  {
    background: #2a2a2a !important;
  }

  .mvntooltipSmall:after, [mvn-tooltip-small]:after
  {
    background: #2a2a2a !important;
    color: #ffffff !important;
  }
}
