css:
@-moz-document domain("wikia.com") {
#WikiaBar,
#WikiaNotifications {
    display: none!important;
}
#WikiaPage {
    width: 95%!important;
    z-index: 0!important;
}
.wds-global-navigation-wrapper,
#globalNavigation {
    position: static!important;
    z-index: 2!important;
}
#WikiaFooter > section:nth-child(2) {
    display: none!important;
}
#WikiaMainContent {
    width: 100%!important;
}
#WikiaRailWrapper,
.WikiaRail {
    float: left!important;
    position: relative!important;
    width: 100%!important;
}
#WikiaRail > section,
#WikiaRail > div {
    width: 30%!important;
    float: left!important;
    clear: none!important;
    padding: 10px!important;
    border-width: 0px!important;
}
div#NATIVE_TABOOLA_RAIL,
#RECIRCULATION_RAIL {
    display: none!important;
}
.wikia-ad {
    display: none!important;
}
.wds-dropdown__content,
.wds-list,
.wds-list li {
    z-index: 2!important;
}
::before {
    content: none!important;
}
::after {
    content: none!important;
}
.WikiaPage .WikiaPageBackground {
    background: #adc;
}
body {
    color: #39424d;
}
a {
    color: #F44336;
}
.SpeechBubble.even .speech-bubble-message,
.SpeechBubble.owner .speech-bubble-message,
.SpeechBubble .speech-bubble-message {
    background: #add;
}
.WikiaArticle pre,
.WikiaArticleComments .MiniEditorWrapper .editarea textarea,
.WikiaArticleComments .MiniEditorWrapper .editarea {
    background: #5cd1d1;
}
a.wikia-button.secondary,
.wikia-single-button a.secondary,
.wikia-menu-button.secondary,
input[type="submit"].secondary,
input[type="reset"].secondary,
input[type="button"].secondary,
.button.secondary,
button.secondary,
.MiniEditorWrapper .loading-indicator .loading-background,
.article-table,
.WikiaTable,
.CategorySelect.articlePage .add {
    background-color: #00b7e0;   
    background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(35%, #59d4d4), color-stop(65%, rgba(49, 192, 192, 0.7)));
    color: #656e78;
}
.SpeechBubble .permalink {
    color: #d3691f;
}
.WikiaArticle b,
.WikiaArticle strong {
    font-weight: bold;
    color: blue;
}
.WikiaPage .page-header__title {
    color: #3F51B5;
}
.WikiaPage .page-header .wds-button.wds-is-secondary:focus:not(:disabled),
.WikiaPage .page-header .wds-button.wds-is-secondary:hover:not(:disabled),
.WikiaPage .page-header .wds-button.wds-is-secondary:active,
.WikiaPage .page-header .wds-button.wds-is-secondary.wds-is-active {
    color: #3F51B5;
}
.WikiaPage .page-header .wds-button.wds-is-secondary {
    color: #9C27B0;
}
}
