css:
@-moz-document domain("www.jb51.net") {
body {
    background-color: #e8e8e8;
}
a {
    color: #555;
}
a:hover {
    color: #333;
}
.logom,
.logor,
.mainlr,
.topimg,
#new .left,
#txtlink {
    display: none;
}
#new .middle .soft,
#new .middle .soft ul li {
    width: 100%;
}
#new .middle {
    width: 100%;
    margin-left: 0;
}
#new .right {
    float: left;
    width: 100%;
    overflow: visible;
}
#new .right .recommended {
    width: 100%;
}
.con_2 > .clearfix {
    display: none;
}
/*顶部*/

#head .toolBar {
    border: none;
}
#head .toolBar,
#head .toolBar .main {
    background-color: #00796B;
}
.navList li .one a {
    border: none;
}
.navList li .one a,
.navUser a {
    color: #fff;
}
.navList li.hover .one a {
    color: #333;
}
.navList li .one .icon {
    display: none;
}
#jb51_topbar .userbar {
    background: transparent;
}
#head {
    background-color: #009688;
    box-shadow: 0 2px 14px 0px #8c8c8c;
}
.main {
    background: transparent;
}
#head .nav_top {
    background-color: #00796B;
}
.nav_top UL A:hover {
    background-color: #006357;
    text-shadow: none;
}
#head .subnav {
    background-color: #008c7c;
    border: none;
}
.subnav UL LI A {
    color: #fff;
}
.subnav UL LI A font {
    display: none;
}
#head .NavBar {
    background-color: #008c7c;
    margin-bottom: 20px;
    color: #fff;
}
#head .NavBar a {
    color: #eee;
}
.tese LI.search-hot A {
    color: #ddd !important;
}
.tese LI.search-hot SPAN {
    color: #fff;
}
.search {
    background-color: #fff;
    border: none;
}
.search input {
    outline: none;
}
/*主体*/

#mainbody {
    background: transparent;
}
#new .middle {
    border: none;
    height: auto;
    margin-bottom: 20px;
    border-radius: 4px;
    box-shadow: 1px 1px 6px 3px #ddd;
}
#new .middle .soft {
    padding: 10px;
    box-sizing: border-box;
}
#new .middle .soft ul li {
    padding-bottom: 15px;
}
#new .middle .title {
    background: #e2e2e2;
}
#new .middle .title .cur {
    background: #fff;
}
#new .middle .soft ul li span {
    color: #777;
}
.con_2 {
    margin-bottom: 10px;
}
}

@-moz-document domain("www.jb51.net") {
#contents {
    background: #e8e8e8;
}
#footer {
    margin-top: 0;
    background: #e8e8e8;
}
.adsbygoogle {
    display: none !important;
}
a[href*="aliyun"] {
    display: none !important;
}
a[href*="youhui.jb51"] {
    display: none !important;
}
.idc3 {
    display: none;
}
#idctu2 {
    display: none;
}
iframe {
    display: none;
}
.article-content {
    border: none;
    background-color: #fff;
    margin-top: 20px;
    border-radius: 4px;
    box-shadow: 1px 1px 15px 0px #ddd;
}
.tabTitleBar {
    margin-top: 20px;
}
.ad-widget-imageplus-sticker {
    display: none !important;
}
}
