css:
@-moz-document domain("www.inoreader.com") {
#subscriptions_buttons,
#sb_rp_tools,
#sb_tp_search_overlay {
    background-color: rgba(255, 255, 255, 0);
    background-image: none
}
#sb_rp_subscribe,
#sb_rp_clip_page,
#sb_rp_create_as,
#sb_rp_add_team,
#sb_rp_heading a,
.inno_toolbar_button {
    color: rgba(0, 0, 0, 0.3);
    text-shadow: none
}
#sb_rp_subscribe:hover,
#sb_rp_clip_page:hover,
#sb_rp_create_as:hover,
#sb_rp_add_team:hover,
#sb_rp_heading a:hover,
.inno_toolbar_button_hover,
.inno_toolbar_button:hover .inno_toolbar_button_mark_read_tick_hint,
.inno_toolbar_button:hover .inno_toolbar_button_mark_read_tick_hint:hover,
.inno_toolbar_button:hover {
    background-color: rgba(0, 0, 0, 0);
    color: rgba(0, 0, 0, 0.8);
    text-shadow: none;
    text-decoration: none
}
#sb_rp_subscribe,
#sb_rp_clip_page,
#sb_rp_create_as,
#sb_rp_add_team {
    background-color: rgba(0, 0, 0, 0);
    font-weight: bold
}
#sb_tp_search_overlay,
#sb_tp_dock:hover,
#feed_searcher {
    background-color: rgba(0, 0, 0, 0);
    background-image: none
}
#sb_tree_part {
    background-color: #f7f7f7;
    background-image: none
}
.tree_pane_docked #sb_tree_part {
    background-color: rgba(255, 255, 255, 0);
    background-image: none
}
.tree_pane_docked #sb_tp_search_overlay {
    display: none
}
.tree_pane_docked #tree_pane {
    box-shadow: none
}
.tree_unreaded {
    font-weight: normal
}
.tree_node_main {
    font-weight: normal
}
#new_articles_overlay:hover,
#new_articles_overlay {
    background-color: rgba(0, 0, 0, 0);
    display: none!important
}
.article_subscribed,
div.article_current_3way {
    background-color: rgba(255, 255, 255, 1);
    border: 0
}
.arrow_div,
div.article_current_3way .arrow_div,
.article_current_collapsed div.arrow_div,
.article .arrow_div,
.articles_feed_group_heading {
    background-color: rgba(255, 255, 255, 1);
    border: 0
}
div.article_current_3way a {
    color: #4487b5
}
#reader_pane.reader_pane_view_style_0>#consent_https_info {
    padding-top: 6px!important;
    padding-bottom: 6px!important
}
#reader_pane.reader_pane_view_style_0>div.ar div.article_header>div {
    padding-top: 6px!important;
    padding-bottom: 6px!important
}
#reader_pane.reader_pane_view_style_0>div.ar div.article_header>div.arrow_div {
    top: 1px!important
}
div.article_current_collapsed {
    background-color: #fff
}
.reader_pane_view_style_1 .article_card,
.reader_pane_view_style_1 .article_card.article_current {
    border: 0;
    border-bottom: 1px solid rgba(0, 0, 0, 0.12)
}
.reader_pane_view_style_2 {
    border-right: 1px solid rgba(0, 0, 0, 0.12)
}
.reader_pane_view_style_3.tiles_wrapper {
    max-width: 1200px;
    margin-left: auto;
    margin-right: auto
}
.reader_pane_view_style_3 div.article_tile {
    border: 0;
    width: 20%;
    height: 300px;
    min-width: 250px;
    max-width: 320px;
    padding: 0;
    margin: 30px 1% 0 1%
}
.reader_pane_view_style_3 div.article_tile.article_current {
    background-color: rgba(255, 255, 255, 1)!important;
    border: none!important;
    box-shadow: none
}
.reader_pane_view_style_3 div.article_tile.article_current a {
    color: #4487b5
}
.reader_pane_view_style_3 .article_tile .article_tile_content_wraper,
.reader_pane_view_style_3 .article_tile .article_tile_content_wraper.no_pic {
    height: 262px
}
.reader_pane_view_style_3 .article_tile .article_tile_picture {
    height: 200px;
    border-radius: 3px
}
.reader_pane_view_style_3 .article_tile .article_tile_title {
    font-size: 15px;
    text-align: center
}
.reader_pane_view_style_3 .article_tile .article_tile_header_date,
.reader_pane_view_style_3 .article_tile .article_tile_footer {
    display: none
}
.reader_pane_view_style_3 .article_tile:hover .article_tile_header_date,
.reader_pane_view_style_3 .article_tile:hover .article_tile_footer {
    display: block
}
.reader_pane_view_style_3 .article_tile_footer_left_buttons,
.reader_pane_view_style_3 .article_tile_footer {
    padding-left: 0
}
.reader_pane_view_style_3 .article_tile_content_wraper .article_tile_title,
.reader_pane_view_style_3 .article_tile_content_wraper .article_tile_content {
    margin-top: 0;
    padding: 0
}
div.article_magazine.article_current {
    box-shadow: none
}
div.article_magazine.article_current .article_magazine_title {
    color: #4487b5
}
#wraper #reader_pane.reader_pane_view_style_0 .articles_feed_group_heading,
#wraper #reader_pane.reader_pane_view_style_1 .articles_feed_group_heading,
#wraper #reader_pane.reader_pane_view_style_2 .articles_feed_group_heading,
#wraper #reader_pane.reader_pane_view_style_3 .articles_feed_group_heading,
#wraper #reader_pane.reader_pane_view_style_4 .articles_feed_group_heading,
.articles_feed_group_heading {
    background-color: rgba(255, 255, 255, 0)
}
.articles_feed_group_heading_title_date {
    padding-left: 18px;
    height: 30px;
    font-size: 18px;
    font-weight: bold
}
#reader_pane.reader_pane_view_style_0 .articles_feed_group_heading,
#reader_pane.reader_pane_view_style_2 .articles_feed_group_heading {
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
    border: 0
}
#subscriptions_buttons {
    border-bottom: 1px rgba(0, 0, 0, 0.12)
}
.article_footer {
    opacity: 0!important
}
.article_footer:hover {
    opacity: 1!important;
    -webkit-transition: all .25s ease-in-out;
    -moz-transition: all .25s ease-in-out;
    -o-transition: all .25s ease-in-out;
    -ms-transition: all .25s ease-in-out;
    transition: all .25s ease-in-out;
    z-index: 1!important
}
.unsubscribed_feed_header,
.unsubscribed_feed_suggestions_label,
#sb_rp_upgrade,
.block_article_ad,
#sinner,
.sinner,
.ad_footer_remove,
.sinner_inner,
.ad_title,
#sinner_container {
    display: none!important
}
#reader_pane.reader_pane_sinner {
    padding-right: 0
}
#discovery_overlay,
.article_content,
.article_title_link,
.article_sub_title,
#reader_pane {
    font-family: Helvetica, sans-serif!important
}
body,
input[type="text"],
input[type="number"],
input[type="password"],
input[type="email"],
#tree_pane {
    font-family: Helvetica, sans-serif!important
}
.article_content {
    display: block!important;
    text-rendering: optimizeLegibility!important;
    font-size: 16px!important;
    max-width: 800px!important;
    text-align: justify;
    margin-left: auto;
    margin-right: auto;
    width: 80%
}
.article_content img,
.article_content iframe {
    display: block!important;
    margin: 0 auto 20px auto!important;
    max-width: 100%!important;
    text-align: center;
    border: 0;
    padding: .15em;
    overflow: hidden!important
}
.article_card .article_title,
#article_dialog .article_title {
    padding-right: 12px;
    padding-top: 30px
}
.article_title_expanded_view {
    margin: 5px auto 0 auto!important;
    position: relative;
    top: 0
}
.article_title_expanded_view,
.article_title {
    font-size: 22px;
    display: block!important;
    text-align: center!important;
    justify-content: center;
    max-width: 80%;
    margin-left: auto;
    margin-right: auto
}
.article_title a:hover {
    text-decoration: none!important;
    opacity: .7
}
.article_sub_title {
    height: 40px;
    display: flex;
    justify-content: center
}
.article_content a {
    text-decoration: none!important
}
.article_content a:hover {
    text-decoration: none!important;
    border-bottom: 2px solid rgba(68, 135, 181, 0.5)
}
.article_list_tab {
    border: 0;
    color: rgba(0, 0, 0, 0.5);
    background-color: #f7f7f7;
    border-right: 0;
    border-top: 0;
    border-bottom: 0;
    border-radius: 0
}
div.article_list_tab.article_list_tab_current {
    color: rgba(0, 0, 0, 0.8);
    background-color: inherit
}
#subscriptions_buttons,
#sb_tp_dock:hover,
#sb_rp_tools,
#sb_reading_part,
#sb_tp_search_overlay,
#sb_tree_part,
#feed_searcher {
    background-color: #4487b5!important;
    background-image: none
}
#sb_tp_search_overlay,
#feed_searcher,
#sb_rp_heading a,
.inno_toolbar_button {
    color: rgba(255, 255, 255, 0.8)!important
}
#sb_rp_heading a:hover,
.inno_toolbar_button_hover,
.inno_toolbar_button:hover .inno_toolbar_button_mark_read_tick_hint,
.inno_toolbar_button:hover .inno_toolbar_button_mark_read_tick_hint:hover,
.inno_toolbar_button:hover {
    background-color: rgba(0, 0, 0, 0);
    color: rgba(255, 255, 255, 1.0)!important;
    text-shadow: none;
    text-decoration: none
}
#tree_pane,
#splitter,
.tree_pane_docked #sb_tree_part,
#reader_pane,
#discovery_overlay,
.articles_feed_group_heading {
    background-color: #f7f7f7;
    background-image: none
}
#subscriptions_buttons {
    position: relative;
    box-shadow: 0 0 8px 0 rgba(0, 0, 0, 0.4)
}
.article_footer {
    background-color: rgba(255, 255, 255, 0)
}
.arrow_div,
.article_subscribed,
div.article .arrow_div {
    background-color: #fff;
    border: 0
}
.reader_pane_view_style_2 .article_full_contents {
    box-shadow: 0 0 4px 0 rgba(0, 0, 0, 0.2)
}
.reader_pane_view_style_0 .ar,
.reader_pane_view_style_2 .ar {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0);
    border-bottom: 1px solid rgba(0, 0, 0, 0.12)
}
#reader_pane {
    border-right: 0
}
.reader_pane_view_style_1 .article_card {
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.2);
    margin: 20px 7px
}
.reader_pane_view_style_3 .article_tile {
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.2);
    border: 0;
    border-radius: 4px
}
.reader_pane_view_style_3 .article_tile .article_tile_picture {
    border-radius: 4px 4px 0 0
}
.reader_pane_view_style_3 div.article_tile.article_current,
.reader_pane_view_style_3 .article_tile:active {
    box-shadow: 0 0 12px 0 rgba(0, 0, 0, 0.3)
}
.reader_pane_view_style_3 .article_tile_footer_left_buttons,
.reader_pane_view_style_3 .article_tile_footer {
    padding-left: 10px!important
}
.reader_pane_view_style_3 .article_tile_content_wraper .article_tile_title,
.reader_pane_view_style_3 .article_tile_content_wraper .article_tile_content {
    padding: 0 18px 18px 18px!important
}
.reader_pane_view_style_3 .article_tile_content_wraper.no_pic .article_tile_title {
    padding: 18px 18px 0 18px!important
}
div.article_magazine.article_unreaded,
div.article_magazine.article,
div.article_magazine.article_current {
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.2);
    border-radius: 3px;
    margin: 12px auto 12px auto
}
div.article_magazine.article_unreaded:active,
div.article_magazine.article_current:active,
div.article_magazine.article:active {
    z-index: 1;
    position: relative;
    box-shadow: 0 2px 16px 0 rgba(0, 0, 0, 0.2)
}
}
