css:
@-moz-document domain("youtube.com") {
  /* Values */
  :root {
    --main-text-color: #7c818c;
    --highlighted-text-color: #5294e2;
    --main-bg-color: #383c4a;
    --second-bg-color: #404552;
    --third-bg-color: #4b5162;
  }
  
  body, .yt-live-chat-renderer-0, .paper-icon-button-0 {
    background: var(--main-bg-color) !important;
    color: var(--main-text-color);
  }
  
  .comment-simplebox-arrow {
    visibility: hidden;
  }
  
  .comment-simplebox-frame, .comment-simplebox-renderer-collapsed-content {
    border: 1px solid var(--main-bg-color);
  }
  
  .ytp-red2 .ytp-swatch-background-color, .ytp-red2 .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: var(--highlighted-text-color);
  }
  
  .yt-uix-checkbox-on-off input[type="checkbox"]:checked + label .checked {
    position: relative;
    line-height: 0.9;
  }
  
  li, .comment-simplebox, .comment-simplebox-text {
    background: var(--second-bg-color);
    color: var(--main-text-color);
  }
  
  .yt-ui-ellipsis, #guide-container, .yt-card, #paper-icon-button, .yt-live-chat-message-input-renderer-0, .yt-live-chat-header-renderer-0 {
    background: var(--second-bg-color);
    color: var(--main-text-color) !important;
  }
  
  #yt-masthead-container {
    background: var(--second-bg-color);
    border: none;
  }
  
  #masthead-search-terms, .yt-uix-button-default {
    background: var(--third-bg-color);
  }
  
  .video-list .video-list-item .title, .video-list .video-list-item .title:hover, #action-panel-details a, .yt-lockup .yt-lockup-byline a, .exp-invert-logo li.guide-section h3 a {
    color: var(--highlighted-text-color) !important;
  }
  
  .masthead-search-terms-border, .search-btn-component {
    border: none;
    box-shadow: none;
    color: var(--main-text-color);
  }
  
  .yt-uix-button-default, .yt-subscriber-count, .yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-branded, .yt-uix-button-subscribe-branded, .yt-subscriber-count {
    border: none;
    box-shadow: none;
    color: var(--main-text-color);
    background: var(--third-bg-color) !important;
  }
  
  .yt-uix-button-subscribe-branded::before {
    content: none;
  }
  
  .yt-badge {
    background: var(--third-bg-color);
    line-height: 11px;
    border-radius: 20px;
    border: none;
  }
  
  .yt-live-chat-text-message-renderer-0 #menu.yt-live-chat-text-message-renderer {
    background: transparent;
  }
  
  #watch-description, .guide-item {
    color: var(--main-text-color);
  }
  
  .exp-invert-logo .guide-item.guide-item-selected, .exp-invert-logo .guide-item.guide-item-selected:hover {
    background: var(--highlighted-text-color);
  }
  
  .guide-item:hover {
    background: var(--third-bg-color);
  }
  
  .yt-live-chat-gaming-promo-renderer-0 {
    height: 0;
    width: 0;
    visibility: hidden;
  }
}
