css:
@-moz-document domain("hooktube.com") {
  
    :root {
      --bg-color: #222;
      --highlight: #a4a4a4;
  }
  
  .navbar {
    background-color:var(--bg-color);
  }

  .jumbotron {
    background-color:var(--bg-color)!important;
    color:var(--highlight);
  }
  
  .container-fluid main {
    background-color:var(--bg-color);
  }
  
  .container-fluid {
    background-color:var(--bg-color);
  }
  
  .text-left {
    color:var(--highlight);
  }
  
  .text-right {
   color:var(--highlight);
  }
  .text-center {
    color:var(--highlight);
  }
  
  .video-title{
    color:var(--highlight);
  }
  
  .card-title {
   color:var(--highlight);
  }
  
  .card-subtitle {
    color:var(--highlight);
  }

  .card-body {
    background-color:var(--bg-color);
    color:var(--highlight);
  }
  
  .list-channel-title {
    color:var(--highlight);
  }
  
  .btn {
    color:var(--highlight);
    border: 2px solid var(--highlight);
  }

  .dropdown-menu.show {
    background-color:var(--bg-color);
  }
  
  .dropdown-item:focus {
    color:var(--highlight);
    text-decoration: none;
    background-color:var(--bg-color);
    -webkit-transition:  -webkit-transform .2s ease-out;
    -moz-transition: -moz-transform .2s ease-out;
    -o-transition: -o-transform .2s ease-out;
    -ms-transition: -ms-transform .2s ease-out; 
    transition: transform .2s ease-out; 
  }
  
  .dropdown-item:hover {
    color:var(--highlight);
    text-decoration: none;
    background-color:var(--bg-color);
    -webkit-transform:scale(.9);
    -moz-transform:scale(.9);
    -o-transform:scale(.9);
    transform:scale(.9);
  }
  
  .show > a {
    outline: 0;
    color: #a4a4a4;
}
  
  p {
    color:var(--highlight);
    background-color:var(--bg-color);
  }
  
  .modal-body {
    background-color:var(--bg-color);
  }
  
  *, *::after, *::before {

    box-sizing: inherit;
    color:var(--highlight);
  }
  
  .navbar-brand img {
  width:100px;
  height:50px;
  box-sizing:border-box;
  padding-left: 160px;
  background: url(https://i.imgur.com/uGLV5C3.png) left top no-repeat;
  }
  
  [type="reset"], [type="submit"], button, html [type="button"] {
    -webkit-appearance: button;
    color:var(--highlight);
    background-color:var(--bg-color);
  }
  
  .col-sm-9 {
    color:var(--highlight);
  }
  
  .text-primary {
    color:var(--highlight)!important;
  }
  
  a:hover {
    color: #ef9a9a;
    text-decoration: none;
  }
  
  a {
     color: #f44336;
  }
  
  .navbar-light .navbar-nav .active > .nav-link, .navbar-light .navbar-nav .nav-link.active, .navbar-light .navbar-nav .nav-link.show, .navbar-light .navbar-nav .show > .nav-link {
    color:var(--highlight);
  }
  
  .form-inline .form-control {
    background-color:var(--bg-color);
    border: 2px solid var(--highlight);
  }
  
  .modal-header {
    border-bottom: 1px solid #a4a4a4f;
    background-color: #222;
  }
  
  .text-center {
    background-color: #222;
  }
  .modal-title {
    color: #222!important;
  }
  
    .close {
    color: #a4a4a4;
    -webkit-transition:  -webkit-transform .2s ease-out;
    -moz-transition: -moz-transform .2s ease-out;
    -o-transition: -o-transform .2s ease-out;
    -ms-transition: -ms-transform .2s ease-out; 
    transition: transform .2s ease-out;
  }
  
  .close:active {
    color: #a4a4a4;
    text-decoration: none;
    background-color: #222;
   -webkit-transform:scale(.9);
   -moz-transform:scale(.9);
   -o-transform:scale(.9);
   transform:scale(.9);
  }
  
  .my-sm-0 {
    color: #a4a4a4;
    -webkit-transition:  -webkit-transform .2s ease-out;
    -moz-transition: -moz-transform .2s ease-out;
    -o-transition: -o-transform .2s ease-out;
    -ms-transition: -ms-transform .2s ease-out; 
    transition: transform .2s ease-out; 
  }
  
  .my-sm-0:active {
   -webkit-transform:scale(.9);
   -moz-transform:scale(.9);
   -o-transform:scale(.9);
   transform:scale(.9);
  }
  
  .modal-title {
    color: #a4a4a4;
  }
  .modal-title {
    margin-bottom: 0;
    line-height: 1.5;
    color: #a4a4a4 !important;
  }
  .modal-title {
  color: #a4a4a4 !important;
  }
  
  .alert-warning {
    color: #222;
    background-color: #a4a4a4;
    border-color: #fff;
  }

  .navbar-light .navbar-toggler {
    color: #a4a4a4;
    border-color: #a4a4a4;
  }
}
