css:
@-moz-document domain("astropolis.pl") {
body {
    background-color: #3c3c3c;
    color: #dedede;
}
a {
    color: #c3891b;
}
span {
    color: rgb(255, 255, 255);
}
#ipsLayout_header header {
    background-color: #3c3c3c;
}
.ipsType_pageTitle {
    color: #fff;
}
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated) {
    background-color: #1c1c1c;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even) {
    background: #2d2d2d;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    color: #ffffff;
    background: #2d2d2d;
}
.ipsButtonBar {
    background: #2d2d2d;
}
.jesion_image_topicB {
    background-color: #2d2d2d
}
.ipsAreaBackground {
    background: #1d1d1d;
}
.ipsComposeArea_dummy {
    background: #2c2c2c;
}
.ipsType_light {
    color: #b3b3b3;
}
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a,
.ipsButtonBar .ipsButtonRow li > a {
    background: #444444;
    color: #aba9a9;
}
.ipsType_richText {
    color: #ffffff;
}
.ipsBox {
    border: 1px solid #3c3c3c;
}
.ipsPagination.ipsPagination_mini a {
    background: rgba(255, 255, 255, 0.12);
    color: #fff;
}
.cForumMiniList.cForumMiniList_multiRoot > li > a {
    color: #fff;
}
.ipsBreadcrumb > ul > li > a {
    color: #fff;
}
.ipsPagination_page a {
    background: #131313;
}
.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #1c1c1c;
}
.ipsButtonBar .ipsPagination .ipsPagination_pageJump a {
    color: #ddd;
}
#ipsLayout_footer a,
#ipsLayout_footer p {
    color: #ffffff;
}
.ipsTabs_panel {
    background: #1c1c1c;
}
.ipsTabs_activeItem {
    background: #444;
    color: #d2d2d2;
}
.ipsWidget .ipsTabs_small .ipsTabs_item:not( .ipsTabs_activeItem) {
    color: rgba(255, 255, 255, 0.6);
}
.ipsAreaBackground_light {
    background: #444444;
}
.ipsApp .ipsButton_link.ipsFollow,
.ipsApp .ipsButton_link.ipsPromote {
    background: #1c1c1c;
}
.ipsApp .ipsButton_link {
    color: #c2d4e4;
    border: 1px solid #3e3e3e;
}
.ipsFollow .ipsCommentCount {
    background: #3e3e3e;
    color: #b3b3b3;
}
html[dir="ltr"] .ipsFollow .ipsCommentCount:after {
    border-color: transparent #3e3e3e transparent transparent;
    border-top-color: transparent;
    border-right-color: rgb(62, 62, 62);
    border-bottom-color: transparent;
    border-left-color: transparent;
}
.ipsBreadcrumb > ul > li {
    color: #ffffff;
}
.ipsBreadcrumb [data-action="defaultStream"],
.ipsBreadcrumb [data-action="markSiteRead"] {
    color: #ffffff;
}
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_page a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_next a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_prev a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_first a,
.ipsPagination:not( .ipsPagination_mini) .ipsPagination_last a {
    color: #ffffff;
    background: #444444;
}
.ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #1c1c1c;
    font-weight: bold;
}
.ipsQuote {
    border-color: #3c3c3c;
    background: #353535;
}
.ipsQuote_citation {
    background: #444444;
    color: #a5a5a5;
}
.ipsReact_reactCount a {
    background-color: #444444;
}
.ipsReact_reactCount span:last-child {
    color: #ffffff;
}
.ipsReact_reactCount {
    border: 1px solid rgba(0, 0, 0, 0.71);
}
a.ipsType_blendLinks,
.ipsType_blendLinks a {
    color: #ffffff;
}
.ipsAreaBackground_reset {
    background: #1c1c1c;
}
.cBlogInfo_content:after {
    background: -moz-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(28, 28, 28, 1) 100%);
    background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, rgb(28, 28, 28) 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, rgba(255, 255, 255, 0)), color-stop(100%, rgba(28, 28, 28, 1)));
    background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, rgb(28, 28, 28) 100%);
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(28, 28, 28, 1) 100%);
    background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, rgb(28, 28, 28) 100%);
    background: -o-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(28, 28, 28, 1) 100%);
    background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, rgb(28, 28, 28) 100%);
    background: -ms-linear-gradient(top, rgba(255, 255, 255, 0) 0%, rgba(28, 28, 28, 1) 100%);
    background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, rgb(28, 28, 28) 100%);
    background: linear-gradient(to bottom, rgba(255, 255, 255, 0) 0%, rgba(28, 28, 28, 1) 100%);
    background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, rgb(28, 28, 28) 100%);
}
.ipsType_sectionTitle {
    background: #444444;
}
.cCalendar td.cCalendar_nonDate {
    background: #444444;
}
.ipsStreamItem_snippet .ipsType_richText {
    color: #fff;
}
a.ipsType_blendLinks,
.ipsType_blendLinks a {
    color: #cccccc;
}
.cke_wysiwyg_frame,
.cke_wysiwyg_div {
    background-color: #333333;
}
.ipsApp .ipsField_autocomplete,
.ipsApp textarea,
.ipsApp input[type="text"],
.ipsApp input[type="password"],
.ipsApp input[type="datetime"],
.ipsApp input[type="datetime-local"],
.ipsApp input[type="date"],
.ipsApp input[type="month"],
.ipsApp input[type="time"],
.ipsApp input[type="week"],
.ipsApp input[type="number"],
.ipsApp input[type="email"],
.ipsApp input[type="url"],
.ipsApp input[type="search"],
.ipsApp input[type="tel"] {
    background-color: #444444;
}
.ipsMenu_title {
    background: #444444;
}
.ipsMenu_item a {
    background-color: #2d2d2d;
    color: #fff;
}
.ipsMenu_sep {
    margin: 0px;
}
.ipsComment {
    background: #444444;
}
.ipsComment:not( .ipsModerated) .ipsComment_header {
    background: #1c1c1c;
}
.cMessage_active {
    background: #444444 !important;
}
.ipsMenu_headerBar,
.ipsMenu_footerBar {
    background: #3c3c3c;
}
.ipsType_sectionHead {
    color: #fff;
}
.ipsMenu {
    background: #1d1d1d;
    border: 1px solid #252525;
}
.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread):not( .ipsDataItem_selected):not( .ipsModerated) {
    background: #1d1d1d;
}
.ipsApp .ipsButton_light {
    background: #444444;
}
#elProfileActivityOverview .ipsStreamItem,
#elUserContent .ipsStreamItem,
#elSingleStatusUpdate .ipsStreamItem {
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.ipsTabs_activeItem {
    background: rgba(255, 145, 9, 0.26);
}
.jesion_slider_topic {
    background-color: #1c1c1c;
}
.cForumMiniList .ipsSideMenu_item .cForumMiniList_title {
    color: rgb(150, 150, 150);
}
.cForumMiniList.cForumMiniList_multiRoot > li > a {
    color: #c3891b;
}
.ipsApp .ipsButton_light {
    background: #1c1c1c;
    color: #fff;
}
.ipsPager_type {
    color: #dadada;
}
.ipsPager .ipsPager_next a:after, .ipsPager .ipsPager_prev a:after {
    color: #777777;
}
.ipsSideMenu_item:not( .ipsSideMenu_itemActive ) a:hover, a.ipsSideMenu_item:not( .ipsSideMenu_itemActive ):hover {
    background: #4c4c4c;
}
#mapCanvas span {
color: black;
}
.ipsHovercard {
    background: #3c3c3c;
}
.ipsDialog > div {
    background: #3c3c3c;
}
.ipsDialog_title {
	color:#fff;
}
.cPastLeaders_cell {
    background: #444444;
}
.cPastLeaders_cell:after {
    color: #fff;
	opacity: 0.6;
}
.cPastLeaders_title span {
	background: #2d2d2d;
}

.cStreamFilter {
    background: #1f1f1f;
}
.cStreamFilter h3 {
    color: #d2d2d2;
}
.cStreamFilter a {
    color: #cccccc;
}
.cStreamFilter p em {
    opacity: 0.8;
}
}
