css:
@-moz-document url-prefix(http://www.membrana.ru/articles), url-prefix(http://www.membrana.ru/lenta) {
  IFRAME,
  HTML > BODY > SPAN:first-child + TABLE,
  HTML > BODY > SPAN:first-child + TABLE + TABLE,
  HTML > BODY > SPAN:first-child + TABLE + TABLE + TABLE,
  HTML > BODY > SPAN:first-child + TABLE + TABLE + TABLE + TABLE,
  HTML > BODY > SPAN:first-child + TABLE + TABLE + TABLE + TABLE + TABLE > TBODY > TR > TD:first-child,
  TABLE[width="240"],
  HTML > BODY > SPAN:first-child + TABLE + TABLE + TABLE + TABLE + TABLE + TABLE + BR + TABLE + TABLE,
  HTML > BODY > SPAN:first-child + TABLE + TABLE + TABLE + TABLE + TABLE + TABLE,
  HTML > BODY > SPAN:first-child + TABLE + TABLE + TABLE + TABLE + TABLE + TABLE + BR + TABLE,
  HTML > BODY > SPAN:first-child + TABLE + TABLE + TABLE + TABLE + TABLE > TBODY > TR > TD:last-child > P
  {
    display: none !important;
  }

  HTML, BODY, TD, DIV, P, SPAN {
    background-color: white !important;
    font-family: Georgia, serif !important;
  }


  TD[width="478"], TABLE[width="478"] {
    width: 100% !important;    
  }
  
  TD[width="478"]{
    padding: 20px !important;
  }

}
