css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("geekweek.pl") {
  
  body {
    font-weight: 400;
    color: #aaa !important;
    background: #1d1d1d !important;
  }
 
  ::selection {
    color: #ddd;
    background: #555;
  }
  
  ::-moz-selection {
    color: #ddd;
    background: #555;
  }
  
  #logo, #logo.home {
    transition: all ease-out .3s;
        filter: drop-shadow(0px 3px 3px rgba(10,10,10,.4));
  }
  
  #logo:hover, #logo.home:hover {
    filter: drop-shadow(0px 12px 5px rgba(10,10,10,.2)) brightness(1.1) saturate(2);
    transform: scale(1.05);
  }
  
  #content {
    margin-top: 0;
  }
  
  #root, #top, #content, #articles, article, section,
  .rightbar {
    background: #2d2d2d !important;
    border: none !important;
    color: #bbb !important;
  }
  
  #recommend {
    background: transparent !important;
  }
  
  #rss-list, #about-us {
    background: none !important;
  }
  
  a {
    color: #ccc !important;
    text-shadow: 1px 1px 1px #333;
    transition: all .1s;
  }
  
  a:hover {
    color: #eee !important;
    text-shadow: 0 0 4px rgba(230,230,255,.4);
  }
  
  #today-article {
    box-shadow: 0 15px 12px -12px rgba(33,33,33,.4);
  }
  
  #main-article h1,
  #articles .image .author {
    color: #3196d8;
  }
  
  #main-article p {
    margin-top: 20px;
  }
  
  #articles article .article-body {
    color: #acf;
  }
  
  #articles article p {
    color: #999 !important;
  }
  
  
  blockquote {
    background: rgba(0,0,0,.2);
    padding: 2px 10px 15px 10px;
    border-radius: 3px;
  }
  
  .black-tags a::before, .black-tags img {
    content: '';
  }
  
  .black-tags a {
    color: #999;
    background: #222;
    border-radius: 3px;
    left: auto;
    right: auto;
    padding: 2px 8px;
  }
  
  #articles article a.more {
    background: #3a3a3a;
    text-transform: uppercase;
    font-size: 12px;
    font-weight: 600;
    transition: all .2s;
    text-shadow: none;
  }
  
  #articles article a.more:hover {
    background-color: #3196d8;
    box-shadow: 0 0 15px rgba(255,255,255, .1);
  }  
  
  .comments-counter:hover .cloud {
    background-position: 0 0 !important;
  }
  
  #comments form hr {
    border: none;
    border-bottom: 2px solid #555;
  }
  
  #comments .comment {
    margin-bottom: 20px;
  }
  
  #comments .comment .content {
    border: none;
    color: #aaa;
    text-shadow: 0 0 1px #000;
  }
  
  #comments .comment .head {
    border-bottom: 1px solid #555;
    box-shadow: 0 6px 12px -12px rgba(33,33,33,.3);
  }
  
  #comments .comment .content::before, #comments .comment .content img {
    content: '';
  }
  
  #comments .comment time {
    color: #999;
  }
  
  #articles h2.border {
    border-top: 2px solid #555;
  }
  
  .pagination {
    background: none;
  }
  
  .pagination ul {
    background: #222;
    box-shadow: 0 6px 12px -12px rgba(33,33,33,.3);
  }
  
  .pagination ul li {
    border-right: 1px solid #333;
  }
  
  .pagination ul li a {
   color: #999;
  }
 
  
  #top ul a, #categories,
  #newest-gallery div figure {
    border-bottom: 1px solid #444 !important;
    box-shadow: 0 15px 12px -12px rgba(33,33,33,.4);
  }
  
  #top ul li:last-of-type a {
    border: none !important;
  }
  
  #categories ul.strong {
    text-shadow: 0 0 5px rgba(100, 100, 255, .5);
  }

  #categories ul li strong:hover {
    text-shadow: 0 0 5px rgba(100, 100, 255, 1);
    color: #6ec4fc;
  }
  
  #categories li.root > a {
    border-right: 1px solid #444 !important;
  }
  
  #categories li.root:last-child > a {
    border-right: 0 !important;
  }
  
  #categories ul.child {
    background: linear-gradient(180deg, rgba(5,5,10,.8), rgba(15,15,20,.7));
  }

  
  #search0 {
    border-top: 1px solid #444;
  }
  
  #search0 .submit {
    opacity: .4;
  }
  
  #controversial h2, #recommend h2,
  #newest-gallery h2, #archives h2,
  #interesting h2, #fb-box h2,
  .box0 h2 {
    background: transparent;
    color: #3196d8;
    text-shadow: 1px 1px 0 rgba(33,33,33,.3);
  }
  
  #fb-box {
    background: linear-gradient(90deg, #2b2b2b 40%, #3e3e3e) !important;
    height: 415px;
    width: 335px;
    border-radius: 0 15px 15px 0;
    box-shadow: 5px -15px 15px -15px rgba(33,33,33,.5), 
      5px 15px 15px -15px rgba(33,33,33,.7);
  }
  
  #fb-box p {
    text-transform: uppercase;
    font-size: 18px; 
    color: #3196d8;
    margin-bottom: 0;
    
  }
  
  #controversial div a {
    border-bottom: 1px solid #555;
    box-shadow: 0 15px 12px -12px rgba(33,33,33,.4);
  }
  

  input.text, textarea {
    background: #222 !important;
    color: #aaa;
    box-shadow: 0 0 3px rgba(255,255,255,.1);
    border: 1px solid #444;
  }
  
  #comments input[type="submit"] {
    margin-right: 290px;
    background: #444;
    text-transform: uppercase;
    width: auto;
    font-family: 'ubuntu', 'sans-serif';
    font-weight: 500;
  }
  
  #comments input[type="submit"]:hover {
    background: #3196d8;
  }
  
  #articles article .tags a {
    background: #222;
    color: #aaa !important;
    border-radius: 3px;
    text-transform: uppercase;
    font-size: 9px;
    text-shadow: none;
    box-shadow: inset 2px 2px 4px rgba(33, 33, 33, .3);
  }
  
  #main-article .image {
    margin-top: 14px;
  }
  
  #articles article .tags a:hover {
    color: #eee !important;
    border-radius: 3px;
  }
  
  #articles article .tags a.black {
    font-weight: 600;
    font-size: 10px;
    color: #eee !important;
    background: #3196d8;
    text-shadow: none;
  }
  
  #articles article .tags a:hover.black {
    background: #3196d8;
  }
  
  
  #articles article .left .date {
    padding-top: 15px;
    background: rgba(40,40,40,.7);
    border: 1px solid #555;
    border-radius: 5px;
    box-shadow: inset 2px 2px 4px rgba(22,22,22,.3);
  }
  
  #articles article figure img {
    border-top: 2px solid #aaa !important;
    border-bottom: 2px solid #aaa !important;
    box-shadow: 0 12px 12px -12px rgba(33,33,33,.6), 0 6px 30px -30px rgba(33,33,33,.9);
  }
  
  #articles article figure figcaption time {
    color: #ccc;
    text-transform: uppercase;
    font-size: 10px;
  }
  
  #articles article figure figcaption span {
    color: #3196d8;
    text-transform: uppercase;
    font-size: 10px;
  }
  
  aside .right .box0 > ul {
    padding: 0;
    border: none;
  }
  
  aside .right .item {
    background: transparent;
    border-bottom: 1px solid #555;
    border-radius: 0;
    box-shadow: 0 15px 24px -24px rgba(33,33,33,.9);
    text-transform: uppercase;
  }
  
  aside.rightbar .right {
    border-left: 1px solid #444;
    background: linear-gradient(90deg, #2a2a2a, #2d2d2d);
  }
  
  aside.rightbar .left .more {
    background: #3a3a3a;
    color: #fff;
    text-shadow: none;
  }
  
  aside.rightbar .left a.more {
    color: #eee;
    text-transform: uppercase;
    font-size: 12px;
    font-weight: 600;
    margin-top: 15px;
    padding: 5px 0;
    transition: all .2s;
  }
  
  #controversial .more {
    bottom: -25px;
  }
  
  aside.rightbar .left .more:hover {
    color: #fff;
    background-color: #3196d8;
    box-shadow: 0 0 15px rgba(255,255,255, .1);
  }
  
  .box0 > .more {
    position: initial;
    border: none;
    background: #3a3a3a;
    padding: 10px;
    border-radius: 4px;
  }
  
  .box0 > .more:hover {
    line-height: 40px;
    background-color: #3196d8;
    box-shadow: 0 0 15px rgba(255,255,255, .1);
  }
  
  #archives::after {
    content: none;
  }
  
  #archives > ol {
    padding-top: 0;
    margin-top: -10px;
  }
  
  #archives .year span.rolled {
    border-bottom: 1px solid #555;
  }
  
  #pixad-box {
    display: none;
  }
  
}
