css:
@-moz-document url-prefix("https://www.nfoservers.com/control") {
body {
    background: none;
    background-color: #252525;
    color: white;
    font-family: "Lucidia Console", "Calibri", "Arial";
    overflow-x: hidden;
}

div {
    color: white;
}

.content > #header {
    position: fixed;
    top: 0px;
    bottom: 0px;
    left: 300px;
    height: 100vh;
    overflow-y: auto;
    overflow-x: hidden;
}

.subbox, .subbox2 {
    background-color: #444;
    border: none;
}

@keyframes notification {
    0% {height: 0px; line-height: 10px;}
    5% {height: 45px; line-height: 45px;}
    95% {height: 45px; line-height: 45px;}
    100% {height: 0px; line-height: 10px;}
}

.successmessage, .errormessage {
    position: fixed;
    top: 0px;
    left: 300px;
    width: calc(100% - 320px);
    background-color: green;
    height: 45px;
    line-height: 45px;
	padding: 0px 10px;
    color: white;
    text-align: center;
    cursor: default;
    overflow: hidden;
    background-image: initial;
    animation-name: notification;
    animation-duration: 5.1s;
    animation-fill-mode: forwards;
}

.errormessage {
    background-color: red;
}

#header {
    width: 300px;
    height: calc(100vh - 60px);
    top: 0px;
    bottom: 60px;
    background-color: #333;
}

#header #current {
    background-color: #844;
    background-image: none;
}

#header ul {
    width: 100%;
}

#header ul li, #header ul li a {
    padding-top: 0px !important;
    padding-bottom: 0px !important;
    line-height: 30px;
    height: 100%;
	width: 100%;
}

#header ul li a {
	width: calc(100% - 30px);
    padding-left: 30px;
    background-repeat: no-repeat !important;
    background-position: 7px center !important;
    background-size: 14px auto !important;
}

#header br + ul, #header br + ul + ul {
    position: fixed;
    bottom: 30px;
    left: 0px;
    height: 30px;
    width: 300px;
    overflow: hidden;
	background-color: #444;
}

#header br + ul + ul {
    bottom: 0px;
}

#header br + ul + ul a {
    background-image: url("data:image/png;base64,") !important;
}

#header br + ul a {
    background-image: url("data:image/png;base64,") !important;
}

.type + ul rem li a {
    background-image: url("data:image/png;base64,") !important;
}

.type {
    padding: 5px 0px;
}

#header > div {
    padding: 10px 0px;
}

.subbox2 textarea {
    background-color: #777;
    color: white;
    width: 99% !important;
}

rem > #header {
	background-color: #444 !important;
    position: fixed;
    left: 0px;
    top: 0px;
    z-index: 1;
    overflow-x: hidden;
}

#header li, #header a, #header #current a {
    background-image: none;
    background: none;
}

#header ul {
    padding: 0px;
}

#header ul li {
    padding: 5px 0px;
}

#header ul li a {
    color: #bbb;
}

#header ul li:hover, #header ul li:hover a {
    background-color: #555;
    color: white;
}

#header #current a {
    color: white;
}

.content {
    left: 300px;
}

.content2 {
    position: absolute;
    width: calc(100vw - 640px);
    left: 300px;
    padding: 10px;
    margin: 0px;
    min-height: calc(100vh - 20px);
}
}
