css:
/* DO NOT include namespace or svg styles wont work */

@-moz-document domain("iichan.hk") {
  /* base64 */
  /* window */
  td.reply, td.highlight,
  #de-win-reply,
  #de-win-cfg,
  .de-win, .de-win-fixed,
  div[de-oppost],
  .reply.de-pview,
  .userdelete,
  .de-thr-hid,
  .de-menu,
  .de-cfg-tab, .de-cfg-tab:hover,
  .de-parea #postform,
  .de-fav-header,
  .de-popup,
  body > form ~ table[border="1"],
  #de-panel,
  div[id^="thread"] > blockquote {
    border-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAIAAAACDbGyAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgLEQoXkmUKTQAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAAuSURBVAjXY7x37x4DDCgpKbEwMDAoKioyMDDU1NQwMDAwQWT27dsHYbDAZbADAMvoCJFTEMi1AAAAAElFTkSuQmCC') 2 !important;
    border-image-width: 2px !important;
    background-color: #bdbdbd !important;
    border-radius: 0;
  }
  
  /* content */
  td.reply > blockquote,
  td.highlight > blockquote,
  div[de-oppost] > blockquote,
  .de-spell-editor, select, .de-cfg-checkbox, .de-fold-block, .nothumb,
  #de-spell-editor,
  #de-panel-info,
  input, textarea {
    background-color: #fff;
    border-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAIAAAACDbGyAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgLEQYOWrvtgQAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAArSURBVAjXVYqxDQAwDMJo1K/5FU6hA0vqyZZ8SGKzW9LUkiQBMPi53Wy3H2UdELc8W34iAAAAAElFTkSuQmCC') 2;
    border-image-width: 2px;
  }

    /* toolbar */
  td.reply .filesize, td.highlight .filesize, div[de-oppost] .filesize, .adminbar {
    border-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAIAAAACUFjqAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgLDzAOxR/yjgAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAAtSURBVBjTY6yurmbADVgYGBhaWlqwytXU1LBAWPv27cOqgokBLxjE0oz4gwUAIPsIM3buTOMAAAAASUVORK5CYII=') 2;
    border-image-width: 2px;
    background-color: #bdbdbd;
  }
  
  /* toolbar grip */
  td.reply .filesize::before,
  td.highlight .filesize::before,
  div[de-oppost] .filesize::before {
    background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAMAAAASCAIAAAARwPPWAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgLEAge2E26XAAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAArSURBVAjXzcZBDQAwDAOxUwc73EIlRCplr3GYX6ZtW0kD2AaG56ed3U0CXLfoD1rM33I/AAAAAElFTkSuQmCC');
  }
  
  /* statusbar grip */
  #de-win-reply,
  .de-refmap:last-child, .omittedposts {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAIAAADZF8uwAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgNDhQky+XvFQAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAABHSURBVBjThc7JDQAwCANBh9JcF61RG/lEysXh90jeYWYoR1JaoarSCgDSivTuFDF6RND0i7cpFNddJjYqxGqqBYDh7rUgOQFHVkEzCrmG9AAAAABJRU5ErkJggg==');
    background-repeat: no-repeat;
    background-position: 100% 100%;
  }
  
  .de-post-btns {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAIAAACpTQvdAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgNDjQdAWRDvwAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAA4SURBVCjPY/z//z8D0YCRkZGFgYHh/v37xKieO3cuAwMDC4RDpB4GBgYmBhLBqIbBoYEFHue0AgCEhAwI4t8xUgAAAABJRU5ErkJggg=='), url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAIAAACpTQvdAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgNDjQdAWRDvwAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAA4SURBVCjPY/z//z8D0YCRkZGFgYHh/v37xKieO3cuAwMDC4RDpB4GBgYmBhLBqIbBoYEFHue0AgCEhAwI4t8xUgAAAABJRU5ErkJggg==');
    background-repeat: no-repeat, no-repeat;
    background-position: calc(100% - 2px) 0, calc(100% - 20px) 0, 15px 0;
    background-origin: content-box;
  }
  
  .de-pview .de-post-btns, div[de-oppost] .de-post-btns, .de-win-buttons {
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAIAAACpTQvdAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgNDjQdAWRDvwAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAA4SURBVCjPY/z//z8D0YCRkZGFgYHh/v37xKieO3cuAwMDC4RDpB4GBgYmBhLBqIbBoYEFHue0AgCEhAwI4t8xUgAAAABJRU5ErkJggg=='), url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAIAAACpTQvdAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgNDjQdAWRDvwAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAA4SURBVCjPY/z//z8D0YCRkZGFgYHh/v37xKieO3cuAwMDC4RDpB4GBgYmBhLBqIbBoYEFHue0AgCEhAwI4t8xUgAAAABJRU5ErkJggg=='),      url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAIAAACpTQvdAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgNDjQdAWRDvwAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAA4SURBVCjPY/z//z8D0YCRkZGFgYHh/v37xKieO3cuAwMDC4RDpB4GBgYmBhLBqIbBoYEFHue0AgCEhAwI4t8xUgAAAABJRU5ErkJggg=='), url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAIAAACpTQvdAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgNDjQdAWRDvwAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAA4SURBVCjPY/z//z8D0YCRkZGFgYHh/v37xKieO3cuAwMDC4RDpB4GBgYmBhLBqIbBoYEFHue0AgCEhAwI4t8xUgAAAABJRU5ErkJggg==');
    background-repeat: no-repeat, no-repeat, no-repeat, no-repeat;
    background-position: calc(100% - 2px) 0, calc(100% - 20px) 0, calc(100% - 38px) 0, calc(100% - 56px) 0;
    background-origin: content-box;
  }
  
  /* button */
  .de-abtn, .de-post-btns + a, .adminbar a, button, input[type="submit"], input[type="button"], #de-panel-logo, body > form ~ table[border="1"] a, .de-btn-src, .de-cfg-tab, .filesize a {
    border-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAIAAAACDbGyAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAB3RJTUUH4QgLEQksCEOwqgAAABl0RVh0Q29tbWVudABDcmVhdGVkIHdpdGggR0lNUFeBDhcAAAAtSURBVAjXbckxEQAwDMNAuRf+DIzNUJIh1079UVJ3c0kqIAlgGzh7NgH1zt8AgmEL9PFGHB0AAAAASUVORK5CYII=') 2;
    border-image-width: 2px;
    background-color: #bdbdbd;
    display: inline-block;
    color: #000;
  }
  
  .de-thr-hid {
    color: #000;
    padding: 3px;
    margin: 0 10px;
    float: none;
    width: auto !important;
  }
  
  .de-thr-hid + div + br {
    display: none;
  }
  
  .de-abtn, .de-abtn:hover, .adminbar a, .adminbar a:hover {
    padding: 3px;
    color: black;
    text-decoration: none;
    min-width: 20px;
    text-align: center;
    font-size: 12px;
    margin: 2px 2px;
  }
  
  .de-thread-buttons {
    font-size: 0;
    margin: 0 18px;
  }
  
  .adminbar {
    margin: 0;
    padding: 3px;
    font-size: 0;
    float: none;
    text-align: left;
  }
  
  .footer, .de-thread-buttons::before {
    display: none;
  }
  
  /* Кнопка "Ответ" */
  .de-post-btns + a, .de-post-btns + a:hover {
    z-index: 1;
    color: black;
    text-decoration: none;
    height: 14px;
    line-height: 14px;
    font-weight: bold;
    padding: 0 3px;
    margin: 3px -1px 3px -5px;
  }
  
  .de-post-btns {
    display: flex;
    flex-direction: row-reverse;
    margin: 3px -4px 0 0;
    padding: 0;
    z-index: 1;
  }
  
  .de-post-btns span {
    display: none;
  }
  
  .de-btn-clear, .de-btn-close, .de-btn-expthr, .de-btn-fav, .de-btn-fav-sel, .de-btn-hide, .de-btn-hide-user, .de-btn-unhide, .de-btn-unhide-user, .de-btn-rep, .de-btn-sage, .de-btn-src, .de-btn-stick, .de-btn-stick-on, .de-btn-toggle {
    margin: -1px 2px 0 0 !important;
  }
  
  body {
    background-color: #008080;
    font-family: 'Arial',sans-serif;
    font-size: 12px;
    color: black;
    margin: 0;
    padding: 0;
    padding-bottom: 25px;
  }
  
  a:hover {
    color: inherit;
  }
  
  .doubledash {
    display: none;
  }
  
  body > form ~ table[border="1"] {
    width: 100%;
  }
  
  body > form ~ table[border="1"] + br {
    display: none;
  }
  
  body > form ~ table[border="1"] tr {
    display: flex;
  }
  
  body > form ~ table[border="1"] td {
    border: none;
    font-size: 0;
  }
  
  body > form ~ table[border="1"] a {
    text-decoration: none;
    color: #000;
    padding: 3px;
    height: 15px;
    font-size: 14px;
    margin-left: 2px;
    min-width: 21px;
    text-align: center;
  }
  
  td.reply > label,
  td.highlight > label,
  div[de-oppost] > label,
  .de-win-head,
  div[id^="thread"] > label {
    color: #fff;
    background: linear-gradient(to right, #000080, #1084d0);
    height: 14px;
    font-weight: bold;
    padding: 2px;
    margin: 5px 4px;
  }
  
  td.reply label,
  td.highlight label,
  div[de-oppost] label {
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    z-index: 0;
  }
  
  div[de-oppost] {
    float: left;
  }
  
  div[de-oppost] label > input,
  td.reply label > input,
  td.highlight label > input,
  div[id^="thread"] > label > input {
    display: none;
  }
  
  div[de-oppost] label > input:checked + span::before,
  td.reply label > input:checked + span::before,
  td.highlight label > input:checked + span::before,
  div[id^="thread"] > label > input:checked + span::before {
    content: '✕';
  }
  
  div[de-oppost] label > input + span::before,
  td.reply label > input + span::before,
  td.highlight label > input + span::before,
  div[id^="thread"] > label > input + span::before {
    content: '';
    background-image: url('https://iichan.hk/iichan_b.ico');
    background-position: center;
    background-size: 14px 14px;
    background-repeat: no-repeat;
    vertical-align: middle;
    display: inline-block;
    width: 14px;
    height: 14px;
    z-index: 2;
    font-weight: bold;
    font-size: 20px;
    line-height: 14px;
    color: red;
    text-align: start;
  }
  
  td.reply, td.highlight, div[de-oppost], .reply.de-pview {
    display: flex !important;
    flex-flow: row wrap;
    justify-content: flex-end;
    background: #c0c0c0;
    color: black;
    padding: 4px;
    min-width: 550px;
  }
  
  div[de-oppost] {
    margin-bottom: 5px;
  }
  
  div[id^="thread"] table {
    border-collapse: collapse;
    margin: 5px 20px;
    clear: both;
  }
  
  td.reply:not(.de-pview),
  td.highlight:not(.de-pview),
  div[de-oppost]:not(.de-pview) {
    position: relative;
  }
  
  td.reply blockquote,
  td.highlight blockquote,
  div[de-oppost] blockquote {
    padding: 7px 10px;
    flex: 1 1 100%;
    margin: 3px 0;
    font-size: 14px;
  }
  
  div[id^="thread"] {
    margin: 0 10px;
  }
  
  div[id^="thread"] > blockquote {
    min-width: 500px;
    padding: 25px 10px 7px 10px;
    margin: 10px;
  }
  
  div[de-oppost] > .filesize,
  td.reply > .flesize,
  td.highlight > .flesize {
    order: 1;
  }
  
  div[de-oppost] > .de-video-obj,
  td.reply > .de-video-obj,
  td.highlight > .de-video-obj {
    order: 2;
  }
  
  div[de-oppost] > a[target="_blank"],
  td.reply > a[target="_blank"],
  td.highlight > a[target="_blank"] {
    order: 3;
  }
  
  div[de-oppost] > blockquote,
  td.reply > blockquote,
  td.highlight > blockquote {
    order: 4;
  }
  
  .de-refmap {
    order: 5;
  }
  
  .omittedposts {
    float: left;
    clear: both;
    width: 100%;
    color: #fff;
    order: 6;
  }
  
  td.reply > blockquote:-moz-only-whitespace,
  td.highlight > blockquote:-moz-only-whitespace,
  div[de-oppost] > blockquote:-moz-only-whitespace,
  div[id^="thread"] > blockquote:-moz-only-whitespace {
    visibility: hidden;
  }
  
  td.reply blockquote p,
  td.highlight blockquote p,
  td.reply blockquote blockquote,
  td.highlight blockquote blockquote,
  div[de-oppost] blockquote p,
  div[de-oppost] blockquote blockquote,
  div[id^="thread"] > blockquote p,
  div[id^="thread"] > blockquote blockquote {
    margin: 0;
    padding: 0;
  }
  
  /* toolbar */
  td.reply .filesize,
  td.highlight .filesize,
  div[de-oppost] .filesize {
    flex: 1 1 100%;
    display: block;
    padding: 5px 4px 3px 4px;
    margin: 2px 0;
    font-size: 0;
  }
  
  /* toolbar grip */
  td.reply .filesize::before,
  td.highlight .filesize::before,
  div[de-oppost] .filesize::before {
    content: '';
    width: 3px;
    height: 18px;
    display: block;
    float: left;
    margin: 1px 3px 1px 1px;
  }
  
  .filesize a {
    font-size: 12px;
    display: inline-block;
    height: 21px;
    line-height: 21px;
    margin: -1px 2px 0 0;
    vertical-align: top;
    padding: 0 4px;
  }
  
  /* thumbnail */
  td.reply > a[target="_blank"],
  td.highlight > a[target="_blank"],
  div[de-oppost] > a[target="_blank"],
  td.reply > .nothumb,
  td.highlight > .nothumb,
  div[de-oppost] > .nothumb {
    margin: 4px 7px 4px 0;
    flex: 0 1 0;
    display: flex;
    justify-content: center;
    flex-direction: column;
  }
  
  div[de-oppost] > a[target="_blank"], div[de-oppost] > .nothumb {
    margin-left: 3px;
  }
  
  td.reply > a[target="_blank"] + blockquote,
  td.highlight > a[target="_blank"] + blockquote,
  div[de-oppost] > a[target="_blank"] ~ blockquote,
  td.reply > .de-video-obj + blockquote,
  td.highlight > .de-video-obj + blockquote,
  div[de-oppost] > .de-video-obj ~ blockquote,
  td.reply > .nothumb + blockquote,
  td.highlight > .nothumb + blockquote,
  div[de-oppost] > .nothumb ~ blockquote {
    flex: 2 0 0;
  }
  
  .thumb, .nothumb {
    margin: 0;
  }
  
  .iichan-image-fullsize {
    max-width: 1000px;
  }
  
  .reflink {
    color: white;
    margin: 2px 2px 0;
    padding: 1px 2px;
    z-index: 1;
  }
  
  .reflink::after {
    display: none;
  }
  
  .de-refmap {
    flex: 1 1 100%;
    margin: 0;
    border: 1px solid;
    border-top-color: #7b7b7b;
    border-left-color: #7b7b7b;
    border-bottom-color: #fff;
    border-right-color: #fff;
    padding: 2px;
  }
  
  .omittedposts {
    margin: 0;
    flex: 1 1 100%;
    border: 1px solid;
    border-top-color: #7b7b7b;
    border-left-color: #7b7b7b;
    border-bottom-color: #fff;
    border-right-color: #fff;
    padding: 2px;
    color: #000;
  }
  
  .filesize ~ .de-video-obj {
    flex: 0 1 auto;
  }
  
  .de-video-obj {
    flex: 0 1 100%;
    margin: 4px 20px 4px 3px;
  }
  
  a.de-video-player {
    width: auto;
  }
  
  .de-video-thumb {
    width: auto;
    height: auto;
    max-width: 100%;
    max-height: 100%;
  }
  
  .postername, .commentpostername, .replytitle, .filetitle, .reflink, .de-post-counter::after {
    color: white;
    font-size: 12px;
    background: none;
    font-weight: bold;
    font-family: 'Arial',sans-serif;
  }
  
  .de-win-head, #de-win-reply {
    border-radius: 0;
  }
  
  .de-parea > div:first-child {
    font-size: 0;
  }
  
  .userdelete {
    padding: 4px;
    clear: both;
    margin: 4px;
  }
  
  hr {
    clear: both;
  }
  
  .logo {
    color: #fff;
  }
  
  .de-btn-src {
    height: 21px;
    width: 21px;
    
  }
  
  #de-panel-logo, .de-panel-button {
    padding: 3px;
  }
  
  #de-panel {
    background-image: none;
    left: 0;
    color: #000;
    padding: 2px;
    height: 21px;
  }
  
  .de-win-body, .de-cfg-body, #de-cfg-buttons, #de-cfg-bar, .de-entry, .postblock {
    border: none !important;
    color: #000;
    background-image: none !important;
  }
  
  .de-win-body, .de-cfg-body, .de-cfg-tab, .de-cfg-tab:hover, #de-cfg-buttons, #de-cfg-bar, .de-entry, .postblock {
    background: transparent !important;
    color: #000;
  }
  
  .de-cf-bar {
    background: transparent;
  }
  
  #de-win-reply {
    clear: both;
  }
  
  #de-spell-rowmeter {
    margin-left: 2px;
  }
  
  .de-win-buttons {
    color: #000;
    height: 20px;
  }
  
  .de-menu {
    color: #000;
  }
  
  .de-menu :not(:last-child) {
    border-bottom: 1px solid gray;
  }
  
  .de-win-open {
    animation: none;
  }
  
  select, .de-cfg-select {
    padding: 0;
  }
  
  #de-win-reply.de-win {
    border-radius: 0;
  }
  
  #de-panel-buttons {
    flex: 1 1 auto;
  }
  
  #de-panel-info {
    margin-left: auto;
    background: #bdbdbd;
    font-size: 12pt;
    font-family: 'Arial',sans-serif;
    padding: 2px 10px;
    align-self: self-end;
    height: 20px;
    line-height: 20px;
  }
  
  .de-panel-button, #de-panel-logo {
    height: auto;
    width: auto;
    line-height: 10px;
  }
  
  .de-panel-button::after, #de-panel-logo::after {
    vertical-align: top;
    display: inline-block;
    height: 15px;
    line-height: 15px;
    padding-right: 3px;
  }
  
  .de-panel-svg, .de-panel-logo-svg {
    width: 15px;
    height: 15px;
    display: inline-block;
  }
  
  #de-panel-goup {
    transform: none;
  }
  
  #de-panel-goup .de-panel-svg {
    transform: rotate(-90deg);
  }
  
  #de-panel-goback {
    transform: none;
  }
  
  #de-panel-goback .de-panel-svg {
    transform: rotate(180deg);
  }
  
  #de-panel-godown {
    transform: none;
  }
  
  #de-panel-godown .de-panel-svg {
    transform: rotate(90deg);
  }
  
  @media (min-width: 1400px) {
    #de-panel-logo::after {
      content: 'Пуск';
    }

    #de-panel-cfg::after {
      content: 'Настройки';
    }

    #de-panel-hid::after {
      content: 'Скрытое';
    }

    #de-panel-fav::after {
      content: 'Избранное';
    }

    #de-panel-vid::after {
      content: 'Видео';
    }

    #de-panel-refresh::after {
      content: 'Обновить';
    }

    #de-panel-goback::after {
      content: 'Назад';
    }

    #de-panel-gonext::after {
      content: 'Вперед';
    }

    #de-panel-goup::after {
      content: 'Вверх';
    }

    #de-panel-godown::after {
      content: 'Вниз';
