css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("410chan.org"), domain("iichan.hk"), domain("nowere.net") {
  html {
    height: 100%;
  }
  
  body {
    min-height: 100%;
    overflow-y: scroll;
    margin: 0;
    padding: 0;
  }
  
  body > form, body > div, body > span, body > p, body > table {
    width: 100%;
    max-width: 1000px;
    margin: 0 auto;
  }
  
  body > .adminbar {
    float: none;
  }

  .logo img {
    margin-top: 40px;
  }
  
  .newthread, .head {
    margin: 1em auto;
  }
  
  /* 410chan.org */
  .topmenu {
    position: fixed;
    max-width: 100%;
    right: 0;
    margin: 0;
  }
}
