css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("gmx.net") {

/* width */
#main {width:100% !important}
#nav-sub {width:16% !important;float:left !important;max-width:14em}
#grid-saver {display:none !important}
div.content,
div.breadcrumb {width:auto !important;float:left !important;margin-left:17% !important;clear:both !important}
div.supply.ab {width:auto !important;float:left !important}
table.x8 {width:auto !important}

/* colors, backgrounds, links */
* {background:transparent none !important;border:0 !important}
body * {background:#fff;color:#888 !important;font-family:monospace;line-height:1.6em}
div.supply * {color:#000 !important}
a {font-weight:500 !important;text-decoration:none !important}
a:visited {color:#888 !important}
a:hover {color:#000 !important}

/* left column */
#nav-sub li * {background-color:#000 !important;color:#fff !important}
#nav-sub ul li li li {border-bottom:1px solid #000 !important}
#nav-sub ul li li li:hover {border-bottom:1px solid #fff !important}
div.end {-moz-border-radius:0 0 1em 0}


/* table layout */
thead, tfoot {line-height:3em}
thead th {border-bottom:1px solid #000 !important}
tfoot th {border-top:1px solid #000 !important;padding-top:1em !important}
th a {font-weight:700 !important}


/* crappy images */
img {display:none !important}
table.special a img {display:inline !important}
td img {display:inline !important}


/* crap */
div.supply p,
div.help,
div.index,
#nav-top,
#footer,
#adv,
#content-start {display:none !important}


/* important details */
div.supply p#alternativeLink,
div.supply div.form form#MI p {display:block !important}
div.supply div.form form#MI p a strong {font-weight:500 !important}


/* menu crap */
li.service {padding-top:0 !important}
ul.l2 li a {display:none !important}
ul.l2 li li a {display:block !important}


/* useless links */
a[href^="http://service.gmx.net/de/cgi/edcards"] {visibility:hidden !important}
a[href^="http://service.gmx.net/de/cgi/g.fcgi/pushmail"] {visibility:hidden !important}
a[href^="http://service.gmx.net/de/cgi/g.fcgi/imap"] {visibility:hidden !important}
a[href^="http://service.gmx.net/de/cgi/g.fcgi/config/sms/"] {visibility:hidden !important}
a[href^="http://service.gmx.net/de/cgi/g.fcgi/config/mailing/infoservice"] {visibility:hidden !important}


/* useful links */
a[href$="DELETE"] {color:#c00 !important}
a[href^="http://service.gmx.net/de/cgi/g.fcgi/mail/reply?"] {color:#080 !important;font-weight:700 !important}
a[href$="all=true"] {color:#000 !important;font-weight:500 !important}


/* search */
#mboxSearchForm,
#mboxSearchDiv {border:1px solid #000 !important;background-color:#fff !important}


/* bugfixes */
table.special {margin-top:2.8em !important}



/* homepage */
#top #wrapper #header,
#top #wrapper #statusline,
#top #container #sitebar,
#top #content .channels,
#top #content .news,
#iframe,
#navigation
  {display:none !important}

#startBottom,
#startBottom img {display:block !important}

/* inbox */
#nav-top-bin a strong {display:block !important;position:absolute !important;right:0 !important;top:0 !important;background:#fff !important;border:1px solid #000 !important;width:5em !important;height:2em !important;padding:0.2em !important;z-index:9999 !important}
#nav-top-bin a strong:hover {border:1px solid #c00 !important}
.ab {margin-top:48px !important}


}
