css:
@-moz-document domain("forum.pt-dorian.net") {
#Frame {
	background-color: #232222;
}
.Banner {
	background-color: #1f1f1f;
}
#Head {
	box-shadow: inset 0 5px 5px 0 #252525, inset 0 -5px 5px 0 #353535!important;
	background: #252525!important;
	border: 1px solid #313131!important;
}
.BreadcrumbsWrapper {
	border-bottom: 1px dotted #6b6b6b!important;
	background-color: #252525;
}
#Body {
	background-color: #252525;
}
.Item.Read {
	background: #252525!important;
}
.Box h4 {
	color: white;
}
.PanelInfo li {
	background-color: #252525!important;
}
.SiteTitle a,
.Discussion .PageTitle h1,
.CommentHeading,
.Message,
.H,
.Excerpt {
	color: #fff!important;
}
.Message img {
	background-color: #ffffff!important;
}
.Item.Alt.Read.ItemDiscussion .ItemContent.Discussion .Title a,
.Item.Read.ItemDiscussion .ItemContent.Discussion .Title a {
	color: #929292!important
}
.Item.New.ItemDiscussion .ItemContent.Discussion .Title a {
	color: #fff!important
}
.Item.Alt.Unread.ItemDiscussion .ItemContent.Discussion .Title a,
.Item.Unread.ItemDiscussion .ItemContent.Discussion .Title a {
	color: #fff!important;
	font-weight: normal;
}
}
