css:
@-moz-document url-prefix("https://www.pixiv.net/member_illust.php?mode=medium&") {
.share-link-container,
.worksShare,
div[style="height:10px"],
.worksOptionRightInfo,
._shortcut-help,
._user-profile-card-badges,
#one_comment,
._melonbooks-dl-promotion-container,
._booth-promotion-container {
  display: none !important;
}

section.reaction-container {
  display: block !important;
}

div.bookmark-container {
  float: right;
  height: 40px;
}

#wrapper,
.layout-column-2 {
  width: 100%;
}

.layout-body,
.layout-a,
.works_display {
  width: auto !important;
}

.works_display img {
  max-width: 100% !important;
}

._unit {
  border-radius: 0 !important;
}

._work-detail-unit {
  display: table;
  width: auto;
  margin: auto;
  border: 0;
  background-color: rgba(255, 255, 255, 0.75);
  border-bottom-left-radius: 5px !important;
  border-bottom-right-radius: 5px !important;
}

#illust-recommend {
  float: none;
  padding: 0;
  display: table-footer-group;
}

#illust-recommend h1 {
  text-align: center;
}

#illust-recommend ul,
.layout-a,
#wrapper {
  margin: 0 !important;
}

.layout-a .layout-column-2,
.layout-a .layout-column-1,
.ui-layout-west,
.area_new {
  float: none;
  width: auto;
  margin-bottom: 20px;
}

.user-tags {
  margin: auto;
  margin-bottom: 10px;
}

.profile-module {
  margin-bottom: 0 !important;
}

div.column-header > nav,
section.work-info,
section.work-tags,
section.reaction-container,
nav.column-menu,
.user-tags {
  min-width: 1000px;
  width: 60%;
  margin: auto;
}

nav.column-menu {
  margin-top: 0 !important;
  border-left: 1px solid rgb(214, 222, 229);
  border-right: 1px solid rgb(214, 222, 229);
  border-bottom: 1px solid rgb(214, 222, 229);
  border-bottom-left-radius: 2px;
  border-bottom-right-radius: 2px;
}

#illust-recommend._unit {
  background: none;
}

@media only screen and (min-width: 1515px) {
  ._user-profile-card {
    position: absolute;
    top: 50px;
    left: 0;
    border: 0;
  }
}

@media only screen and (max-width: 1515px) {
  ._user-profile-card {
    display: none;
  }
}
}

@-moz-document domain("pixiv.net") {
.ad,
.ads_area,
.multi-ads-area,
.ad-footer,
.ad-printservice,
ul._toolmenu,
#toolbar-items,
._popular-introduction,
.comic-hot-works,
footer._classic-footer,
.ad-multiple_illust_viewer,
nav.category-list,
h1.header-logo,
.ads_anchor,
a.message,
a._premium-badge,
a[href="/premium.php?ref=header"],
.ad-bigbanner,
nav.column-menu + br,
.comic-container,
.dic-ranking,
#page-mypage .area_new,
.item.fanbox,
.item.spotlight,
.item.contests,
.item.booth-follow-items,
.followbooster-after-bookmark-caption,
.thumbnail-menu {
  display: none !important;
}

._global-header .layout-wrapper {
  height: 45px;
}

._global-header .ui-search {
  right: 178px !important;
  bottom: 9px !important;
}

._unit.complete-unit {
  padding-bottom: 0 !important;
  border-bottom: 0;
}

._share-links {
  display: none !important;
}
}
