css:
@-moz-document domain("www.google.com") {
#viewport{
    /*http://i.imgur.com/hoUDBwo.png*/
    background: url(http://i.imgur.com/hoUDBwo.png) no-repeat;
    background-size: cover;
    /* width: 100%; */
    /* height: 100%; */
    background-position: center center;
}

/*Google search bar*/
#viewport .sbibod{
    background-color: transparent;
     border-radius: 10px;
}
#viewport .gstl_0.sbib_a{
    background-color: rgba(255,255,255,.3);
    border-radius: 10px;
}


#viewport .sbdd_b, .sbsb_a {
    background: transparent;
    border-radius: 0 0 10px 10px;
}

#hdtb{
    border-bottom: none !important;
    box-shadow: 0px 0px 15px 3px rgba(0, 0, 0, .5);
}

/*Google search recent searches dropdown*/
#viewport ul.sbsb_b{ 
    background-color: rgba(255, 255, 255, .5) !important;
    border-radius: 0 0 10px 10px;
}

#viewport ul.sbsb_b li{
    padding: 5px;
}
#viewport ul.sbsb_b li:hover{
    background-color: rgba(255, 0,0, .5);
}
#viewport ul.sbsb_b li:nth-last-child(1):hover, #viewport ul.sbsb_b li:nth-last-child(2):hover{
    background-color: rgba(255, 255, 255, 0) !important;
}
#viewport ul.sbsb_b li:hover span, ul.sbsb_b li:hover a {
    color: whitesmoke;
}


/*google image logo*/
/*https://orig11.deviantart.net/4e4b/f/2012/253/c/f/mario_bros_google_doodle_by_alexphantascy-d5ebnz9.png*/

#hplogo{
 content:url("https://orig11.deviantart.net/4e4b/f/2012/253/c/f/mario_bros_google_doodle_by_alexphantascy-d5ebnz9.png"); 
}


/* View Saved on Images button click*/
/*End View Saved*/
#ab_ctls{
  padding-top:18px !important;
}
.ab_ctl{
padding: 10px;
    border-radius: 5px;
}
.ab_ctl:first-child{
    background-color: gold;
}
.ab_ctl:last-child{
    background-color: rgba(255, 255, 255, .3);
    color: white !important;
}
/*Footer*/
#viewport #fbar{
  background: rgba(0,0,0,.9);
  border-top: 1px solid black;
}


/*search found google page*/
/*header*/
.srp #searchform .sfbg.nojsv, .sfbgg, #top_nav, #hdtbSum, .sfbgx{
    background-color: #1565C0 !important;
    position: fixed;
    width: 100%;
    z-index: 10;
}
.srp #searchform{
    position: fixed;
}
.tsf{
    z-index: 11;
}

#resultStats, #rcnt{
    margin-top: 40px;
    /*z-index: -1;*/
}

/*header links*/
#hdtb-msb .hdtb-mitem.hdtb-msel, #hdtb-msb .hdtb-mitem.hdtb-msel-pre div{
    color: gold !important;
    border-bottom-color: gold !important;
    width: auto !important;
}

.hdtb-mitem.hdtb-imb a{
    color: rgba(255, 255, 255 ,.7) !important;
}

#hdtb-msb-vis div{
    text-align: center;
	width: 85px;
}
#hdtb-msb-vis div:hover{
    background-color: rgba(255, 255, 255, 0);
    padding: 0 0 12px 0;
}
#hdtb-msb-vis div:hover a{
	font-weight: bold;
}

#hdtb-msb-vis div:nth-child(2):hover{
    border-bottom: 3px solid #FF6363 !important;
}
#hdtb-msb-vis div:nth-child(2):hover a{
    color: #FF6363 !important;
}

#hdtb-msb-vis div:nth-child(3):hover{
    border-bottom: 3px solid #0093FC !important;
}
#hdtb-msb-vis div:nth-child(3):hover a{
    color: #0093FC !important;
}

#hdtb-msb-vis div:nth-child(4):hover{
    border-bottom: 3px solid #00AA11 !important;
}
#hdtb-msb-vis div:nth-child(4):hover a{
    color: #00AA11 !important;
}

#hdtb-msb-vis div:nth-child(5):hover{
    border-bottom: 3px solid gold !important;
}
#hdtb-msb-vis div:nth-child(5):hover a{
    color: gold !important;
}

.srp a[role=button]{    
    background-color: rgba(255,255,255,.3);
    color: rgba(255,255,255,.7) !important;
	border: 1px solid rgba(255,255,255,0) !important;
    border-radius: 5px;
}

.srp a[role=button]:hover{
    background-color: rgba(255, 255,255, .1) !important;
}
  
  .srp .hdtb-tl{
   background: rgba(255,255,255,.3) !important;
   padding: 6px;
}

/*search bar .srp is the body*/
.srp .sfibbbc .gstl_0.sbib_a{
    background-color: rgba(255, 255, 255, .5) !important;
  }

.sbibod{
     background-color: rgba(255, 255, 255, .1) !important;
}
.sbibod input{
     background-color: rgba(255, 255, 255, 0) !important;
}

/*search icon*/
.sbico-c{
    background-color: gold;
    margin: 0;
    top: 0;
}

/*search bar suggestions*/
.srp .sfibbbc ul li{
    padding: 10px;
}
.srp .sfibbbc ul li:hover{
    background-color: rgba(255, 0, 0, .5);
    color: whitesmoke;
}

.sbibod {
    background-color: transparent;
}

.srp .sfibbbc input{
    color: white;
}

/*Report inappropriate predictions*/
#sbfblt {
    background-color: #1565C0;
    color: whitesmoke;
    position: relative;
    bottom: 15px;
}
}
