css:
@-moz-document domain("filelist.io") {

body,html
{
   background: #111619;
   letter-spacing: 0.1px;
   line-height: 19px;
}


.mainheader {
    width: 100%;
    height: 250px;
    background: #1a2025;
    min-width: 1050px;
}

#navigation {
    top: 170px;
    width: 972px;
    height: 44px;
    margin: 0 5px;
    font-family: Helvetica,Arial,Verdana,sans-serif;
    position: absolute;
    background: rgba(0, 0, 0, 0.28);
    border: none;
    border-radius: 5px;
    box-shadow: rgba(0, 0, 0, 0.25) 0px 0px 2px;
    color: #fff;
    text-shadow: none;
}

#nav ul li, #nav ul li a {
    color: #32404c !important;
    text-decoration: none;
    font-weight: lighter;
    letter-spacing: 0.1px;
    text-shadow: none !important;
    transition: all 0.5s;
}

#nav ul li:hover, #nav ul li a:hover {
    color: #fff !important;
}

.statusbar {
    width: 425px;
    margin-top: 50px;
    padding: 5px 5px 5px 5px;
    color: #6b8398;
    float: right;
    font-size: 10px;
    background: #222a31;
    border: none;
    border-radius: 4px;
}

a {
    color: #8eaac1 !important;
    transition: all 0.5s;
    text-shadow: #000 0px 0px 4px;
}

a { color: #4f6e88 !important; transition: all 0.5s;}
a:hover { color: #9ed0f9 !important; }
.visitedlinks a:visited {
    text-decoration: none;
    color: #2b3b50 !important;
}
.logo {
    width: 400px;
    height: 180px;
    float: left;
}

.globalFreeLeech {
    width: 60%;
    background: #1a2025;
    border: 1px solid #252d35;
    color: #E0E0E0;
    opacity: 0.9;
    margin: 0px auto;
    padding: 7px;
    font-weight: bold;
}

.cblock-content {
    width: 978px;
    padding: 10px;
    background: rgb(11, 14, 16);
    position: relative;
    border-radius: 10px;
}

.cblock-top {
    width: 977px;
    height: 6px;
    display: block;
    background: none;
}

input, select, input.btn{
    background: #111619;
    border: none !important;
    color: #fff !important;
    box-shadow: none !important;
}

select option {
    color: #fff !important;
}

.pager a, .pager a:link {
    background: #111619 !important;
    border: none !important;
    color: #fff !important;
}

.pager span.current {
    margin: 0 2px;
    padding: 4px 6px;
    background: #202c33;
    border: none;
    font-weight: bold;
    font-size: 8pt;
    color: #FFFFFF;
    border-radius: 3px;
}

.colhead {
    font-weight: bold;
    color: #E0E0E0;
    background: #151a1d;
    border: 1px solid #444;
    padding: 5px;
}

.torrenttable {
    float: left;
    padding: 2px 0px;
    border: none;
    border-bottom: solid 1px rgba(253, 254, 255, 0.01);
}

.torrenttable img {
    border-radius: 9px;
}

::-webkit-scrollbar {
    width: 1em;
}
 
::-webkit-scrollbar-track {
    background: transparent;
}
 
::-webkit-scrollbar-thumb {
  background-color: #1c2429;
}

.cblock-bottom {
    background: none;
}

#footer {
    background: #0b0e10;
    padding-top: 35px;
    padding-bottom: 15px;
    width: 100%;
    height: 110px;
    bottom: 55px;
    position: absolute;
    min-width: 1050px;
}

#footerInner h2 {
    padding-bottom: 4px;
    margin-bottom: 14px;
    color: #36576d;
    font-size: 15px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.02);
    font-family: Arial;
}

#copyright {
    border-top: none;
    background: rgb(11, 14, 16);
    width: 100%;
    height: 55px;
    bottom: 0;
    position: absolute;
    min-width: 1050px;
}

fieldset {
    padding: 1em;
    border: 1px solid #13181b;
    background: #111619;
}
.cblock-header {
    width: 965px;
    height: 30px;
    float: left;
    background: #1a2025;
    position: relative;
}

.cblock-headerleft {
    width: 6px;
    height: 30px;
    display: block;
    float: left;
    background: #1a2025;
}

.cblock-headerright {
    width: 6px;
    height: 30px;
    display: block;
    float: left;
    background: #1a2025;
}

legend {
    padding: 2px 4px;
    background: #1d2529;
    color: #FFFFFF;
}

textarea {
    background: #161c1f;
    border: none;
    color: #fff;
}

input.lbtn {
    background: #0b0e10 !important;
    height: 20px;
    padding: 2px 0px 4px 0px;
    font-size: 12px !important;
    border: none !important;
    cursor: pointer;
    color: #fff !important;
}

.nblock-content {
    width: 684px;
    height: 100%;
    padding: 20px 0 10px 0;
    background: #1a2025;
    position: relative;
}

.colhead {
    font-weight: bold;
    color: #E0E0E0;
    background: #151a1d;
    border: none;
    padding: 5px;
}

.nblock-header {
    width: 671px;
    height: 30px;
    float: left;
    background: #2a353e;
    position: relative;
}

.nblock-headerright {
    width: 6px;
    height: 30px;
    display: block;
    float: left;
    background: #2a353e;
}

.nblock-headerleft {
    width: 6px;
    height: 30px;
    display: block;
    float: left;
    background: #2a353e;
}

.sblock-header {
    width: 267px;
    height: 30px;
    float: left;
    background: #2a353e;
    position: relative;
}

.sblock-headerleft {
    width: 6px;
    height: 30px;
    display: block;
    float: left;
    background: #2a353e;
}

.sblock-headerright {
    width: 6px;
    height: 30px;
    display: block;
    float: left;
    background: #2a353e;
}

.sblock-content {
    width: 280px;
    height: 100%;
    padding: 20px 0 10px 0;
    background: #151a1d;
    position: relative;
}

#poll_separator {
    margin-top: 5px;
    padding-top: 5px;
    border-top: 1px solid rgba(237, 248, 255, 0.01);
}

.sblock-bottom {
    width: 279px;
    height: 6px;
    display: block;
    background: #161c1f;
    margin-bottom: 20px;
}


td.colhead {
    font-weight: bold;
    color: #E0E0E0;
    background: #0b0e10;
    padding: 5px;
}

td {
    font-size: 8pt;
    border: 1px solid rgb(17, 22, 25);
    color: #E0E0E0;
}

table.main, table.mainouter {
    background-color: #101315;
    /* padding: 15px !important; */
}
}
