css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hooktube.com") {
  
  a {
    color: #81a2be;
    text-decoration: none;
    background-color: #1d1f21;
    -webkit-text-decoration-skip: objects;
}
  
  .badge {
    display: inline-block;
    padding: .25em .4em;
    font-size: 75%;
    font-weight: 700;
    line-height: 1;
    color: #969896;
    text-align: center;
    white-space: nowrap;
    vertical-align: baseline;
    border-radius: .25rem;
}
  
  body {
    margin: 0;
    font-family: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #c5c8c6;
    background-color: #1d1f21;
}
  
  .btn-block {
    background-color: #1d1f21;
    color: #b4b7b4;
    display: block;
    width: 100%;
}
  
  .btn-secondary {
    color: #fff;
    background-color: #1d1f21;
    border: none;
}
  
  .btn-secondary:hover{
    background-color: #0c0d0e;
    border: none;
  }
  
  .btn-success {
    color: #b5bd68;
    background-color: #1d1f21;
    border: none;
}
  
  .card {
    color: #969896;
    font: normal 15px Consolas;
    position: relative;
    left: -15px;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #1d1f21;
    background-clip: border-box;
    border: none;
    border-radius: .25rem;
}
  
  .card-title {
    color: #c5c8c6;
    font: bold 18.5px Consolas;
    margin-bottom: .45rem;
}
  
  .close {
    float: right;
    font-size: 1.5rem;
    font-weight: 700;
    line-height: 1;
    color: #c5c8c6;
    text-shadow: none;
    opacity: 0.8;
}
  
  #comments .row {
    color: #c5c8c6;
    font: normal 15px Consolas;
    padding-top: 2rem;
}
  
  .h5, h5 {
    color: #c5c8c6;
    font-family: Consolas;
    font-size: 1rem;
}
  
  .form-control {
    display: block;
    width: 100%;
    padding: .5rem .75rem;
    font-size: 1rem;
    font-family: Consolas;
    line-height: 1.25;
    color: #969896 !important;
    background-color: #0c0d0e !important;
    background-image: none;
    background-clip: padding-box;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: .25rem;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
  
  #generic-title {
    color: #969896 !important;
    padding-bottom: 1rem;
}
  
  .h4, h4 {
    color: #969896 !important;
    font-size: 1.2rem;
}
  
.jumbotron {
    padding: 2rem 1rem;
    margin-bottom: 2rem;
    background-color: #1d1f21 !important;
    border-radius: .3rem;
}
  
  .list-channel-title {
    color: #c5c8c6;
}
  
  .modal-content {
    position: relative;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    background-color: #1d1f21;
    background-clip: padding-box;
    border: 1px solid rgba(0,0,0,.2);
    border-radius: .3rem;
    outline: 0;
}
  
  .modal-header {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-align: center;
    align-items: center;
    -ms-flex-pack: justify;
    justify-content: space-between;
    padding: 15px;
    border-bottom: none;
}
  
  .modal-title {
    font: normal 16px Consolas;
    color: #c5c8c6;
    margin-bottom: 0;
    line-height: 1.5;
}
  
  ::-moz-selection {
  background: #424242 ; /* Gecko Browsers */
}
  
.navbar {
    padding-bottom: 0;
    padding-top: 0;
    background-color: #1d1f21;
    border-bottom: none;
}
.navbar-brand img {
    width: 100px;
    height: 50px;
    box-sizing: border-box;
    padding-left: 160px;
    background: url(https://i.imgur.com/4k1jIrC.png) left top no-repeat;
}
  
  .navbar-light .navbar-nav .active > .nav-link, .navbar-light .navbar-nav .nav-link.active, .navbar-light .navbar-nav .nav-link.show, .navbar-light .navbar-nav .show > .nav-link {
    color: #c5c8c6;
}
  
  p {
    color: #c5c8c6;
    font: normal 13px Consolas;
    margin-top: 0;
    margin-bottom: 1rem;
}
  
  .progress {
    display: -ms-flexbox;
    display: flex;
    overflow: hidden;
    font-size: .75rem;
    line-height: 1rem;
    text-align: center;
    background-color: #373b41;
    border-radius: .25rem;
}
  
  #progress-bar-likes div {
    background-color: #D2D2D2;
}
  
  #search-button {
    color: #c5c8c6;
    background-color: #0c0d0e;
  }
  
  .text-left {
    color: #c5c8c6;
    font: normal 16.5px Consolas;
    text-align: left !important;
}
  
  .text-primary {
    color: #6699cc !important;
}
  
  .text-right {
    color: #c5c8c6;
    font: normal 14px Consolas;
    text-align: right !important;
}
  
  #video-keywords .badge {
    border: 1px solid #e0e0e0;
    background: none;
}
  
    #video-settings{
    color: #c5c8c6;
    background-color: #1d1f21;
  }

}
