css:
@-moz-document domain("phpdeveloper.org") {
@font-face {
  font-family: 'Open Sans';
  src: local('Open Sans'), local('OpenSans'), /* in case the font is installed */
       url(http://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}


.container-fluid > .row > .col-md-12 > .row > .col-md-3 {
    display: none;
}
.container-fluid > .row > .col-md-12 > .row > .col-md-9 > div,
.container-fluid > .row > .col-md-12 > .row > .col-md-9 > br:first-child {
    display: none;
}
.container-fluid > .row:first-child,
.container-fluid > .row:nth-child(2),
.container-fluid > .row:last-child {
    display: none;
}
body {
    font-size: 15px;
    font-family: 'Open Sans';
    background: #333;
    color: #fff;
}
a {
    color: #fff;
}
.container-fluid {
    max-width: 900px;
    margin: 0 auto;
}
#archive_table {
    max-width: 900px;
}
#archive_table td {
    padding: 2px 2px;
}
#archive_table td.archive_day {
    padding-top: 35px;
}
#archive_table td.archive_day a {
    color: #eee;
}
#archive_table td img {
    display: none;
}
#archive_table td a.archive_link {
    color: #eee;
}
#archive_table td a {
    font-size: inherit !important;
}
#archive_table td.link_date {
    display: none;
}
footer {
    display: none
}
}
