css:
@-moz-document url-prefix('https://cybre.space/web/') {
@font-face {
  font-family:"premillenium";
  src: url('http://cybre.space/extra/MSSansSerif.ttf') format('truetype');
}

body {
  font-size:13px;
  font-family: "MS Sans Serif", "premillenium", sans-serif;
  color:black;
}

.ui .columns-area {
  background: #007878;
}

.column > .scrollable {
  background: #bfbfbf;
  border: 2px outset #bfbfbf;
  border-top-width:0px;
}

.column-header__wrapper {
  color:white;
  font-weight:bold;
  background:#7f7f7f;
}

.column-header {
  padding:2px;
  font-size:13px;
  background:#7f7f7f;
  border: 2px outset #bfbfbf;
  border-bottom-width:0px;
  color:white;
  font-weight:bold;
}

.column-header__wrapper.active {
  background:#00007f;
}

.column-header__wrapper.active::before {
  display:none;
}
.column-header.active {
  box-shadow:unset;
  background:#00007f;
}

.column-header.active .column-header__icon {
  color:white;
}

.column-header__button {
  background: #bfbfbf;
  color: black;
  line-height:0px;
  font-size:14px;
  max-height:20px;
  padding:0px 2px;
  margin-top:2px;
  border: 2px outset #bfbfbf;
  border-top-width:1px;
  border-left-width:1px;
}

.column-header__button.active, .column-header__button.active:hover {
  border:2px inset #bfbfbf;
  background-color:#7f7f7f;
}

.column-header__back-button {
  background: #bfbfbf;
  color: black;
  padding:2px;
  max-height:20px;
  margin-top:2px;
  border: 2px outset #bfbfbf;
  border-top-width:1px;
  border-left-width:1px;
  font-size:13px;
  font-weight:bold;
}

.column-back-button {
  background:#bfbfbf;
  color:black;
  border:2px outset #bfbfbf;
  padding:2px;
  font-size:13px;
  font-weight:bold;
}

.column-back-button--slim-button {
  position:absolute;
  top:-22px;
  right:4px;
  max-height:20px;
  max-width:60px;
  padding:0px 2px;
}

.column-back-button__icon {
  font-size:11px;
  margin-top:-3px;
}

.column-header__collapsible {
  border-left:2px outset #bfbfbf;
  border-right:2px outset #bfbfbf;
}

.column-header__collapsible-inner {
  background:#bfbfbf;
  color:black;
}

.column-header__collapsible__extra {
  color:black;
}

.column-header__collapsible__extra div[role="group"] {
  border: 2px groove #bfbfbf;
  border-radius:4px;
  margin-bottom:8px;
  padding:4px;
}

.column-settings__section {
  color:black;
  font-weight:bold;
  font-size:11px;
  position:relative;
  top: -12px;
  left:4px;
  background-color:#bfbfbf;
  display:inline-block;
  padding:0px 4px;
  margin-bottom:0px;
}

.setting-meta__label, .setting-toggle__label {
  color:black;
  font-weight:normal;
}

.setting-meta__label span:before {
  content:"(";
}
.setting-meta__label span:after {
  content:")";
}

.setting-toggle {
  line-height:13px;
}

.react-toggle .react-toggle-track {
  border-radius:0px;
  background-color:white;
  border: 2px inset #bfbfbf;
  
  width:12px;
  height:12px;
}

.react-toggle:hover:not(.react-toggle--disabled) .react-toggle-track {
  background-color:white;
}

.react-toggle .react-toggle-track-check {
  left:2px;
  transition:unset;
}

.react-toggle .react-toggle-track-check svg path {
  fill: black;
}

.react-toggle .react-toggle-track-x {
  display:none;
}

.react-toggle .react-toggle-thumb {
  border-radius:0px;
  display:none;
}

.text-btn {
  background-color:#bfbfbf;
  border:2px outset #bfbfbf;
  padding:4px;
}

.text-btn:hover {
  text-decoration:none;
  color:black;
}

.text-btn:active {
  border:2px inset #bfbfbf;
}

.setting-text {
  color:black;
  background-color:white;
  border:2px inset #bfbfbf;
  font-size:13px;
  padding:2px;
}

.setting-text:active, .setting-text:focus,
.setting-text.light:active, .setting-text.light:focus {
  color:black;
  border-bottom:2px inset #bfbfbf;
}

.column-header__setting-arrows .column-header__setting-btn {
  padding:3px 10px;
}

.column-header__setting-arrows .column-header__setting-btn:last-child {
  padding:3px 10px;
}

.missing-indicator {
  background-color:#bfbfbf;
  color:black;
  border:2px outset #bfbfbf;
}

.missing-indicator > div {
  background: url('data:image/png;base64,')
    no-repeat;
  background-position:center center;
}

.status__wrapper {
  border: 2px groove #bfbfbf;
  margin:4px;
}

.status {
  border: 1px inset #bfbfbf;
  background-color:white;
  margin:4px;
  padding-bottom:40px;
  margin-bottom:8px;
}

.status.status-direct {
  background-color:#bfbfbf;
}

.status__content {
  font-size:13px;
}

.status.light .status__relative-time,
.status.light .display-name span {
  color: #7f7f7f;
}

.status__action-bar {
  box-sizing:border-box;
  position:absolute;
  bottom:-1px;
  left:-1px;
  background:#bfbfbf;
  width:calc(100% + 2px);
  padding-left:10px;
  padding: 4px 2px;
  padding-bottom:4px;
  border-bottom:2px groove #bfbfbf;
  border-top:1px outset #bfbfbf;
  text-align: right;
}

.status__wrapper .status__action-bar {
  border-bottom-width:0px;
}

.status__action-bar-button {
  float:right;
}

.status__action-bar-dropdown {
  margin-left:auto;
  margin-right:10px;
}
.status.light .status__content a {
  color:blue;
}

.dropdown__trigger.icon-button {
  padding-right:6px;
}

.detailed-status {
  background:white;
  background-clip:padding-box;
  margin:4px;
  border: 2px groove #bfbfbf;
  padding:4px;
}

.detailed-status__display-name {
  color:#7f7f7f;
}

.detailed-status__display-name strong {
  color:black;
  font-weight:bold;
}

.detailed-status__action-bar {
  background-color:#bfbfbf;
  border:0px;
  border-bottom:2px groove #bfbfbf;
  margin-bottom:8px;
  justify-items:left;
  padding-left:4px;
}
.icon-button {
  background:#bfbfbf;
  border: 2px outset #bfbfbf;
  border-top-width:1px;
  border-left-width:1px;
  padding:0px 0px 0px 0px;
  margin-right:4px;
}

.icon-button:active {
  border:2px inset #bfbfbf;
}

.status__action-bar > .icon-button {
  padding:0px 15px 0px 0px;
  min-width:25px;
}

.icon-button.star-icon,
.icon-button.star-icon:active {
  background:transparent;
  border:none;
}

.icon-button.star-icon > i {
  background:#bfbfbf;
  border: 2px outset #bfbfbf;
  border-top-width:1px;
  border-left-width:1px;
  padding-bottom:3px;
}

.icon-button.star-icon:active > i {
  border:2px inset #bfbfbf;
}

.detailed-status__action-bar-dropdown {
  margin-left:auto;
  justify-content:right;
  padding-right:16px;
}

.detailed-status__button {
  flex:0 0 auto;
}

.detailed-status__button .icon-button {
  padding-left:2px;
  padding-right:25px;
}

.status-card {
  border-radius:0px;
  background:white;
  border: 1px solid black;
  color:black;
}

.status-card:hover {
  background-color:white;
}

.status-card__title {
  color:blue;
  text-decoration:underline;
  font-weight:bold;
}

.status-card__description {
 color:black; 
}

.account__display-name strong, .status__display-name strong {
  color:black;
  font-weight:bold;
}

.account .account__display-name {
  color:black;
}

.account {
  border-bottom: 2px groove #bfbfbf;
}

.reply-indicator__content .status__content__spoiler-link, .status__content .status__content__spoiler-link {
  border-radius:0px;
  background:#bfbfbf;
  border:2px outset #bfbfbf;
}

.reply-indicator__content .status__content__spoiler-link:hover, .status__content .status__content__spoiler-link:hover {
  background:#bfbfbf;
}

.reply-indicator__content .status__content__spoiler-link:active, .status__content .status__content__spoiler-link:active {
  border:2px inset #bfbfbf;
}

.reply-indicator__content a, .status__content a {
  color:blue;
}

.notification {
  border: 2px groove #bfbfbf;
  margin:4px;
}

.notification__message {
  color:black;
  font-size:13px;
}

.notification__display-name {
  font-weight:bold;
}

.drawer {
  
}

.drawer__header {
  background: #bfbfbf;
  border:2px outset #bfbfbf;
  justify-content:left;
  margin-bottom:0px;
  padding-bottom:2px;
  border-bottom:2px groove #bfbfbf;
}

.drawer__tab {
  color:black;
  border:2px outset #bfbfbf;
  padding:5px;
  margin:2px;
  flex: 0 0 auto;
}

.drawer__tab:first-child::before {
  content:"Start";
  color:black;
  font-weight:bold;
  font-size:15px;
  width:80%;
  display:block;
  position:absolute;
  right:0px;

}

.drawer__tab:first-child {
  position:relative;
  padding:5px 15px;
  width:40px;
  font-size:0px;
  color:#bfbfbf;
  
  background-image: url("data:image/png;base64,"); 
  background-repeat:no-repeat;
  background-position:8%;
  background-clip:padding-box;
  background-size:auto 50%;
}

.drawer__header a:hover {
  background-color:transparent;
}

.drawer__header a:first-child:hover {
  background-image: url("data:image/png;base64,"); 
  background-repeat:no-repeat;
  background-position:8%;
  background-clip:padding-box;
  background-size:auto 50%;
  transition:unset;
}

.drawer__tab:first-child {
  
}

.search {
  background:#bfbfbf;
  padding-top:2px;
  padding:2px;
  border:2px outset #bfbfbf;
  border-top-width:0px;
  border-bottom: 2px groove #bfbfbf;
  margin-bottom:0px;
}

.search input {
  background-color:white;
  color:black;
  border:2px inset #bfbfbf;
}

.search__input:focus {
  background-color:white;
}

.drawer__inner {
  background-color:#bfbfbf;
  border: 2px outset #bfbfbf;
  border-top-width:0px;
}

.navigation-bar {
  color:black;
}

.navigation-bar strong {
  color:black;
  font-weight:bold;
}

.autosuggest-textarea__textarea, .spoiler-input__input {
  border-radius:0px;
  border:1px inset #bfbfbf;
}

.autosuggest-textarea__textarea {
  border-bottom:0px;
}

.compose-form__uploads-wrapper {
  border-radius:0px;
  border-bottom:1px inset #bfbfbf;
  border-top-width:0px;
}

.compose-form__upload-wrapper {
  border-left:1px inset #bfbfbf;
  border-right:1px inset #bfbfbf;
}

.compose-form__buttons {
  background-color:#bfbfbf;
  border-radius:0px;
  box-shadow:unset;
  border:2px groove #bfbfbf;
  margin-top:4px;
  padding:4px 8px;
}

.privacy-dropdown.active .privacy-dropdown__value {
  background: #bfbfbf;
  box-shadow:unset;
}

.privacy-dropdown__option.active, .privacy-dropdown__option:hover,
.privacy-dropdown__option.active:hover {
  background:#00007f;
}

.privacy-dropdown.active .privacy-dropdown__dropdown {
  box-shadow:unset;
  border-radius:unset;
  color:black;
  border:2px outset #bfbfbf;
  background: #bfbfbf;
}

.privacy-dropdown__option__content {
  color:black;
}

.privacy-dropdown__option__content strong {
  font-weight:bold;
}

.compose-form__warning::before {
  content:"Tip:";
  font-weight:bold;
  display:block;
  position:absolute;
  top:-10px;
  background-color:#bfbfbf;
  font-size:11px;
  padding: 0px 5px;
}

.compose-form__warning {
  position:relative;
  box-shadow:unset;
  border:2px groove #bfbfbf;
  background-color:#bfbfbf;
  color:black;
}

.compose-form__warning a {
  color:blue;
}

.compose-form__warning strong {
  color:black;
  text-decoration:underline;
}

.compose-form__buttons button.active:last-child {
  border:2px inset #bfbfbf;
  background: #dfdfdf;
  color:#7f7f7f;
}

.compose-form__upload-thumbnail {
  border-radius:0px;
  border:2px groove #bfbfbf;
  background-color:#bfbfbf;
  padding:2px;
  box-sizing:border-box;
}

.compose-form__upload-thumbnail .icon-button {
  max-width:20px;
  max-height:20px;
  line-height:10px !important;
}

.compose-form__upload-thumbnail .icon-button::before {
  content:"X";
  font-size:13px;
  font-weight:bold;
  color:black;
}

.compose-form__upload-thumbnail .icon-button i {
  display:none;
}

.emoji-dialog.with-search {
  box-shadow:unset;
  border-radius:0px;
  background-color:#bfbfbf;
  border:1px solid black;
  box-sizing:content-box;
  
}

.emoji-dialog .emoji-search {
  color:black;
  background-color:white;
  border-radius:0px;
  border:1px inset #bfbfbf;
}

.emoji-dialog .emoji-search-wrapper {
  border-bottom:2px groove #bfbfbf;
}

.emoji-dialog .emoji-category-title {
  color:black;
  font-weight:bold;
}

.reply-indicator {
  background-color:#bfbfbf;
  border-radius:3px;
  border:2px groove #bfbfbf;
}

.button {
  background-color:#bfbfbf;
  border:2px outset #bfbfbf;
  border-radius:0px;
  color:black;
  font-weight:bold;
}

.button:hover, .button:focus {
  background-color:#bfbfbf;
}

.button:active {
  border:2px inset #bfbfbf;
}

#Getting-started {
  background-color:#bfbfbf;
  border:2px inset #bfbfbf;
  border-bottom-width:0px;
}

#Getting-started::before {
  content:"Start";
  color:black;
  font-weight:bold;
  font-size:15px;
  width:80%;
  text-align:center;
  display:block;
  position:absolute;
  right:2px;
}

#Getting-started {
  position:relative;
  padding:5px 15px;
  width:60px;
  font-size:0px;
  color:#bfbfbf;
  
  background-image: url("data:image/png;base64,"); 
  background-repeat:no-repeat;
  background-position:8%;
  background-clip:padding-box;
  background-size:auto 50%;
}

.column-subheading {
  background-color:#bfbfbf;
  color:black;
  font-size:0px;
  border-bottom: 2px groove #bfbfbf;
  padding:0px;
  margin:0px;
}

.column-link {
  background-color:transparent;
  color:black;
  padding-left:40px;
}

.column-link:hover {
  background-color:#00007f;
  color:white;
}

.getting-started__footer {
  display:none;
}

.getting-started__wrapper::before {
  content:"Mastodon 95";
  font-weight:bold;
  font-size:23px;
  color:white;
  line-height:30px;
  padding-left:20px;
  padding-right:40px;
  
  left:0px;
  bottom:-30px;
  display:block;
  position:absolute;
  background-color:#7f7f7f;
  width:200%;
  height:30px;
  
  -ms-transform: rotate(-90deg);
  
  -webkit-transform: rotate(-90deg);
  transform: rotate(-90deg);
  transform-origin:top left;
}

.getting-started__wrapper {
  border:2px outset #bfbfbf;
  background-color:#bfbfbf;
}

.account__header {
  background-color:#7f7f7f;
}

.account__header .account__header__content {
  color:white;
}

.account__action-bar__tab > span {
  color:black;
  font-weight:bold;
}

.account__action-bar__tab strong {
  color:black;
}

.account__action-bar {
  border: unset;
}

.account__action-bar__tab {
  border: 1px outset #bfbfbf;
}

.account__action-bar__tab:active {
  border: 1px inset #bfbfbf;
}

.dropdown--active .dropdown__content > ul {
  background:#ffffcc;
  border-radius:0px;
  border:1px solid black;
  box-shadow:unset;
}

.dropdown--active::after {
  display:none;
}

.dropdown--active .icon-button {
  color:black;
  border: 2px inset #bfbfbf;
}

.dropdown--active .dropdown__content > ul > li > a {
  background:transparent;
}

.dropdown--active .dropdown__content > ul > li > a:hover {
  background:transparent;
  color:black;
  text-decoration:underline;
}

.dropdown__sep {
  border-color:#7f7f7f;
}

.detailed-status__action-bar-dropdown .dropdown--active .dropdown__content.dropdown__left {
  left:unset;
}


.embed-modal, .error-modal, .onboarding-modal,
.actions-modal, .boost-modal, .confirmation-modal, .report-modal {
  border-radius:0px;
  border: 2px outset #bfbfbf;
  background:#bfbfbf;
}

.actions-modal::before, 
.boost-modal::before, 
.confirmation-modal::before,
.report-modal::before {
  content: "Confirmation";
  display:block;
  background:#00007f;
  color:white;
  font-weight:bold;
  padding-left:2px;
}

.boost-modal::before {
  content: "Boost confirmation";
}

.boost-modal__action-bar > div > span:before {
  content: "Tip: ";
  font-weight:bold;
}

.boost-modal__action-bar, .confirmation-modal__action-bar, .report-modal__action-bar {
  background:#bfbfbf;
  margin-top:-15px;
}

.embed-modal h4, .error-modal h4, .onboarding-modal h4 {
  background:#00007f;
  color:white;
  font-weight:bold;
  padding:2px;
  font-size:13px;
  text-align:left;
}

.confirmation-modal__action-bar .confirmation-modal__cancel-button {
  color:black;
}

.confirmation-modal__action-bar .confirmation-modal__cancel-button:active, 
.confirmation-modal__action-bar .confirmation-modal__cancel-button:focus, 
.confirmation-modal__action-bar .confirmation-modal__cancel-button:hover {
  color:black;
}

.confirmation-modal__action-bar .confirmation-modal__cancel-button:active {
  border: 2px inset #bfbfbf;
}

.embed-modal .embed-modal__container .embed-modal__html,
.embed-modal .embed-modal__container .embed-modal__html:focus {
  background:white;
  color:black;
  border: 1px inset #bfbfbf;
}

.modal-root__overlay,
.account__header > div {
  background: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAACCAYAAABytg0kAAAACXBIWXMAAC4jAAAuIwF4pT92AAAAFnpUWHRUaXRsZQAACJnLzU9JzElKBwALgwLXaCRlPwAAABd6VFh0QXV0aG9yAAAImUvOKMrPS8wBAAuaAugOwWPyAAAAEUlEQVQImWNgYGD4z4AE/gMADwMB/414xEUAAAAASUVORK5CYII=');
}
}
