css:
@-moz-document url-prefix("http://slatestarcodex.com"), url-prefix("https://slatestarcodex.com") {

* {
    text-shadow: none !important;
    border-color: darkgrey !important;
    font-family: sans-serif, FontAwesome !important;
}

html
  {
      background: #131920;
      color: lightgrey;
}

#left-sidebar {
    background: #131920;
}

#primary.widget-area {
    background-color: #131920;
}

#comment {
    background: #212c3a;
    color: lightgrey !important;
}

#comments {
    background: #131920;
}

.commentholder {
    background-color: #212c3a !important;
    color: lightgrey;
}

.fn {
    color: lightgrey !important;
}

#respond
{
    background: #131920;
}

.pjgm-postmeta {
    background-color: #212c3a;
    border: none;
    color: slategrey;
    text-shadow: none;
}

.hentry {
    background-color: #212c3a;
    color: lightgrey;
}

.widget-title {
    color: white !important;
}

#pjgm-content {
    color: lightgrey;
}

#pjgm-box {
	background-color: #131920;
}

.xoxo {
    background-color: #212c3a !important;
    color: lightgrey !important;
}

.pjgm-posttitle a:link, .pjgm-posttitle a:visited { 
    color: white;
}

#pjgm-content .pjgm-posttitle, h3#comments-title, h3#reply-title, .widget-title {
    color: white;
}

a:link {
    color: lightblue;
}

#pjgm-header {
	background: none;
	
}

#pjgm-ender {
	background: #212c3a;
	
}

}
