@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("archlinux.org") {


* {
background: #191919 !important;
border-color: #3e3e3e !important;
color: #8a8a8a !important;
font-size: 12px !important;
}

input,
button,
select,
textarea {
background: #191919 !important;
border: 1px solid #cb4b16  !important;
}

ul {
list-style-image: none !important;
}

div#archnavbar {
border-color: #268bd2  !important;
}

div#archnavbar * {
padding: 5px !important;
}

div.postbody,
div.postright {
border-color: #268bd2  !important;
}

.pun .linksb .crumbsplus .pagepost,
.pun .postlinksb .crumbsplus .pagepost {
border-bottom: none !important;
border-top: 1px solid #268bd2  !important;
}

.pun .linkst .crumbsplus .pagepost {
border: none !important;
}

.icon {
border-color: #cb4b16  !important;
}

.icon-new,
.inew {
border-color: #af5f00 !important;
}

.end-box,
.end-box *,
.top-box,
.top-box * {
background: none !important;
}

#onlinelist,
#brdstats,
#brdmenu *,
#quickpost,
#brdmain {
border: none !important;
}

p.timestamp,
span.conr,
span.byuser {
color: #705b46 !important;
}

ul li *,
ul li {
color: #a67b07 !important;
}

h1,
h1 *,
h2,
h2 *,
h3,
h3 *,
h4,
h4 *,
h5,
h5 *,
h6,
h6 * {
color: #556b2f !important;
}

