css:
@-moz-document domain("0chan.hk") {
/*main*/
::selection{background:#308DFC}

*{transition:color 0s!important}
html body{font-family:"Trebuchet MS",Trebuchet,serif;font-size:10pt;background:#bbb}
#content{background:#eee url('data:image/png;base64,') 0px 0px repeat-x;width:calc(98% - 270px);margin-left:calc(1% + 260px);box-shadow:#0c0c0c 0 0 3px 1px}
#content>div{padding:47px 11px}
a,span.a,.post .post-footer .post-button-reply,.post-button,.u0-post-bottom-icon,.btn-link{color:#f60;transition:color 0s!important}
.btn-link:hover,.btn-link:focus,a:hover,span.a:hover{color:#06f}
.headmenu-title span,.headmenu-title a{color:#f60!important}
#content>div>div>div[style="margin-top: 20px;"]{margin:0!important}

.btn-default,.btn-primary,.btn-info{color:#f60;background:#f6f6f6;border:1px solid #ccc;box-shadow:none!important;}
.btn-primary:active:hover,.btn-primary:active:focus,.btn-primary:active.focus,.btn-primary.active:hover,.btn-primary.active:focus,.btn-primary.active.focus,.open>.btn-primary.dropdown-toggle:hover,.open>.btn-primary.dropdown-toggle:focus,.open>.btn-primary.dropdown-toggle.focus,.btn-primary:hover,.btn-default:hover,.btn-primary:focus,.btn-primary.focus,.btn-primary.disabled:hover,.btn-primary.disabled:focus,.btn-primary.disabled.focus,.btn-primary[disabled]:hover,.btn-primary[disabled]:focus,.btn-primary[disabled].focus,fieldset[disabled] .btn-primary:hover,fieldset[disabled] .btn-primary:focus,fieldset[disabled] .btn-primary.focus,.btn-info:hover,.btn-info:focus,.btn-info.focus,.btn-info:active:hover,.btn-info:active:focus,.btn-info:active.focus,.btn-info.active:hover,.btn-info.active:focus,.btn-info.active.focus,.open>.btn-info.dropdown-toggle:hover,.open>.btn-info.dropdown-toggle:focus,.open>.btn-info.dropdown-toggle.focus,.btn-info:active,.btn-info.active,.open>.btn-info.dropdown-toggle,.btn-default:focus,.btn-default.focus,.btn-default:active:hover,.btn-default:active:focus,.btn-default:active.focus,.btn-default.active:hover,.btn-default.active:focus,.btn-default.active.focus,.open>.btn-default.dropdown-toggle:hover,.open >.btn-default.dropdown-toggle:focus,.open>.btn-default.dropdown-toggle.focus,.btn-default:active,.btn-default.active,.open>.btn-default.dropdown-toggle{color:#c77405;background:#fdf5ce;border-color:#fbcb09}

.headmenu{position:initial;background:none;border:none;height:auto;white-space:normal;padding:0}
.headmenu-title{font-family:"Trebuchet MS",Trebuchet,serif;position:initial;font-size:2em;line-height:initial;font-weight:bold;padding:1px;text-align:left}
.headmenu-title span{color:#f60!important}
.headmenu-buttons{position:initial}
.headmenu-buttons-left{position:fixed;top:0;left:280px;background:#eee;border:1px #CCC solid;border-top:none;border-radius:0 0 5px 5px}
.headmenu-buttons-right{padding-top:1em;margin-top:.5em;border-top:1px solid #bbb;text-align:center}

.post{background:#ddd;border:solid 1px #ccc;border-radius:5px;box-shadow:none;min-width:0}
.post-popup>.post{border:1px #666 dashed;box-shadow:#666 0 0 10px}
*:not(.post-popup)>.post-op{border:none;background:none;box-shadow:none;margin:0;width:100%}
*:not(.post-popup)>.post-op>.post-body.post-inline-attachment,*:not(.post) .post-op>.post-body{max-height:none;max-width:none!important;padding:0}
.post .post-header{font-size:1em;padding:0 4px 0 0;white-space:nowrap;display:flex;flex-wrap:nowrap;align-items:baseline;flex-direction:row}
.post-img{vertical-align:top;margin:0;max-width:100%}
.post-img>div,.post-img>span>figcaption{display:inline-block!important;position:relative!important;opacity:1!important;margin:0!important;padding:0}
.post-img>span{margin-left:-4px}
.post-img>span>figcaption>span{float:none!important}
.post-img-full{max-width:calc(100% - 40px)!important;height:auto!important}
.post-img figcaption{font-size:.8em;background:none;color:#666}
.post-img-labels{color:#666}
.post-img-buttons{float:right;margin-right:20px!important}
.post-img-label{border:none!important;color:inherit!important;font-family:inherit!important}
.post-img-label:after{content:", "}
.post-img a{display:block;max-width:100%}
.post-img .post-img-thumbnail,.post-img .post-img-full{margin:2px 20px}
.post-header .pull-right{float:none!important}
.post .post-header .post-date{margin:0 5px 0 4px;color:inherit;font-style:normal}
.post-id{font-size:.8em;font-weight:bold;order:2}
.post-id>a{color:#333}
.post-id>a:hover{color:#800000}
.post-id>span{display:none}
.post .post-body{max-width:100%!important;padding:0;margin:.5em}
.post .post-body .user-was-banned{color:#f00;font-size:1em}
blockquote,.quote{font-style:normal;color:#789922}
.post .post-body .post-referenced-by,.post .post-body .post-replied-by{font-style:normal;font-size:11px;color:#333}
.thread>div>div[style="margin-left: 25px; font-weight: bold;"]{font-weight:normal!important;margin:0!important;color:#666}
.post .post-footer{border:none;padding:0;margin:0}
.post-footer .pull-right{float:left!important}
.post-button{padding:0;color:#fff!important;background:#aba8a6;border-radius:3px}

.board-info.block{border:solid 2px #aaa;background:#333;color:#fff;box-shadow:none;margin:0;width:100%}
.close,.board-info a{color:#ddf!important;opacity:1!important;text-shadow:none}
.modals-wrapper2{background:#333}
.modals-wrapper2 .modals-wrapper3{width:100%;padding:13px}
.modals-wrapper2 .modals-wrapper3>button.close{top:14px;right:14px}

mark,.spoiler{background:#000;color:#000;padding:0}
mark:hover,.spoiler:hover{background:none}

/*frame*/
.sidebar,.sidebar .sidemenu-boards-search input{background:#2b2b2b}
.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a:hover,.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a.active{background:#000;border-color:#000}
.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a:hover .sidemenu-board-title,.sidebar .sidemenu-boards-list-box .sidemenu-boards-list .sidemenu-board-item a.active .sidemenu-board-title{color:#eee}
}
