css:
/* Name : Supremacy 1914 Responsive Design
 * Author : Bergjnl
 * Version : 0.0.12
 * Latest update : 2016-12-29
 */

@-moz-document
	domain("supremacy1914.com"),
	domain("supremacy1914.de"),
	domain("supremacy1914.nl"),
	domain("supremacy1914.es"),
	domain("supremacy1914.pl"),
	domain("supremacy1914.it"),
	domain("supremacy1914.fr"),
	domain("supremacy1914.gr")
	{

	@media screen and (max-width: 980px) {
		#miniProfileContainer
		{
			display: none;
		}

		/* Menu on the bottom of the screen */
		#menuContainer
		{
			bottom: 20px;
		}

		ul.mainmenu
		{
			display: flex;
		}

		ul.mainmenu li
		{
			float: none;
			padding: 0;
		}

		/* Removed empty children */
		ul.mainmenu li:first-child,
		ul.mainmenu li:last-child
		{
			margin: auto;
		}

		/*
		 * Start newspaper
		 */

		/* News article full with */
		.newspaper_article
		{
			width: 100%;
		}

		.newspaper_overview_section
		{
			width: 100% !important;
		}

		.newspaper_information,
		.newspaper_ranking
		{
			height: 330px;
		}

		/*
		 * End newpaper
		 */

		/*
		 * Start resource bar
		 */

		/* Fixing the resource bar */
		#resourceBarContainer > div
		{
			width: 100%;
		}

		.glasspanel ul
		{
			display: flex;
			flex-wrap: wrap;
		}

		.glasspanel li
		{
			border: 0;
			width: 24%;
		}

		/*
		 * End resource bar
		 */

		/* Province bar */
		.province_bar_segment .province_bar_button
		{
			width: 100%;
			margin-left: 0;
			height: 100%;
		}

		.province_bar_content > .province_bar_segment
		{
			width: 25% !important;
		}

		#func_province_commands
		{
			width: 65%;
		}

		#provinceBarContainer .province_bar_segment
		{
			width: 18% !important;
			padding: 0 2%;
			margin-right: 1%;
		}

		#func_spyprov_reports
		{
			display: none;
		}

		#provinceBarContainer
		{
			width: 100%;
			margin-left: 0;
			left: 0;
		}

		#province_bar_title
		{
			width: 100%;
			margin-left: 0;
		}

		.province_bar_content
		{
			left: 0;
			right: 0;
			display: flex !important;
			justify-content: space-between;
		}

		.province_bar_segment button
		{
			margin-top: 5px;
		}

		/* End resource bar
		 *
		 */

		/*
		 * Begin army bar
		 */

		#armyBarContainer{
			width: 99%;
			margin-left: 0;
			left: 0;
			transform: scale(1, 1) !important;
		}

		#army_bar_title
		{
			margin-left: 0;
		}

		.army_bar_content
		{
			width: 100%;
			left: 0;
			right: 0;
			display: flex !important;
		}

		.army_bar_image_segment
		{
			margin-left: 0;
		}

		.sync_army span,
		.forced_march span
		{
			display: none
		}

		/* Army Order panel */
		#commands_segment
		{
			margin: 0;
			height: 115px;
		}

		#commands_segment #func_command_buttons
		{
			display: flex;
			flex-wrap: wrap;
			justify-content: space-around;
		}

		#commands_segment .command_name
		{
			display: none;
		}

		/* Make two columns */
		#commands_segment .command_wrapper
		{
			width: 50%;
		}

		#commands_segment .via_command
		{
			width: 50%;
			padding: 0;
			color: transparent;
			text-indent: 27px;
			margin-top: 14px;
		}

		/*
		 * End army bar
		 */

		/* Alliancy,
		Diplomacy,
		Production,
		Stockmarket,
		Resource,
		unitDetail,
		and PlayerDetail container */
		#diplomacyContainer,
		#provinceProductionsContainer,
		#inGameAllianceContainer,
		#stockmarketContainer,
		#resourcesContainer,
		#playerDetailContainer,
		#unitDetailContainer
		{
			top: 0 !important;
			left: 0 !important;
			width: 100% !important;
			height: 100% !important;
			/* One above the resource menu bar */
			z-index: 20002 !important;
		}

		/* Extra fix for resource,
		province production,
		unitDetail,
		and playerDetail container */
		#resourcesContainer,
		#playerDetailContainer,
		#provinceProductionsContainer,
		#unitDetailContainer
		{
			min-width: 0 !important;
		}

		/* Prdocution window */
		#provinceProductionsContainer
		{
			transform: none;
		}

		/* unitDetail */
		#unitDetailContainer .func_scrollable .overview
		{
			display: flex;
			flex-wrap: wrap;
		}

		#unitDetailContainer .func_scrollable .overview > div
		{
			width: 100%;
		}

		#unitDetailContainer .func_scrollable .overview .unit-description
		{
			text-align: center;
		}

		/* Production header */
		.building_popup_content .building_header
		{
			display: flex;
			flex-wrap: wrap;
			height: 0;
		}

		.production_slots
		{
			width: 100%;
			display: flex;
			flex-wrap: wrap;
		}

		.production_slots .func_constructions,
		.production_slots .func_productions
		{
			width: 46%;
			margin: 0 2%;
		}

		.production_slot
		{
			width: 100%;
		}

		#func_production_tabs
		{
			top: 180px;
		}

		/* Production body */
		.amount_cell
		{
			display: none;
		}

		#table_prov_popup
		{
			width: 100%;
		}

		.cost_cell
		{
			height: auto;
		}

		.cost_table tbody
		{
			display: flex;
		}

		.cost_table tbody tr
		{
			display: flex;
			flex-direction: column;
			border: none !important;
		}

		.cost_table tbody tr td
		{
			height: 16px;
		}

		/* Diplomacy header */
		#func_diplomacy_header_table tr:last-child
		{
			display: flex;
			width: 100%;
		}

		#func_diplomacy_header_table tr:last-child th
		{
			word-break: normal;
			display: flex;
			align-items: center;
			height: 35px;
		}

		.diplomacy_table_head table th .sort_action
		{
			margin: -6px 0 0;
		}

		.player_table_wrapper
		{
			padding: 0;
		}

		/* Diplomacy window */
		#diplomacy_threads .box_title
		{
			text-align: center;
			padding-top: 10px;
		}

		#func_diplomacy_messages
		{
			display: flex;
			flex-flow: column;
		}

		#func_diplomacy_messages div
		{
			margin-bottom: 5px;
		}

		#func_diplomacy_messages #diplomacy_threads
		{
			top: 0;
			bottom: 0;
			left: 0;
			height: 35%;
			width: 100%;
			position: relative;
		}

		#func_diplomacy_messages #diplomacy_thread_action
		{
			text-align: right;
		}

		#func_diplomacy_messages #diplomacy_message_list
		{
			top: 0;
			bottom: 0;
			right: 0;
			left: 0;
			height: 50%;
			position: relative;
		}

		#func_diplomacy_message_creation
		{
			width: 100% !important;
			background: none !important;
			position: relative;
			border-radius: 4px;
			border: 1px solid #b5ad98;
			position: relative !important;
			bottom: 0 !important;
			right: 0 !important;
		}

		#func_diplomacy_message_creation textarea
		{
			background: rgba(255, 255, 255, 0.21) !important;;
			width: 50% !important;
		}

		.trade_button .autoizeLine
		{
			white-space: pre-line;
		}

		.func_show_create_trade
		{
			height: 50px;
		}

		/* Diplomacy player list */
		.func_player_row
		{
			display: flex;
		}

		table.team_game_table td
		{
			/* Fixes the horizontal centering */
			height: auto;
			display: flex;
			align-items: center;
		}

		.func_player_row
		{
			height: 50px;
		}

		#func_diplomacy_table .selected .cell_messages
		{
			width: 45px;
		}

		.cell_messages
		{
			padding: 0;
		}

		.cell_player_type .func_player_invite
		{
			padding: 5px;
		}

		#func_diploTableScroll .overview
		{
			height: 100%;
		}

		/* Stockmarket */
		#clientControlsContainer
		{
			top: 63px;
			bottom: auto;
		}

		#clientControlsContainer .client_controls
		{
			width: 34px;
			top: 34px;
			right: 0;
			z-index: 1000;
		}

		#func_client_logout
		{
			display: none !important;
		}

		/* Prevent buy/sell popup behind parent */
		#inputDialogContainer
		{
			z-index: 20004 !important;
		}

		/* Resource settings */
		.resources_page
		{
			width: 100%;
			padding: 0;
		}

		.resource_table tr
		{
			display: flex;
			width: 100%;
			justify-content: center;
		}

		.resource_category_total_row
		{
			border-top: 1px solid rgba(0, 0, 0, 0.15);
		}

		.resource_category_total_row td
		{
			border-top: none !important;
		}

		.resource_category_header
		{
			width: 100%
		}

		.resource_table .resize_name,
		.resource_table .resize_amount
		{
			width: 80px;
		}

		.resource_category_total_row td:first-child
		{
			width: 38px;
		}

		.column_resource_rate
		{
			width: 50px;
		}
	}

	@media screen and (max-width: 760px)
	{
		/* Removes the portrait,
		team */
		#func_diplomacy_table .cell_portrait,

		#func_diplomacy_table .cell_team,

		.team_game_table tr:last-child th:nth-child(2),
		.team_game_table tr:last-child th:nth-child(9),
		.team_game_table tr:last-child th:nth-child(10)
		{
			display: none !important;
		}

		.diplomacy_table_head
		{
			top: 85px;
		}

		.diplomacy_table_body
		{
			top: 120px;
		}

		#func_page_diplomacy_table div:first-child tr
		{
			display: flex;
		}
	}

	@media screen and (max-width: 700px) {
		#newspaper_search
		{
			width: 100%;
			display: flex;
		}

		.diplomacy_search_box
		{
			width: 100%
		}

		.newspaper_tabs ul
		{
			display: flex;
			width: 100%;
		}

		#func_news_cat_filter
		{
			display: flex;
			text-align: center;
			margin-top: 40px;
		}

		#func_news_cat_filter li
		{
			display: block;
			flex-grow: 1;
			width: 100%;
			padding-top: 10px;
		}

		/* Removed text */
		#newspaper_switch_day_text
		{
			display: none;
		}

		/* Remove army and province title bar */
		#province_bar_title,
		#army_bar_title
		{
			display: none;
		}

		#provinceBarContainer
		#armyBarContainer
		{
			height: auto;
		}

		/* Remove resource icon */
		#func_morale_info,
		#func_resource_info,
		.three_columns span:last-child
		{
			display: none;
		}

		#chatContainer,
		#provinceListContainer
		{
			display: none;
		}

		/* Army bar */
		/* */
		.army_bar_image_segment
		{
			display: none;
		}

		/* Province bar*/
		/* */
		#func_province_commands .province_bar_segment_footer .autoResizeLine
		{
			display: none;
		}
	}

	@media screen and (max-width: 640px) {
		/* Removes the playername,
		and message button. */
		#func_diplomacy_table .cell_name,

		.team_game_table tr:last-child th:nth-child(3),
		#func_diplomacy_table .cell_messages
		{
			display: none !important;
		}

		.resource_table tr
		{
			flex-wrap: wrap;
		}

		#func_resource_overview thead
		{
			display: none
		}

		.production_slot
		{
			width: 100%;
		}

		.production_slots .func_constructions,
		.production_slots .func_productions
		{
			width: 100%;
			margin: 0;
		}

		#func_production_tabs
		{
			top: 260px;
		}
	}

	@media screen and (max-width: 520px) {
		/* Removes the number of provinces */
		#func_diplomacy_table .cell_provinces,
		.team_game_table tr:last-child th:nth-child(4)
		{
			display: none !important;
		}

		.building_popup_content .building_details .glass_panel_tabs li::before,
		.building_popup_content .building_details .glass_panel_tabs li::after
		{
			display: none;
		}
	}

	@media screen and (max-width: 400px) {
		.glasspanel li
		{
			border: 0;
			width: 32%;
		}

		#clientControlsContainer
		{
			top: 92px !important;
		}

		.province_bar_content > .province_bar_segment
		{
			display: none;
		}

		#func_province_commands
		{
			width: 100%;
		}
	}
}
