css:
@-moz-document domain(windesheim.htaal.nl) {
  body{
    background: #ebebeb !important;
  }

  #vraagstelling {
    font-size: 1.2em !important;
  }
  
  #vraag_view{
    background: white;
    text-align: left;
    padding: 50px;
    margin: 20px;
    margin-top: 50px !important;
    margin-bottom: 50px;
    box-shadow: 3px 3px rgba(0, 0, 0, 0.37);
  }
  
  #de_tip{
    margin-top: 65px;
  }

  .drag_me{
    margin: 0;
    margin-right: 10px;
  }

  .verstuur_btn{
    margin-left: 25px;
    margin-right: 0;
    float: right;
  }

  .meerk_antw{
    background: #137dc5;
    font-size: 1.2em;
    padding: 20px;
    margin-bottom: 20px;
    transition: none;
	color: white;
  }
  
  input[type=text].invul_antw{
    padding: 10px;
  }
  
  .meerk_antw:hover{
    background: rgba(19, 125, 197, 0.5);
  }
  
  .goed_antw, .goed_antw:hover{
    background: #11ad45;
    color: white;
  }
  
  .fout_antw, .fout_antw:hover, .niet_gekozen_antw, .niet_gekozen_antw:hover{
    background: rgba(19, 125, 197, 0.5);
    color: white;
  }
  
  p.opdracht{
    text-transform: none;
    color: black;
    text-align: center;
    font-weight: 800;
    font-size: 1.5em;
  }
  
  .rechts_kleur_fout, .links_kleur_fout, .rechts_kleur_goed, .links_kleur_goed{
    background: none !important;
  }

  .bg_ball, #tussen_kleur{
    display: none !important;
  }
}
