/* i really want this to be global */
/* ---------------- ANIMATION ---------------- */
@keyframes Credits
{
    0%
{
        background-image: url(https://dl.dropbox.com/s/mp5qiti8vztntuv/Crediti.png?dl=0);
        background-position: 111% 100%
    }
    90%
{
        background-image: url(https://dl.dropbox.com/s/mp5qiti8vztntuv/Crediti.png?dl=0);
        background-position: 200% 100%
    }
}
@keyframes Coverbeat
{
    0%
{
        -webkit-mask-size: 100%;
        width: 124%;
        top: -11%;
        left: -3%;
        -webkit-filter: brightness(100%);
    }
    5%
{
        -webkit-filter: brightness(100%);
    }
    10%
{
        -webkit-filter: brightness(100%);
    }
    30%
{
        -webkit-mask-size: 90%;
        width: 139%;
        top: -19%;
        left: -11%;
        -webkit-filter: brightness(100%);
    }
    55%
{
        -webkit-mask-size: 100%;
        width: 124%;
        top: -11%;
        left: -3%;
        -webkit-filter: brightness(100%);
    }
    58%
{
        -webkit-filter: brightness(200%);
    }
    60%
{
        -webkit-filter: brightness(100%);
    }
    65%
{
        -webkit-filter: brightness(100%);
    }
    80%
{
        -webkit-mask-size: 90%;
        width: 139%;
        top: -19%;
        left: -11%;
        -webkit-filter: brightness(100%);
    }
    100%
{
        -webkit-mask-size: 100%;
        width: 124%;
        top: -11%;
        left: -3%;
        -webkit-filter: brightness(100%);
    }
}
@keyframes Bannerbeat
{
    0%
{
        -webkit-filter: brightness(100%);
    }
    2%
{
        -webkit-filter: brightness(110%);
    }
    8%
{
        -webkit-filter: brightness(100%);
    }
    100%
{
        -webkit-filter: brightness(100%);
    }
    /* ---------------- INITIAL SCREEN ---------------- */
}
.landing__content
{
    background: url(https://dl.dropbox.com/s/r1dyi2umcifpbip/TITLESCREEN.png?dl=0), url(http://i.imgur.com/28XH9Li.jpg);
    background-size: cover;
    animation: beatHero 2.5s infinite;
    -webkit-animation: beatHero 2.5s infinite
}
.site-message
{
    position: fixed;
    bottom: 10%;
    left: 17%;
    height: 75%;
    width: 70%;
    background: url(https://dl.dropbox.com/s/31kcmz1qb0gki2q/AD.jpg.png?dl=0) center/cover no-repeat
}
.site-message .cookie-content
{
    margin-bottom: -27%;
    font-size: 10px
}
.cookie-close .cookie-close-btn
{
    font-size: 10px;
    margin-left: 70%;
    margin-top: -100%
}
.modal__container::before
{
    background-color:#2e98fc
}
.concept
{
    background-image: url(https://i.imgur.com/GVLhzBZ.png);
    background-size: cover
}
.modal__container::after
{
    background-image: url(https://dl.dropbox.com/s/sh8ivv2wil6n1rx/AD3.png?dl=0);
    background-size: cover
}
/* ---------------- MENU ---------------- */
/* -------- dance room number -------- */

#room-info::before
{
    display:none
}
#just-dance-now .danceroom__label
{
    color: #4627f1;
    font-size: 20%;
    margin-top: 0%;
    opacity:0.6;
    font-family: "Just Dance";
    text-transform:uppercase;
    text-align:right;
    margin-right:54%
}
#just-dance-now .danceroom__number
{
    color: #0a0330;
    text-shadow:none;
    margin-top: -3%;
    opacity:0.85;
    font-family: "Just Dance";
    text-transform:uppercase;
    text-align:right;
    margin-right:75%
}
/* -------- covers -------- */

.song--focused .song__decoration::after
{
    background-image: url(https://i.imgur.com/bqAe7CG.png);
    z-index: 99999;
    width: 100%;
    height: 100%;
    left: 9%;
    top: 1%
}
.song--focused .song__tag--free::before
{
    display: none
}
.song__tag::before
{
    right: -20%;
    top: -4%;
    display: none
}
.title-container .artist
{
    display: none
}
.title-container .title
{
    opacity: 0.5;
    top: -10%;
    left: 5%;
    font-size: 110%
}
.song__cover
{
    width: 140%;
    -webkit-mask-image: url(https://i.imgur.com/mYnOcrH.png);
    -webkit-mask-size: 85%;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    top: -11%;
    left: -11%;
}
.song--focused .song__cover
{
    opacity: 2 !important;
    -webkit-mask-image: url(https://i.imgur.com/sn4t24T.png);
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    animation: Coverbeat 1s linear infinite;
}
#song-selection
{
    background-image: url(https://i.imgur.com/lNoEjq7.png);
    background-size: cover;
}
.song:not(.focused) {
       margin-left:-30px
}
/* -------- select coach -------- */
/* -------- superior screen -------- */
#preview .song-action__button
{
    -webkit-animation: none!important;
    animation: none!important;
    margin-top: -1%;
    margin-right: -312%;
    height:90%;
    width:250%;
    background-size:200% 200%;
    background-image: url(https://i.imgur.com/sn4t24T.png);
    opacity:0
}
#preview .cover .click-song,
#preview .cover .hi-res-cover,
#preview .cover .low-res-cover,
#preview .cover .song-preview,
#preview .cover .song-preview::after
{
    top: 0;
    left: 0;
    width: 300%;
    height: 300%;
    border-radius: 4
}
#preview .song-detail__text
{
    font-size:116%;
    text-transform:uppercase
}
#preview .song-detail__title
{
    margin-left: 260px;
    margin-top: -11184px
}
#preview .song-detail__artist
{
    margin-left: 120px;
    text-align:center;
    margin-top: -1113px!important
}
#preview .cover
{
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
    width: 98.6%;
    height: 1.3rem;
    top: -56.3% !important;
    left: -6.8%;
    z-index: -1;
    animation: Bannerbeat 4s infinite
}
#admin-menu .menu__body:hover
{
    animation:Credits 30s linear;
}
#admin-menu div.menu__body
{
    background: url(https://dl.dropbox.com/s/s7djs8npz1j9umr/Base.png?dl=0);
    background-size: cover;
}
/* ---------------- TRANSIZIONE ---------------- */
/* ---------------- NASCONDI ---------------- */
#preview .song-details::before
{
    display:none
}
#preview .social-stats
{
    display: none!important;
}
.hud .connect-phone-info.connect-phone-info--clickable,
.room-info::before
{
    display: none !important
}
#room-info .danceroom__qr-code-wrapper
{
    display: none
}
.landing-text,
.landing__video,
.explore.section
{
    display: none
}
.dancercard .player-flag,
.dancercard .player-exp,
.dancercard .avatar-wrapper,
.controller-coins
{
    display: none
}
#preview .song-action__label
{
    display: none
}
#preview .song-preview.visible
{
    display: none
}
#preview .song-action__price-tag
{
    display: none!important
}
#preview .song-action__button.song-action__free::before
{
    display: none!important
}
.preview--bg
{
    display: none!important
}
#just-dance-now .modal .concept p,
#just-dance-now .modal span,
.concept__line.equation,
#just-dance-now .concept__line.concept__line--shift-left.concept__title,
.download
{
    display: none
}
/* ---------------- DANCERCARD ---------------- */
/* -------- base -------- */
.song-selection .dancercard
{
    top: -20%;
    left:80%
}
.dancercard .player-color::after
{
    height: 999%;
    -webkit-animation: none!important;
    animation: none!important;
    top: 30%
}
#players .crown::before
{
    background-image: url(https://i.imgur.com/mcf2kEe.png)
}
.dancercard .player-color
{
    height: 25%;
    width: 100%;
    -webkit-animation: none!important;
    animation: none!important;
    ;
    top: 80%;
    margin-left: -23%;
    -webkit-mask-image: url(https://i.imgur.com/yiEDckW.png);
    -webkit-mask-size: 100%;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
}
.dancercard .player-name
{
    top: 33%;
    text-shadow: none
}
/* -------- feedback -------- */

.stars.player-stars
{
    top: 55%;
}
.crown.leader,
.crown
{
    margin-top: 35%;
    margin-left: -13%
}
#players .feedback-label
{
    background-image: url(https://i.imgur.com/0a1UxsV.png);
    background-repeat: no-repeat;
}
#players .feedback
{
    background-image: url(https://i.imgur.com/C3WAd9k.png);
    background-repeat: no-repeat;
    width: 180%;
    height: 160%;
}
#players .feedlabel-good
{
    top: 30%;
    left: -10%;
    width: 100%;
    height: 100%;
}
#players .feedlabel-perfect
{
    top: 30%;
    left: 5%;
    width: 100%;
    height: 100%;
}
#players .feedlabel-yeah
{
    top: 20%;
    left: -5%;
    width: 100%;
    height: 100%;
}
#players .feedlabel-ok
{
    left: 5%;
    top: 60%
}
#players .feedback-good
{
    width: 140%;
    height: 130%;
    left: -30%
}
#players .feedback-ok
{
    width: 145%;
    top: -15%;
    left: -15%
}
#players .feedback-perfect
{
    width: 160%;
    height: 140%;
    left: -30%
}
#players .feedback-yeah
{
    width: 160%;
    height: 180%;
    left: -30%;
    top: -25%
}
/* ---------------- JUST DANCE ---------------- */
/* -------- test -------- */
#racetrack.animate .platform
{
    bottom:-40%;
    left:25%;
    height:1%;
    -webkit-transform: rotate(-103deg)
}
#lyrics .fragment.unfilled
{
    left: 0%;
    height:10%;
}
/* -------- pictograms -------- */

#beat
{
    width: 42.2%;
    height: 120%;
    background-image: url(https://i.imgur.com/FSWrk93.png);
    opacity: 1;
    background-repeat: no-repeat;
    background-size: 100% 100%;
    background-position: bottom;
}
/* ---------------- SCORE ---------------- */

#afterdance .results
{
    left: -10%;
}
#afterdance .results .players .scrollArea .scrollContainer .player .color .flag
{
    display: none
}
#crown-container .crown
{
    margin-left: -40%;
    margin-top: 0%;
}
#afterdance .results .song-info
{
    display: none
}
#afterdance .img--afterDance
{
    display: none
}
