css:
@-moz-document domain("mail.google.com") {
/* Style mail list read */
.yO {
    color: rgb(0,0,0);
    color: rgba(0,0,0,0);
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.1);
    transition: all 0.8s;
}
.yO:hover {
    color: rgb(0,0,0);
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.7);
    transition: all 0.5s;
}

/* Style mail list unread */
.zE {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.2);
    transition: all 0.8s;
}
.zE:hover {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.89);
    transition: all 0.5s;
}

/* Style attachments */
.brc {
    background: rgb(0,0,0);
    background: rgba(255,255,255,0.1);
    color: gray;
    transition: all 0.5s;
}
.brc:hover {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.7);
    color: black;
}

/* Style inbox tabs */
.aKk {
    border-radius: 10px 10px 0 0;
    overflow: hidden;
}
.aKk, .aKh {
    background: none;
}
.aKk .aAA {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.4);
    transition: all 0.5s;
}
.J-KU-JW {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.7);
}

/* Style mail list selected */
.x7 {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.4);
    transition: all 0.5s;
}

/* Styling the box listing */
.Cp {
    max-height: 200px;
    overflow-y: auto;
    overflow-x: hidden;
    border: none;
}
.xY {
    border: none;
}

/* Styling the scroll bar for the .Cp boxes */
.Cp::-webkit-scrollbar-track {
    border-radius: 10px;
    -moz-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    background-color: #F5F5F5;
}
.Cp::-webkit-scrollbar {
    width: 10px;
    background: none;
}
.Cp::-webkit-scrollbar-thumb {
    border-radius: 10px;
    background-color: rgb(100,200,100);
    background-image: -webkit-linear-gradient(45deg,
                                              rgb(70, 150, 220) 25%,
                                              transparent 25%,
                                              transparent 50%,
                                              rgb(70, 150, 220) 50%,
                                              rgb(70, 150, 220) 75%,
                                              transparent 75%,
                                              transparent)
}

/* Styling the search bar up top */
.gbqfqw {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.5);
    border: none;
}

/* Styling the compose button */
.z0 {
    margin-left: 0;
    padding-top: 10px;
    padding-bottom: 20px;
}
.z0 .T-I, .z0 .T-I-JW {
    background: rgb(255,255,255);
    background: rgba(255,255,255,0.6);
    transition: all 0.5s;
}
}
