css:
@-moz-document domain("github.com") {
.repohead.experiment-repo-nav {
    background-color: #0747A6 !important;
}

.public a {
    color: #DEEBFF !important;
}

.repo-list .public a {
    color: #0747A6 !important;
}

.repo-list .public h3 a, .issue-list .issue-list-item a, .pinned-repo-item-content a, #user-repositories-list a, .d-block a, .mini-repo-list a, .mini-repo-list .public .octicon {
    color: #0747A6 !important;
}

.public .octicon {
    color: #DEEBFF !important;
}

.public .path-divider {
    color: #fff !important;
}

.Header {
    background-color: #0747A6 !important;
    color: #DEEBFF !important;
}

.Header .container-lg {
    width: 93% !important;
  	max-width: 1720px !important;
    margin: 0 auto !important;
}

.reponav-item.selected {
    border-color: rgba(0, 0, 0, 0) !important;
    background-color: #133f88 !important;
    color: #DEEBFF !important;
}

.reponav-item:hover {
    background-color: #123878 !important;
}

.reponav-item:active {
    background-color: #2684FF !important;
}

.reponav-item, .reponav-item .octicon {
    color: #DEEBFF !important;
}

.reponav-item .Counter {
    color: #0747A6 !important;
    background-color: #DEEBFF !important;
}

.reponav-item {
    padding: 6px 12px 8px !important;
    border-radius: 3px !important;
}

.reponav-item:hover .Counter {
    color: #123878 !important;
}

.reponav-item:active .Counter {
    color: #2684FF !important;
}

.reponav-item.selected .Counter {
    color: #133f88 !important;
}

.reponav {
    margin-top: 0 !important;
    top: 0 !important;
}

.pagehead .container {
    margin: 0 auto 12px !important;
    width: 90% !important;
  	max-width: 1680px !important;
}

.pagehead {
    margin-bottom: 0 !important;
    border-bottom: 1px solid #F4F5F7 !important;
}

.container.new-discussion-timeline.experiment-repo-nav {
    padding-top: 12px !important;
}

.js-repo-meta-container {
    margin: 0 auto !important;
}

.btn {
    border: none !important;
    border-radius: 3px !important;
    background-image: none !important;
    font-size: 14px !important;
}

.btn-with-count {
    background: #fff !important;
    border: none !important;
    border-radius: 3px 0 0 3px !important;
    color: #0747A6 !important;
}

.social-count {
    border: none !important;
    background-color: #123878 !important;
    color: #DEEBFF !important;
    padding: 8px 12px !important;
}

.BtnGroup-item, .file-navigation .get-repo-select-menu {
    margin-left: 10px !important;
}

.file-navigation .branch-select-menu {
    margin-right: 10px !important;
}

.btn-sm {
    background-color: #F4F5F7 !important;
    color: #24292e !important;
    font-weight: 400 !important;
    padding: 8px 12px !important;
    box-shadow: none !important;
}

.btn-sm:hover {
    background-color: #EBECF0 !important;
}

.btn-sm:active {
    background-color: #DEEBFF !important;
    color: #0052CC !important;
}

.btn-primary {
    background-color: #0052CC !important;
    color: #fff !important;
}

.btn-primary:hover {
    background-color: #0065FF !important;
}

.btn-primary:active {
    background-color: #0747A6 !important;
    color: #fff !important;
}

.overall-summary {
    border: none !important;
    background-color: #F4F5F7 !important;
}

.repository-lang-stats-graph {
    border: none !important;
    height: 12px !important;
    border-bottom: 1px solid #F4F5F7 !important;
}

.repository-lang-stats-graph .language-color:first-child, .repository-lang-stats-graph .language-color:last-child {
    border: none !important;
}

.file-navigation.in-mid-page {
    padding-top: 16px !important;
    width: 980px !important;
    margin: 0 auto !important;
}

.commit-tease, .file-wrap {
    width: 980px !important;
    margin: 0 auto !important;
}

.boxed-group {
    padding-top: 12px !important;
}

.file-wrap, .commit-tease {
    border: none !important;
}

.notification-indicator .mail-status {
    background: #87e2aa none !important;
    border: 2px solid #0747A6 !important;
}

.commit-tease {
    padding: 12px !important;
    margin-bottom: 10px !important;
    border-radius: 3px !important;
    background-color: #F4F5F7 !important;
}

.boxed-group > h3, .boxed-group .heading {
    padding: 12px !important;
    background-color: transparent !important;
    border: none !important;
}

.readme .markdown-body, .readme .plain {
    border: none !important;
    border-top: 1px solid #ddd !important;
    padding: 16px !important;
}

.subnav-item {
    border: none !important;
    padding: 8px 12px !important;
    font-weight: 400 !important;
    background-color: #F4F5F7 !important;
    color: #505F79 !important;
}

.subnav-item:hover {
    background-color: #EBECF0 !important;
}

.subnav-item:active {
    background-color: #DEEBFF !important;
    color: #0052CC !important;
}

.subnav-item.selected, .subnav-item.selected:hover, .subnav-item.selected:focus {
    background-color: #42526E !important;
    color: #fff !important;
}

.subnav-item + .subnav-item {
    margin: 0 !important;
    margin-left: 2px !important;
}

.form-control, .form-select {
    box-shadow: none !important;
}

.form-control.focus, .form-control:focus, .form-select.focus, .form-select:focus {
    border-color: #2684FF !important;
}

.branch-group-heading {
    background: transparent !important;
    padding: 12px !important;
    border: none !important;
    color: #172B4D !important;
}

.branch-group-heading:hover {
    background-color: #f5f5f5 !important;
}

.more-branches {
    background: #F4F5F7 !important;
    color: #172B4D !important;
    padding: 12px !important;
    border: none !important;
    border-radius: 3px !important;
    margin-top: 8px !important;
}

.branch-summary {
    border: none !important;
    border-top: 1px solid #dfe2e5 !important;
    padding: 16px 12px !important;
}

.branches .branch-actions {
    top: -6px !important;
    right: 0 !important;
}

.branches .branch-actions .State--small {
    padding: 8px !important;
    font-size: 14px !important;
}

.branch-a-b-count .bar-behind .meter {
    border-radius: 0 !important;
    height: 5px !important;
    background-color: #ddd !important;
}

.branch-a-b-count .bar-ahead .meter {
    border-radius: 0 !important;
    height: 5px !important;
    background-color: #3572b0 !important;
}

.branch-a-b-count .count-half:last-child {
    border-left: 1px solid #ccc !important;
    height: 20px !important;
}

.subnav-search-context .btn {
    padding: 7px 12px !important;
    border-radius: 3px 0 0 3px !important;
    background-color: #F4F5F7 !important;
    color: #24292e !important;
}

.subnav-search-context .btn:hover {
    background-color: #EBECF0 !important;
}

.subnav-search-context .btn:active {
    background-color: #0747A6 !important;
    color: #fff !important;
}

.blankslate {
    background-color: transparent !important;
    border: none !important;
    border-radius: 0 !important;
    box-shadow: none !important;
}

.user-profile-nav {
    border-bottom: 0 !important;
    border-radius: 3px !important;
}

.underline-nav {
    height: 48px !important;
    margin: 12px 0 !important;
}

.user-profile-nav .underline-nav {
    margin: 0 !important;
}

.underline-nav-item {
    color: #42526E !important;
    border-radius: 3px !important;
}

.underline-nav-item:hover {
    background-color: #eaecf0 !important;
}

.underline-nav-item:active {
    background-color: #DEEBFF !important;
}

.underline-nav-item .Counter {
    background-color: #586069 !important;
    color: #fff !important;
    padding: 2px 6px !important;
}

.underline-nav-item:hover .Counter {
    color: #eaecf0 !important;
}

.underline-nav-item:active .Counter {
    color: #DEEBFF !important;
}
.underline-nav-item.selected .Counter {
	    background-color: #0052CC !important;
        color: #eaecf0 !important;
}

.underline-nav-item.selected {
    border-bottom: 0 !important;
    background-color: #eaecf0 !important;
    border-radius: 3px !important;
    color: #0052CC !important;
}

.underline-nav-item.selected:active {
    background-color: #DEEBFF !important;
}

.page-gist-edit .octicon-gist-secret {
    color: #DEEBFF !important;
}

.gisthead .repohead-details-container .meta .text-gray {
    color: #fff !important;
}

.gisthead .repohead-details-container .meta a, .gisthead .repohead-details-container .lh-condensed a {
    color: #DEEBFF !important;
}

.repohead h1 .octicon, .repohead h1 {
    color: #DEEBFF !important;
}

.experiment-repo-nav.repohead.gisthead h1 .octicon {
    margin-top: -2px !important;
}

.boxed-group.flush .boxed-group-inner {
    margin-top: 12px !important;
}

.user-profile-nav.is-stuck {
    border-bottom: solid 1px #d1d5da !important;
    background-color: #fff !important;
}

.user-profile-nav.is-stuck .underline-nav-item.selected, .user-profile-nav.is-stuck .underline-nav-item, .user-profile-nav.is-stuck {
    border-radius: 0 !important;
}

.user-profile-sticky-bar::after, .user-profile-mini-vcard {
    height: 48px !important;
}

.RecentBranches-item {
    line-height: 36px !important;
}

.Header .header-search-scope {
    border-right-color: #0747A6 !important;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif !important;
}
.text-red{
	color: #ed5e76 !important;
}
.file-navigation-option .input-group .form-control{
	width: 100% !important;
    height: 36px !important;
}
}

@-moz-document regexp(".*github\\.com\\/search.*") {
#js-pjax-container .mb-4{
	background-color: #F4F5F7 !important;
    border-bottom: 0 !important;
}
}

@-moz-document domain("gist.github.com") {
.user-list li .actions{
	top: 3px !important;
}
.user-list li{
    padding: 8px 0 8px 30px !important;
}
.user-list{
	border-top: none !important;
}
.user-list li:hover{
	background-color: rgb(246,248,250) !important;
}
.text-gray{
	color: #DEEBFF !important;
}
}
