css:
@-moz-document domain("fastgrow.jp") {
body {
    font-family: "Yu Gothic", YuGothic, sans-serif;
    font-feature-settings: "palt";
    letter-spacing: .14em;
}
p, li, input, textarea, th, td {
    font-size: 1.6rem;
    line-height: 1.875;
}

.p-article__title {
    color: #475d69;
}
.c-relatedInfo > section > span {
    color: #577381;
}
.c-person__name,
.c-person__position,
.p-article__description p {
    color: #263238;
}
.p-article__description > *:not(:first-child) {
    margin-top: .875em;
}
}
