@-moz-document domain(docs.google.com) {
	/* your code goes here */
  .docs-homescreen-itemholder-content {
    position:relative;
    z-index:1;
  }
  .docs-homescreen-floater-grid-header, .docs-homescreen-floater-list-header {
    z-index:100;
  }
  .docs-homescreen-grid-item {
    	overflow:hidden;
  }
  
    .docs-homescreen-grid-item, .docs-homescreen-grid-item-thumbnail{
      position:relative;
                z-index: 2;
                -webkit-transition: all 100ms ease-in;
                -webkit-transform: scale(1);
                -ms-transition: all 100ms ease-in;
                -ms-transform: scale(1);
                -moz-transition: all 100ms ease-in;
                -moz-transform: scale(1);
                transition: all 100ms ease-in;
				transform-origin: top;
    }
.docs-homescreen-grid-item-thumbnail{ 
  			transform-origin: top left;
}

  .docs-homescreen-grid-item:hover, .docs-homescreen-grid-item:hover .docs-homescreen-grid-item-thumbnail{
      position:relative;
                z-index: 10;
                -webkit-transform: scale(1.6);
                -ms-transform: scale(1.6);
                -moz-transform: scale(1.6);
    }
.docsshared-menu-bubble .docs-homescreen-iconmenu .goog-menuitem {
			padding-left:0 !important;
  			padding-right:0 !important;
}
  
}
