.heading.logo img {
    display: none;}

.heading.logo img {
    background: url("https://image.ibb.co/kuimWU/spot_logo_login5.png") no-repeat center center !important;
    background-size: 206px !important;
    padding: 0px;
    width: 0px ;}

.heading.logo img {
    position: relative; 
    overflow: hidden;
    margin: 0;
    height: 100%;
    width: 100%;
    filter: grayscale(.8);}

.heading.logo img{
    width: 100%;
    height: 100%;}

.heading.logo::before {
    position: absolute;
    top: 50%;
    left: 50%;
    z-index: 2;
    display: block;
    content: '';
    width: 0;
    height: 0;
    background: rgba(0,0,0,.2);
    border-radius: 100%;
    transform: translate(-50%, -50%);
    opacity: 0;}

.heading.logo {
    animation: grow ease-in-out 5s infinite forwards;}

@media screen and (-webkit-min-device-pixel-ratio:0) { 
.heading.logo::before {
    margin: 0px -70px;
    animation: ring 4s infinite;}}
   
@-webkit-keyframes grow {
    0% {
	filter: grayscale(.8);}
    40% {
	filter: grayscale(.5);}
    100% {
        filter: grayscale(0);}}

@media screen and (-webkit-min-device-pixel-ratio:0) {
@-webkit-keyframes ring {
    0% {
	opacity: 0;
        background: rgba(7, 209, 89, 1);}
    10% {
	opacity: 1;
        background: rgba(7, 209, 89, 1)}
    40% {
	opacity: 1;
        background: rgba(7, 209, 89, .05)}
    100% {
	width: 150%;
	height: 800%;
        opacity: 0;}}}

@media all and (min--moz-device-pixel-ratio:0) {
.heading.logo::before {
    margin: 0px -70px;
    animation: ring 4s infinite ;}}

@-webkit-keyframes grow {
    0% {
	filter: grayscale(1);}
