css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hotukdeals.com") {
body.height--all-full
{
    overflow: visible;
    height: auto;
}
#main
{
    height: auto !important;
    overflow: visible;
}
/* Nav */
.subNav
{
}

.subNav .space--t-3
{
    padding-top: 0 !important;
}
.subNav-item {
    /*height: 3.1em;*/
}
.subNavMenu .subNavMenu-item {
    /*height: 3.1em;
    line-height: 3.1em;*/
}
.subNavMenu.subNavMenu--morph    
{
    
}

/* Deal Lists */

.listingProfile .listingProfile-header
{
    padding-top: 5px !important;
    padding-bottom: 5px !important;
}
.listingProfile .listingProfile-header > .page2-center
{
    display: none;
}
.listingProfile-header picture > img
{
    width: 3.5em;
    min-width: 3.5em;
    height: auto;
}

.thread--type-list {
    border-radius: 0;
    border-top: 0;
    margin-top: 0 !important;
}
    .listLayout .js-threadList .js-pagi-top
    {
        margin-top: 0;
        padding-top: 0.2em !important;
        padding-bottom: 0.2em !important;
    }
.listLayout .js-threadList article .thread-header.space--b-3 {
    padding-bottom: 0;
}
.listLayout .js-threadList article .fGrid-last > .space--b-2 {
   /* padding-bottom: 0;*/
    display: none; /* Hide the deal store group */
}
/*.listLayout .js-threadList article.thread--type-list.space--mt-2
    {
        margin-top: .2em;
    }*/
.listLayout .js-threadList .thread--type-list
    {
        margin-top: 0;
        border-radius: 0;
        border-bottom: solid 1px #f1f1f1;
    }
.listLayout .js-threadList .thread--type-list .threadGrid
    {
        margin-top: 0;
        padding-top: 0.2em !important;
        padding-bottom: 0.2em !important;
    } 

.listLayout .js-threadList .thread--type-list .threadGrid .threadTempBadge
{
    position: relative;
}    
.listLayout .js-threadList .thread--type-list .vote-box
    {
        height: 25px;
    }
    
.listLayout .js-threadList .thread--type-list .threadGrid-headerMeta .space--b-2
    {
            padding-bottom: .23em;
    }

 .listLayout .js-threadList .thread--type-list .threadGrid   .threadGrid-body
    {
            padding-top: 0;
    padding-bottom: 0.2em;
    }
    .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title
    {
        display: block;
    }
    .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .thread-title
    {
        padding-right: 8px;
    }
        .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .thread-title .thread-title--list::after
        {
            display: none;
        }
    .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .thread-title,
    .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .overflow--fade
    {
        display: inline !important;
        vertical-align: baseline;
    }
    .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .thread-title *,
    .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .overflow--fade *
    {
        display: inline !important;
        vertical-align: baseline;
    }
    .listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .overflow--fade .thread-divider
    {
        height: auto;
    }
.listLayout .js-threadList .thread--type-list .threadGrid  .threadGrid-title .overflow--fade:after
{
    display: none;
}
    .listLayout .js-threadList .thread--type-list .threadGrid-image .thread-listImgCell
    {
        width: 9em !important;
    }
    
/* Compact Styles */    

.listLayout .js-threadList .thread--type-list.thread--compact
{
	padding-top: 0.3em;
	padding-bottom: 0.3em;
}	
.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-image {
    display: none;
}

.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-headerMeta
{
	display: block;
	-ms-grid-row: 1;
	grid-row-start: 1;
	grid-row-end: 5;
	-ms-grid-row-span: 4;
	-ms-grid-column: 1;
	grid-column: 1;
	padding: 0.6em 0.5em 0 0;
	width: 120px;
}
	.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-headerMeta > .flex
	{
		display: block;
	}
	.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-headerMeta > .flex .meta-ribbon
	{
		padding-left: 0;
		display: block;
	}
	
.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title
{
	display: block;
}
	.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .thread-title
	{
		display: block;
		float: left;
		width: 80%;
	}
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .thread-title  .lineClampInner--2
		{
			float: none;
			width: auto;
			margin-left: 0;
		}

		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .thread-price
		{
			vertical-align: baseline;
			padding-right: 0.2em;
			display: inline-block;
		}
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .flex
		{
			float: left;
			width: 20%;
		}
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .flex div
		{
			display: inline;
		}
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .flex div.thread-divider {
			display: none;
		}
		
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-footerMeta, 
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-dealBtn 
		{
			opacity: 0.8;
		}
		.listLayout .js-threadList .thread--type-list.thread--compact:hover .threadGrid-footerMeta, 
		.listLayout .js-threadList .thread--type-list.thread--compact:hover .threadGrid-dealBtn 
		{
			opacity: 1;
		}
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .threadGrid-footerMeta .ctrl--m, 
		.listLayout .js-threadList .thread--type-list.thread--compact .threadGrid-title .threadGrid-dealBtn .ctrl--m
		{
			height: 30px;
		}
 /*   
.listLayout .js-threadList article.thread--type-list .thread-title,
.listLayout .js-threadList article.thread--type-list .thread-title.box--all-b{
    display: inline;
    vertical-align: middle;
}
.listLayout .js-threadList article.thread--type-list .box--all-i{
    display: inline-block;
    padding-top: 0;
    vertical-align: middle;
}
    
    
.listLayout .js-threadList article.thread--type-list .space--fromW3-l-4,
.listLayout .js-threadList article.thread--type-list .vote--list .vote-infoActionWrap
{
    padding-left: 1em;
}
.listLayout .js-threadList article.thread--type-list .fGrid
{
    padding-bottom: 0.6em;
    padding-top: 0.6em;
}
    
    .listLayout .js-threadList article.thread--type-list .fGrid-right.thread-listImgCell
    {
        margin-left: 1em;
        margin-right: 1em;
        width: 8em;
    }
    .listLayout .js-threadList article.thread--type-list .fGrid-right.thread-listImgCell .btn
    {
        padding: .4em .7em;
    }
    */

    .listLayout .js-threadList article.thread--type-list.thread--expired
    {
        font-size: 85%;
    }
      .listLayout .js-threadList .thread--type-list.thread--expired .threadGrid  .threadGrid-image
       {
           padding-right: 3.84454em;
       }
    
.listLayout .js-threadList article.thread--type-list .vote-down::before, 
.listLayout .js-threadList article.thread--type-list .vote-up::before {

}    
.img--fromW2-square-xl    
{
    width: 3.5em;
    min-width: 3.5em;
    height: 3.5em;
}
/* List page header */

.profileHeader .profileHeader-content,
.profileHeader > .page-content
{
    padding-bottom: 0.6em !important;
    padding-top: 0.6em !important;
}
@media (min-width: 64em) {
    .grpAvatar {
        width: 3.5em;
        min-width: 3.5em;
    }
}

}
