css:
@-moz-document domain("toledo.kuleuven.be") {
/* 
* Toledo dark theme by localhost
* v2.0
*/

/* optional settings */

/* reversed homepage */
@media (min-width: 991px) {
    .flex-container {
        flex-direction: row-reverse;
    }
}
/*--*/


/* vars*/
body {
--color-black: #000000;
--color-white: #ffffff;
--color-grey: #222222;
--color-white-dimmed: #c1c1c1;
--color-border: #6e6e6e;
}

/* body */
body {
    background-color: var(--color-grey);
}

/* nav bar(s) */
nav {
    border: none !important;
}
nav, 
.dropdown-menu>li>a,
.dropdown-menu
{
    background-color: var(--color-black) !important;
}
.navbar-nav>li>a:hover,
.dropdown-menu>li>a,
.tol-no-upcoming-text,
.tol-upcoming a:hover,
.tol-enrollment-label a {
    color: var(--color-white) !important;
}
.navbar-nav>.open>a, 
.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover,
.dropdown-menu>li>a:hover
{
    color: var(--color-white) !important;
    background-color: var(--color-grey) !important;
}

/* text */
.ng-binding {
    color: var(--color-white-dimmed);
}

/* tables */
.panel-heading {
    background-color: var(--color-black) !important;
    color: var(--color-white) !important;
    border: 1px solid var(--color-border) !important;
}
.panel,
.tol-events-list .tol-event
{
    background-color: var(--color-grey) !important;
}
.panel-default {
  border: none !important;
}
.tol-enrollment {
  border: 1px solid var(--color-border) !important;
}
.tol-enrollment-links {
    border-top: none !important;
}
.tol-events-list .tol-event a:hover{
    background-color: inherit;
}
.tol-timeline-label-content h4,
.tol-events-list .tol-event.tol-unread-item,
.tol-events-list .tol-event.tol-unread-item:hover
{
    background-color: var(--color-black) !important;
    color: var(--color-white) !important;
}
.panel-body {
    background-color: var(--color-grey) !important;
    color: var(--color-white) !important;
    border: 1px solid var(--color-border) !important;
}
.tol-timeline-heading h4,
.tol-timeline-label-content,
.tol-timeline-time,
.tol-upcoming-list li {
    background-color: var(--color-black) !important;
}


/* buttons */
.btn, 
.btn-primary,
.input-group-addon {
    background-color: var(--color-black) !important;
    color: var(--color-white) !important;
    border: 1px solid var(--color-border) !important;
}
.btn-primary:hover,
.btn:hover {
    background-color: var(--color-grey) !important;
}
.profile-icon {
    background: none !important;
}

/* messages - notificatios */
.tol-navbar-flyout-content {
  border-bottom: none !important;
  border-top: none !important;
}

.tol-navbar-flyout-content li a:hover {
  background-color: var(--color-grey) !important;
}

.tol-navbar-flyout-messsage {
  background-color: var(--color-black) !important;
  color: var(--color-white) !important;
  border: 1px solid var(--color-grey) !important;
}
.dropdown-menu .divider {
  display: none;
}

/*read*/
.tol-navbar-flyout li .tol-read-item {
  background-color: var(--color-black) !important;
  border: 1px solid var(--color-border) !important;
}

/* unread */
.tol-navbar-flyout li .tol-unread-item {
  background-color: var(--color-black) !important;
  border: 1px solid var(--color-border) !important;
}
.tol-navbar-flyout li .tol-unread-item .tol-title,
.tol-navbar-flyout li .tol-unread-item .tol-date {
  color: var(--color-white) !important;
}

/* footer */
footer {
    display: none !important;
}

/* containers - wrappers */
.wrapper  {
    background-color: var(--color-black) !important;
}
}
