css:
@-moz-document domain("mail.notes.na.collabserv.com") {.thread-people { font-size:13px; }
.seq-msg-row .subject { font-size:16px; margin:10px 0; color: #333; font-weight: 500;}
.seq-msg-row .msg-info { padding-bottom: 14px; padding-top:14px; }
.seq-msg-row > .avatar { width:40px; height:40px; }
.seq-msg-row > .avatar:hover .check-icon { top:12px; left:12px; }
.seq-msg-row > .avatar .initials { font-size: 18px; }
.seq-msg-row.checked > .avatar .check-icon {top:12px; left:12px;}

.dockable.docked-compose { bottom:20px; right:40px; -webkit-box-shadow: 0px 0px 24px 0px rgba(0,0,0,0.4); -moz-box-shadow: 0px 0px 24px 0px rgba(0,0,0,0.4);
box-shadow: 0px 0px 24px 0px rgba(0,0,0,0.4); padding: 30px 40px; max-width: 880px; max-height: 90%; }
.dockable.docked-compose .content { border:0; }

.lotusFormField.socpimSubject, .lotusFormField.socpimTo, .socpimComposeView .socpimComposeDialog .lotusForm2 .lotusFormField.socpimRcptSummary { border:0 !important; padding: 30px 10px 0;  }

.lotusFormField.socpimSubject { padding: 10px 10px 30px; }
.socpimComposeView .socpimComposeDialog .socpimSubject input {font-size:36px; font-weight:600; opacity: 0.7;}
.socpimComposeView .socpimComposeDialog input {height:48px;}
.dockable.docked-compose.has-action-bar .title-and-content > .action-bar {background:#fff; }
.dockable.docked-compose .action.tear-off svg, .dockable.docked-compose .action.close .close-nocircle {fill:#666;}
.socpimComposeView .socpimComposeDialog .socpimToolbarCtrl {border:0;}

action-bar title-bar { padding:0;}

.socpimComposeView .socpimComposeDialog .socpimDialogBody .socpimMailHeader .to-area { padding: 10px 0}

.lotusFormField.socpimTo { padding:0 10px;}
/*.lotusui30 .lotusForm2 { overflow:hidden; }*/

.socpimComposeView .socpimComposeDialog .socpimDialogFooter { border:0; }
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimDiscard, .socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend  { border-radius: 40px; padding: 12px 30px !important; height: auto !important;  font-size: 14px !important; }
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimDiscard:hover::before, .socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimDiscard:focus::before { display:none;}

.threaded .socmail-mailreadview.collapsed .mail-read-view {font-size:14px;}
.threaded .read-view:not(.body-open) {height:64px;}
.read-view button.toggle-btn {top: 24px;}
.threaded .read-view .action-bar { padding-bottom: 36px; padding-top:36px; }
.socpimMailingList .socpimNameBtn {font-size:14px}
.threaded .socmail-mailreadview.collapsed .pim-mailread-recipient {width:200px;}
.read-view .triage-actions {left:210px;}
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend.socpimBtnEnabled {border-radius:50px; }
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend.socpimBtnEnabled:hover::before, .socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend.socpimBtnEnabled:focus::before{border:0 !important;}
.docked-compose .socpimComposeView.showTo.showCc.showBcc .socpimDialogBody .socpimMailHeader, .docked-compose .socpimComposeView.showTo .socpimDialogBody .socpimMailHeader, .tear-off > .socpimComposeView.showTo.showCc.showBcc .socpimDialogBody .socpimMailHeader, .tear-off > .socpimComposeView.showTo .socpimDialogBody .socpimMailHeader {overflow:hidden;}
.ics-scbanner .orglogo {display:none !important;}


/*----- INBOX LIKE EXPERIENCE --- */
/* your are going to need extras JS to use this...*/

.seq-window {top:60px; background-color:#f1f1f1; height:calc(100% - 60px);}
.seq-window .seq-mcv { padding:30px 16%; background-color:#f1f1f1 !important; margin-top:40px;}
.seq-mcv .message-display-settings .threads-state {background:#f1f1f1;}
.seq-mcv .preview-container { min-width:100%;  z-index:2; display:none;}
.preview-panel {width:100%;}
.preview-panel .read-content { max-width: 100%; margin: 30px;}

.seq-mcv .resizable-divider {display:none;}

.condensed.seq-mcv .left-container, .seq-mcv .left-container {min-width:100%;}

.seq-mcv .inbox-container {background-color:transparent;}
.seq-mcv .left-header {border:none; width:100%;}

.messageList.react-message-list {top:10px;}

.seq-msg-row {flex-wrap:wrap-reverse; background: #fff; margin:1px 2px; box-shadow: 0 -1px 0 #e0e0e0, 0 0 2px rgba(0,0,0,.12), 0 2px 4px rgba(0,0,0,.24);    width: 96%; -webkit-user-select: text; -moz-user-select: text; -ms-user-select: text; user-select: text; position: relative; margin: 0 auto; transition: all 0.2s ease-out;}
.seq-msg-row.selected {background:#fff; border:none !important; width:99%; box-shadow: 0 0 6px rgba(0,0,0,.16), 0 6px 12px rgba(0,0,0,.32); margin-top: 60px !important; margin-bottom:60px !important;}
.seq-msg-row::after {border:0;}

.condensed.seq-mcv .seq-msg-row > .avatar {order:1; height:38px; width:38px; top:10px;}
.seq-mcv.condensed .seq-msg-row > .avatar .initials { font-size:16px;}

dl.msg-info { padding-left:80px !important;}
.seq-msg-row.selected dl.msg-info {border-bottom:1px solid #ccc;}
.avatar {position: absolute;}


.seq-msg-row.selected:not(.checked)::before { border:none;}
.seq-msg-row:hover, .seq-msg-row.focused, .seq-msg-row:focus { border:none; border-left: 3px solid #5596e6; z-index:0; padding: 1px 0px; }

.socmail-mailreadview .mail-read-view .pim-mailread-mailcontent {font-size:14px;}

.seq-msg-row .divider, .seq-msg-row .dueDateDivider { background-color:#f1f1f1;}
:not(.focused):not(:hover):not(:focus).seq-msg-row .divider::after, :not(.focused):not(:hover):not(:focus).seq-msg-row .dueDateDivider::after {border:0;}
.messageList .seq-msg-row {margin-top:80px;}
.preview-panel .action-bar {border-bottom:none;}

.seq-header{ position: fixed !important; margin-top: -58px !important; width: 80% !important; background: transparent !important; padding: 0 20px !important;}
.itm-toggler {display:none;}
.ics-scbanner a, .ics-scbanner .people, .ics-scbanner .apps {display:none;}
.outgoing-button {display:none;}


/*----New Message---*/
.set-side-bar { position:fixed; bottom:20px; right:10px; background:transparent !important; border:none; overflow:hidden; flex-wrap: wrap-reverse; width:140px; height:66px;}
.set-side-bar:hover{ height:740px;}
.static-sets { display: -webkit-flex; display: flex;  padding: 0px; margin: 0px; position: relative; right: 8px; flex-wrap: wrap-reverse; width: 70px;  height: 200px;     margin-left: 24px;}
.active-sets {display:flex; flex-wrap:wrap; width: 70px !important; height: 210px; }
.carousel {flex-wrap: wrap; right: 0; margin: 0 8px; height: 560px;}
.people-set {top: initial !important; left: initial !important; flex-direction: column; transform: none !important; position: relative !important;}
.left-end.carousel.sets-overflown .scroll-button.left {display: none; }
.suggested-sets { width: 80px !important; margin-left: 4px !important;}
.set-side-bar .set-actions {display:none;}
.carousel.sets-overflown .scroll-button.right {display:none; }
.carousel .scroll-button {display:none;}

.carousel .scroll-container { height: 90%; position: relative; display: flex; width: 100px !important; align-items: flex-end; flex-direction: column-reverse; margin-top:-60px;}
.suggested-sets::before { height: 48px; display: block; border-right: 0; content: ''; position: absolute;  left: 0; top: 19px;}
.compose-button {width:50px; height:50px; border-radius:50% }
.carousel.sets-overflown .displayed-sets { overflow: hidden; padding: 0; flex-wrap: wrap-reverse; margin-left: -14px;}

/*---- calendar ----*/
.calendar-Container {display:none;}
.seq-window.calendar .seq-mcv {    height: calc(100% - 80px);}

/*

*/
.seq-window .compose-button {  display:none;  width: 54px !important; max-height: 54px !important; border-radius: 50%; font-size: 29px; margin: 10px; padding-bottom: 7px; background-color:#01579B; }
.seq-window .compose-button:hover::before, .seq-window .compose-button:focus::before {border:0 !important;}
.set-base.inbox {display:none;}

.ics-scbanner {height:60px; z-index:1; background-color:#01579B !important;}

}
