.JKKBXFILp09eIN4WugwgY {
	background: url('http://greentechrecyclers.com/wp-content/uploads/2014/10/Dark-Grey-Background.png') no-repeat bottom left fixed!important;
	background-color: #080808;
	opacity: 0.8;
}
.tracklist-row--active .position,
.tracklist-row--active .tracklist-duration,
.tracklist-row--active .tracklist-name,
.tracklist-row--active .tracklist-play-pause,
.navBar-link--active,
.navBar-link--active:focus,
.btn.btn-fg-green,
.connect-device-list-item--active,
.connect-device-list-item--active .connect-device-list-item__device-subtitle,
.connect-device-list-item--active .connect-device-list-item__icon,
.cover-art .cover-art-playback,
.inputBox-input {
	color: rgb(216, 70, 54) !important;
	caret-color: rgb(216, 70, 54) !important;
}
.navBar-link--active:hover {
	color: rgba(255, 255, 255, 1) !important;
}
.Vg0L1vwJXv5JSYn-HUx0C._2rScaV3bBHoj7O47a_JnaH:after,
.control-button--active-dot:after,
._2rScaV3bBHoj7O47a_JnaH:after,
._2rScaV3bBHoj7O47a_JnaH:hover:after {
	background-color: rgb(216, 70, 54) !important;
}
.progress-bar__fg {
	background-color: rgba(255, 255, 255, 0.7);
	border-radius: 2px;
}
._2rScaV3bBHoj7O47a_JnaH {
	color: #fff;
}
.Vg0L1vwJXv5JSYn-HUx0C:hover {
	color: #fff;
}
.progress-bar:hover .progress-bar__fg {
	background-color: #fff;
}
.control-button {
	background-color: transparent;
	border: none;
	color: #fff;
	cursor: pointer;
	width: 32px;
	min-width: 32px;
	height: 32px;
}
.control-button--circled:after {
	content: "";
	position: absolute;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	border-radius: 500px;
}
.control-button:hover {
	color: rgba(255, 255, 255, 0.4);
}
.tracklist-row.active .duration,
.tracklist-row.active .play-pause,
.tracklist-row.active .position,
.tracklist-row.active .track-name {
	color: rgba(255, 255, 255, 0.4)!important;
}
.navlist-itemlink.is-active {
	color: #fff;
}
.btn.btn-green:focus,
.btn.btn-green:hover {
	background-color: rgba(255, 255, 255, 0.4);
}
.btn.btn-green {
	background-color: rgba(255, 255, 255, 0.4);
	padding: 13px 44px;
}
.inputBox-input {
	box-sizing: border-box;
	width: 100%;
	background-color: transparent;
	border: none;
	color: rgb(216, 70, 54);
	font-weight: 700;
	font-size: 62px;
	line-height: 1.2;
	text-shadow: 0 0 0 #fff;
	-webkit-text-fill-color: transparent;
	-moz-text-fill-color: transparent;
	-ms-text-fill-color: transparent;
}
.now-playing-bar-container {
	width: 72% !important;
	background: rgba(10, 10, 10, 0.9);
	position: fixed;
	bottom: 0;
	left: 20%;
	display: flex;
	border-radius: 15px !important;
	margin: 10px !important;
	-webkit-box-orient: vertical;
	-webkit-box-direction: normal;
	-ms-flex-direction: column;
	flex-direction: column;
	height: auto;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
}
.inputBox {
	position: relative;
	background-color: rgba(0, 0, 0, 0.3);
	padding: 1.5em 2em;
	margin-left: -50px;
}
.navBar {
	padding: 24px 24px 10px;
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-orient: vertical;
	-webkit-box-direction: normal;
	-ms-flex-direction: column;
	flex-direction: column;
	height: 100%;
	-webkit-user-select: none;
	background: rgba(0, 0, 0, 0.8);
	opacity: .9;
}
.nav-bar-container {
	width: 180px;
	position: fixed;
	top: 0;
	left: 0;
	height: 100vh;
	z-index: 100;
	background-color: rgba(0, 0, 0, .5);
	overflow: auto;
	min-height: 0!important;
	will-change: transform;
}
.cover-art .cover-art-playback:hover {
	border-color: #fff;
	-webkit-transform: scale(1.2);
	transform: scale(1.2);
}
.cover-art .cover-art-playback {
	background-color: transparent;
	display: none;
	position: absolute;
	z-index: 3;
	width: 62px;
	height: 62px;
	margin-left: -31px;
	margin-top: -31px;
	top: 50%;
	left: 50%;
	color: #e19944;
}
.control-button--circled:after {
	content: "";
	position: absolute;
	top: 0;
	right: 0;
	bottom: 0;
	left: 0;
	border-radius: 500px;
	box-shadow: none;
}
.control-button--circled:hover:after {
	box-shadow: none;
}
._3pZQC_msqsNA7nsxYWWeGJ {
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-align: center;
	-ms-flex-align: center;
	align-items: center;
	background-color: #545454;
	padding-left: 20px;
	padding-right: 20px;
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	height: 50px;
	z-index: 102
}
@media (min-width: 1200px) {
	.col-lg-3 {
		width: 17.7% !important;
		padding: 5px !important;
		margin: 0 10px 0 10px !important;
		border-radius: 5px !important;
	}
}
}
