css:
@-moz-document domain("www.dumpert.nl") {
body{
	background-image: none !important;
    background: #f9fdf7 !important;
}
.header{
	background-image: none !important;
    background: #f9fdf7 !important;
    padding-bottom: 0 !important;
}
#dumphole{
	background-image: none !important;
    background: #f9fdf7 !important;
    height: 125px !important;
}
#dumphole h1{
    color: #fff !important;
    background: #56af24 !important;
	text-shadow: none !important;
    top: 16px !important;
    animation: none !important;
    font-family: "Helvetica Neue Black",Arial,sans-serif !important;
    box-shadow: 0 2px 8px 0 rgba(0,0,0,0.2) !important;
    padding: 16px 0 !important;
}
#dumphole h1:hover{
	color: #56af24 !important;
    background: #fff !important;
}
#dumphole h2{
visibility: hidden !important;
    width: 0 !important;
    height: 0 !important;
}
.header .dump-srv{
	margin-top: 4px !important;
}
.dump-fnd{
	margin-right: 8px !important;
}
.allsprites-sprite, a.dumpthumb span.foto, a.dumpthumb span.video, .header .dump-lgo, .header .dump-srv .nsfw, .header .dump-srv .nsfw.on, .pagination li a span, .pagination li.volgende a span, .dump-mail, .dump-embed, .dump-enhance, .dump-mut a, .dump-mut .dump-bad, .dump-wgt>a>.dump-amt:before, .dump-wgt .dump-tweet>span:first-child:before, .dump-wgt .dump-like>span:first-child:before, .dump-wgt .dump-share>span:first-child:before, #comments article footer .baby, #comments article footer a.modmark, #comments article footer a.modmark:hover, #comments article footer a.modmark.m, #comments article footer a.modmark.m:hover, #comments article footer .nsb, #comments article footer .nsb:hover, .mob-headlines li.gs .logo, .mob-headlines li.dk .logo, .mob-headlines li.uc .logo, .mob-headlines li.ab .logo, .dump-mnu, .mobnav .dump-vid span, .mobnav .dump-img span, .mobnav .dump-top span, .mobnav .dump-themas span, .mobnav .dump-view span, .mobnav .dump-dump span, .dump-soc .dump-app .dump-fb, .dump-soc .dump-app .dump-tw, .dump-soc .dump-app .dump-send, .dump-soc .dump-app .dump-wa{
	background-image: url("http://g2f.nl/0vk0bh3") !important;
}
.header .desknav a{
	color: #56af24 !important;
}
.header .desknav a:hover, .header .desknav em a{
	color: #000 !important;
}
.header .dump-srv>*{
    background-color: white !important;
    border: none !important;
    border-radius: 0 !important;
    box-shadow: 0 1px 2px 0 rgba(0,0,0,0.15) !important;
    padding: 8px 0 6px !important;
}
.dump-fnd input[type="text"], .dump-fnd input[type="search"]{
	font: 14px Arial !important;
}
.dump-fnd input[type="submit"]{
	background: #56af24 !important;
    box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2) !important;
    padding: 6px !important;
    margin-right: 8px !important;
}
.dump-fnd input[type="text"], .dump-fnd input[type="search"], .dump-fnd select{
	color: #000 !important;
}
.dump-fnd select[name="cat"]{
	visibility: hidden !important;
    width: 0 !important;
    height: 0 !important;
}
.header .desknav{
	margin-top: 12px !important;
}
.header .dump-srv .nsfw{
	box-shadow: none !important;
    margin-top: 10px !important;
    padding-bottom: 0 !important;
}
.header .dump-srv{
margin-right: 16px !important;
}
body .dump-main{
	background-image: none !important;
    background: none !important;
}
section#top5, section#autobahn, section#themashighlight, section#gsheaders, section#dkheaders, section#upcoming{
	box-shadow: 0 2px 2px 0 rgba(0,0,0,0.1) !important;
    background: #fff !important;
    margin: 28px 0 0 !important;
}
#upcoming ul{
		box-shadow: 0 2px 2px 0 rgba(0,0,0,0.1) !important;
        background: #fff !important;
}
#glamorama h1, #snheaders h1, #upcoming h1{
	margin-bottom: 0 !important;
}
#upcoming li{
	padding: 8px !important;
}
#top5 .moretop5{
	padding: 10px 10px !important;
}
#top5>h1{
	color: #fff !important;
}
#gsheaders li a, #dkheaders li a, #wkdaily li a, .headlineblock li a{
	color: rgb(132,132,132) !important;
}
#gsheaders li a:hover, #dkheaders li a:hover, #wkdaily li a:hover, .headlineblock li a:hover{
	color: rgb(0,0,0) !important;
}
#gsheaders li, #dkheaders li, #wkdaily li, .headlineblock li{
	padding: 2px !important;
}
#gsheaders ul, #dkheaders ul, #wkdaily ul, .headlineblock ul{
	padding: 8px 0 !important;
}
#top5 .dumpthumb .details h1{
	padding-bottom: 8px !important;
}
#top5 .dumpthumb .details time, #top5 .dumpthumb .details .stats{
    color: rgb(132,132,132) !important;
    padding: 2px !important;
}

.dump-cnt>article{
	background-image: none !important;
    background: #fff !important;
}

#bekijkook a.dumpthumb{
		background-image: none !important;
    background: #fff !important;
    		box-shadow: 0 2px 2px 0 rgba(0,0,0,0.1) !important;
border: none !important;
}
#upcoming li div.title{
	color: rgb(133,133,133) !important;
}
#upcoming li div.title:hover{
	color: #000 !important;
}
#top5 .dumpthumb:nth-child(8){
	visibility: hidden !important;
    height: 0 !important;
    width: 0 !important;
}
#bekijkook h2{
	margin-bottom: 16px !important;
}
body.overview .dump-cnt .dumpthumb{
			background-image: none !important;
    background: #fff !important;
    		box-shadow: 0 2px 2px 0 rgba(0,0,0,0.1) !important;
border: none !important;
    margin-right: 6px !important;
}
.pagination, .contentselector{
	margin: 8px 0 !important;
}
.pagination:nth-child(1){
	visibility: hidden !important;
    width: 0 !important;
    height: 0 !important;
}
.dump-ftr h1{
	background: none !important;
    color: #56af24 !important;
}
.dump-ftr{
	background: none !important;
    padding-top: 32px !important;
}
.dump-ftr input[type="text"], .dump-ftr input[type="email"], .dump-ftr textarea, .dump-ftr .dump-fil>div{
	border: 1px solid rgba(133,133,133,.3) !important;
    border-radius: 0 !important;
}
@media screen and (max-width: 1200px) {
    .header{
	position: fixed !important;
    z-index: 9999 !important;
                                            background: #fff !important;
                                            left: 0 !important;
                                            width: 98% !important;
                                            box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2) !important;
	}
    .dump-cnt{
                                            margin-top: 52px !important;
                                        }
    .mobnav{
                                            background-image: none !important;
                                            background: #fff !important;
                                            width: 98% !important;
                                            margin-left: 0 !important;
                                        }
    #commentscontainer h2{
                                            background: none !important;
                                            color: #000 !important;
                                            padding: 0px 13px !important;
                                        }
    .header .dump-lgo{
                                            top: 0 !important;
                                        }
}
@media screen and (max-width: 639px) {
    .header{
	top: 0 !important;
                                        }
    #bekijkook a.dumpthumb{
                                            width: 100% !important;
                                        }
    .mob-headlines h1{
                                            color: #000 !important;
                                            background: none !important;
                                        }
}
}

@-moz-document domain("comments.dumpert.nl") {

article.comment{
	padding: 8px !important;
    margin-bottom: 0 !important;
    background: #fff !important;
    border: 1px solid rgba(133,133,133,.3) !important;
}
article.comment.topcomment{
	border: 1px solid rgba(102,194,33,.3) !important;
    margin-bottom: 0 !important;
    background: rgba(102,194,33,.15) !important;
    box-shadow: 0 4px 8px 0 rgba(0,0,0,0.1) !important;
}
.subcomments .comment{
	border: none !important;
    border-bottom: 1px dotted rgba(133,133,133,.3) !important;
}
.subcomments{
    border:  1px dotted rgba(102,194,33,.3) !important;
	border-top: none !important;
    background: #fff !important;
}
.subcomments .toggle-show{
	padding: 8px !important;
}
}
