css:
@-moz-document domain("gamerclubgermany.de")
{



.main-bg
{
    background: url(https://i.imgur.com/OwyqQXA.jpg) #1B2838 no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}

.container.boxed {
    background-color: rgba(23, 26, 33, 0.57);
    border: 1px solid rgba(0, 0, 0, 0.19);
    color: #fff;
    border-top: 0;
    padding: 20px;
    min-height: 100%;
}

body > nav > div > div.navbar-header > a > div > img
{
    height: 0 !important;
    width: 0 !important;
    padding-left: 89px !important;
    padding-top: 33px !important;
    background: url(https://i.imgur.com/S4ArEiK.png) no-repeat !important;
}

.navbar-default .navbar-nav>li>a {
    color: #adadad;
    text-transform: uppercase;
}
.container.boxed>.breadcrumbs h1
{
    color: #f90707;
    font-size: 30px;
}

.box-highlight
{
    background: rgba(234, 23, 23, 0.7);
}

#matchingApp > div > div > div > img
{
    height: 0 !important;
    width: 0 !important;
    padding-left: 640px !important;
    padding-top: 360px !important;
    background: url(https://i.imgur.com/1dH7Xv6.gif) no-repeat !important;
}

#bs-example-navbar-collapse-1 > a > img
{
    height: 0 !important;
    width: 0 !important;
    padding-left: 0px !important;
    padding-top: 0px !important;
    background: url(https://i.imgur.com/hYRvuIw.png) no-repeat !important;
}

.wrapper {
    background: #171a21;
}

.footer-v1 .copyright {
    color: #8f98a0;
    background-color: rgb(23, 26, 33);
}
}
