css:
@-moz-document domain("www.rtl-sdr.com")
{
  body
  {
    background: rgba(12, 12, 12, 0.96) !important;
    color: #ccc;
/*here is the color variable for the body text - change this to which you prefer*/;
  }

  input, textarea, select
  {
    background: rgba(0, 0, 0, 0.6);
    border: 1px solid #ccc;
    color: #ddd;
  }

  #nav a
  {
    background-color: #607D8B;
  }

  #site-name a
  {
    color: #ccc;
  }

  #searchsubmit
  {
    background: url(data:image/png;base64,);
    height: 21px;
    margin: 4px;
    opacity: .5;
    width: 20px;
  }

  #footer
  {
    border-top: 12px solid #333;
    padding: 5px 0 0 0;
  }

  #nav .redback a
  {
    background: rgba(196, 41, 27, 0.68);
    color: #ccc;
  }

  #nav .redback a:hover
  {
    background: rgba(255, 87, 34, 0.8);
    color: black;
  }

  #nav a:focus, #nav a:hover
  {
    background-color: rgba(27, 127, 204, .8);
  }

  input[type=submit]:hover, input[type=submit]:focus
  {
    background-color: rgba(255, 87, 34, 0.8);
    color: black;
  }

  label
  {
    color: #9E9E9E;
  }

  h1 a, h2 a, h3 a, h4 a, h5 a, h6 a
  {
    color: #8F8F8F;
    text-decoration: none;
  }

  a:focus, a:hover
  {
    color: #009688;
  }

  p, .content ul, .content ol, .type-post header time, .type-attachment header time
  {
    font-size: 133%;
    line-height: 1.3;
  }

  .fa_slider_simple.default .fa_slide_content div.description p
  {
    font-size: 13px !important;
  }

  .ssbp-wrap .ssbp-list
  {
    font-size: 100% !important;
  }

  input[type=submit]
  {
    background-color: #8c2219;
    color: #ccc;
  }

  h1, h2, h3, h4, h5, h6
  {
    color: #ccc;
  }

  h1 a:hover, h1 a:focus, h2 a:hover, h2 a:focus, h3 a:hover, h3 a:focus, h4 a:hover, h4 a:focus, h5 a:hover, h5 a:focus, h6 a:hover, h6 a:focus
  {
    color: #607D8B;
  }

  strong
  {
    color: #ccc;
    font-weight: bold;
  }

  .wp-polls .Buttons
  {
    border: 1px solid rgba(28, 28, 28, 0.9);
    background-color: #767676;
  }

  blockquote span
  {
    color: #999 !important;
    display: block;
  }

  cite
  {
    color: #ffd966;
  }

  .reply a
  {
    background: -webkit-gradient(linear, left top, left bottom, from(#888), to(#333));
    color: #dbdbdb;
    border: 1px solid #151515;
  }

  .reply a:hover, .reply a:focus
  {
    background: #ededed;
    background: -moz-linear-gradient(top, #fff, #ededed);
    background: -o-linear-gradient(top, #fff, #ededed);
    background: -webkit-gradient(linear, left top, left bottom, from(#888), to(#666));
  }

  .multiplebgs blockquote
  {
    background-image: url(data:image/png;base64,), url(data:image/png;base64,);
    background-repeat: no-repeat;
    background-position: 0px 0px, bottom right;
  }
}
