css:
@-moz-document domain("www.pepper.pl")
  {
* {
    border-radius: 0 !important;
    border-color: #666 !important;
}
body {
    color: #ddd;
}
.btn {
    background-color: #e5152a;
}
.bg--off {
    background-color: #333;
}
.nav {
    color: #fff;
    background-color: #000000;
}
.navMenu-trigger {
    background-color: #222;
}
.subNav,
.subNav--light {
    background-color: #444;
}
.subNavMenu {
    color: #ffffff;
}
.subNav-label,
.subNavMenu-label {
    color: #ffffff;
}
.bg--main,
.thread--type-card,
.thread--type-list,
.widget,
.threadWidget,
.threadWidget--card {
    background-color: #000000;
}
.thread-title {
    color: #ddd;
}
.thread-footer {
    background-color: #1f1f1f;
}
.mute--text2,
.thread--expired.thread--type-card .thread-title,
.thread--expired.thread--type-list .thread-title {
    color: #ffffff;
}
.thread--type-card.thread--expired .thread-header {
    background-color: #4c4c4c;
}
.mute--text,
.notification-item--read,
.userHtml-quote,
.userHtml .userHtml-quote-source {
    color: #bbbbbb !important;
}
.vote-flag.mute--text {
    color: #000000;
}
.vote-down,
.vote-up {
    border: 1px solid #444;
    background-color: #222;
}
.user {
    color: #ffffff;
}
.input,
.inputBox,
.toolbar,
.voucher-code,
.voucher-teaser {
    border: 1px solid #676767;
    background-color: #333;
}
.bg--em {
    background-color: #333;
}
.link,
.staticPageHtml a,
.userHtml a {
    color: #e5152a;
}
.text--color-blue,
.popover-title {
    color: #e5152a;
}
.voteBar.voteBar--sticky-on {
    background-color: #000;
    border-bottom: 1px solid #666;
}
.popover--default .popover-content,
.popover--menu .popover-content,
.notifications-content {
    background-color: #444;
}
.comments-marker {
    background-color: #e5152a;
}
.comments-marker:after {
    border-top: 1em solid #e80000;
}
.text--backgroundPill {
    background-color: #ad0000;
}
.thread-update--edit,
.thread-update:hover,
.vote-bar:before,
.autoSuggest-suggestionsBox--open,
.conversationList-msg,
.notification-item {
    background-color: #333333;
}
.subNavMenu--menu .subNavMenu-list,
.conversationList-msg:not(.conversationList-msg--active):hover,
.notification-item:hover {
    background-color: #444;
}
input,
a.js-cycle-action.box--all-b,
span.overflow--ellipsis.box--all-b.text--b {
    color: #ccc;
}
.navDropDown-link:focus,
.navDropDown-link:hover {
    background-color: #555;
}
.btn.bRad--l-r,
.btn--mode-success,
.toolbar-btn,
.toolbar-btn--expand-increase {
    background-color: #e5152a;
}
.btn--mode-success:focus,
.btn--mode-success:hover,
.toolbar-btn:focus,
.toolbar-btn:hover,
.toolbar-btn--expand-increase:focus,
.toolbar-btn--expand-increase:hover {
    background-color: #bb1021;
}
.cept-comment-link {
    background-color: transparent;
}
.merchantProfile-footer {
    background-color: #333;
}
/* Mroczny Modifications */
.cept-description-container {
	color: #ffffff;   
}
.cept-threadUpdate {
	color: #ffffff;
}
.cept-comment-container {
    color: #ffffff;
}
.subNavMenu-btn {
    background-color: #000000;
}
.comments-indent--left {
    color: #ffffff;
}
.tGrid-cell {
    color: #ffffff;
}
.border--color-borderGrey {
    background-color: #000000;
}
.boxList-item {
    background-color: #000000;
}
.widget-space {
    background-color: #000000;
}
.boxSec-title {
    color: #ffffff;
}
.bg-white {
    background-color: #858585;
}
.merchantProfile-footer {
    background-color: #000000;
}
.hide--toW4 {
    color: #ffffff;
}
.threadWidget-item--type-card {
    background-color: #000000;
}
.popover--border-navDropDownPrimary {
    color: #ffffff;
}
.card-inner {
    background-color: #000000;
} 
.btn--mode-transparent {
    background-color: #000000;
}
.voteBar {
    background-color: #000000;
}
.cept-nav-subheadline {
    	color: #ffffff;
}
}
