css:
@-moz-document domain("soundcloud.com")
{
  .playbackSoundBadge__titleLink:hover
  {
    color: white;
  }

  .playbackSoundBadge__titleLink:focus
  {
    color: #666;
  }

  .commentItem.m-creatorComment
  {
    background: none;
  }

  .inboxItem:hover, .inboxItem:focus, .inboxItem.unread, .inboxItem.active
  {
    background: none;
  }

  .conversation__actions
  {
    background: none;
    box-shadow: 0 1px 0 #333;
  }

  .inbox__item::before
  {
    background: #333;
  }

  .playControls__bg
  {
    background-color: #333 !important;
  }

  .audibleEditForm__form, .editTrackItem__additional
  {
    background: #111;
  }

  .dialog, .dialog__arrow
  {
    background: #111;
    border: none;
  }

  .searchTitle, .searchTitle__text
  {
    background: none;
    border-color: #333;
  }

  .sound__body
  {
    background: #111;
  }

  div.chartTrack:hover
  {
    background: none;
  }

  body#oauth2_authorization.popup #main-wrapper .authorize-text,#authorize-token,#main-wrapper-inner,body#oauth2_authorization.popup #main-wrapper .oauth-connect-form-wrapper #tab-connect,.sessions__formContainer
  {
    color: #666;
    background: #0c0c0c;
  }

  ul.tabs li.current a
  {
    background: #111;
    border-color: #333;
  }

  ul.tabs,.userbadge.context
  {
    background: #0c0c0c;
    border-color: #333;
  }

  .chartsMain_listHeader
  {
    border-bottom-color: #333;
  }

  .linkMenu__list,
    .linkMenu
  {
    background: #0c0c0c;
  }

  .listenLogin
  {
    background-color: #0c0c0c;
  }
    /*.soundTitle__title{
          font-family:Roboto;
          font-weight:400
        }*/
  .trackItem.sc-type-small.sc-type-light
  {
    background: #0c0c0c;
  }

  .trackItem.sc-type-small.sc-type-light:hover
  {
    background: #111;
  }

  .trackItem.active:not(.m-disabled) .trackItem__additional, .trackItem.active .trackItem__additional,
    .trackItem.hover .trackItem__additional,
    .trackItem__additional
  {
        /*background: transparent linear-gradient(to right, transparent, #0c0c0c 17px) repeat scroll 0% 0%*/
    background: none;
  }

  .trackItem.hover:not(.m-disabled) .trackItem__additional
  {
    background: none;
  }

  .loading
  {
    background: transparent url("https://i.imgur.com/88GVdhs.gif") no-repeat scroll center center;
  }

  .paging-eof:before
  {
    background: url("https://i.imgur.com/NbLbdVI.png") center center no-repeat;
  }

  .trackItemWithEdit.hover .trackItemWithEdit__additional,
    .trackItemWithEdit:hover,
    .trackItemWithEdit__additional.sc-media
  {
    background: none;
  }

  .g-upsell-container
  {
    background: none;
    border: none;
  }

  .sc-border-dark
  {
    border-color: #333;
  }

  .trackItemWithEdit.active,
    .trackItemWithEdit.active .trackItemWithEdit__additional,
    .tabs__tabs,
    .g-modal-section,
    .addToPlaylistItem__actions,
    .g-dark,
    .sc-background-light,
    .readMoreBadge__countWrapper
  {
    background: #111;
  }

  .dropbar__content
  {
    color: #ccc;
    background: #0c0c0c;
  }

  .l-fixed-top-one-column > .l-top
  {
    background: #0c0c0c;
  }

  a.playbackSoundBadge__context:focus,
    .playbackSoundBadge__title:focus
  {
    color: #666;
  }

  .commentFormDisabled.visible.small,
    .compactTrackList__moreLink:focus,
    .compactTrackList__moreLink:hover
  {
    background-color: #111;
  }

  .soundTitle__playButton.g-type-shrinkwrap-block
  {
    box-shadow: none;
    background: transparent !important;
  }

  body
  {
    color: #666;
    background: #0c0c0c;
  }

  a.sc-link-dark,
    a.sc-ministats,
    .g-tabs-link,
    .sound__uploadTime,
    .sc-buylink,
    .commentPopover.darkText .commentPopover__body
  {
    color: #666;
  }

  .commentItem:hover .commentItem__controls
  {
    background: none;
  }

  .sc-buylink:hover
  {
    color: #ccc;
  }

  .commentForm,
    .commentForm__wrapper,
    .commentForm__transition,
    .commentForm.m-active,
    .commentForm__input
  {
    color: #ccc;
    background-color: #111 !important;
    border-color: #111 !important;
  }

  .sc-border-box
  {
    color: #666;
    border: none;
  }

  .sc-button
  {
    background-color: #111;
    border-color: #333;
    color: #666;
  }

  .sc-button:hover
  {
    border-color: #666;
    color: #666;
  }

  .uploadMain__chooserContainer
  {
    background-color: #111;
  }

  .uploadMain__foot
  {
    background-color: transparent;
  }

  .modal,
    .modal.modalWhiteout
  {
    background-color: rgba(0, 0, 0, 0.6);
  }

  .modal__modal
  {
    background-color: #111;
  }

  .composeTextfield .textfield__input,
    .recipientChooser .tokenInput__wrapper
  {
    background-color: #000;
    border-color: #333;
  }

  textarea,
    input
  {
    background-color: #111;
  }

  .sc-button:disabled,
    .sc-button:disabled:hover
  {
    color: #333;
    background-color: #111;
    border-color: #333;
  }

  .g-modal-title-h1,
    .composeMessage__bottomWrapper
  {
    border-color: #333;
  }
    /* changed gtabs from bottom border to just border */
  .g-tabs,
    .g-form-section-head
  {
    border-color: #333;
  }

  .sc-border-light,
    .sc-border-light-right,
    .sc-border-light-bottom
  {
    border-color: #333;
  }

  .dashbox__box
  {
    border-color: #333;
  }

  .l-footer,
    .sound__soundActions
  {
    background: #0c0c0c;
  }

  .playControls__inner,
    .compactTrackListItem,
    .compactTrackListItem.clickToPlay.active,
    .compactTrackListItem.clickToPlay:hover,
    .compactTrackListItem.clickToPlay:focus
  {
    background-color: #333;
  }

  .header__logoLink
  {
    background-color: #111;
    transition: background-color 400ms;
  }

  .header__logoLink:hover
  {
    background-color: #f50;
  }

  .compactTrackListItem.clickToPlay.active .compactTrackListItem__additional,
    .compactTrackListItem.clickToPlay:hover .compactTrackListItem__additional,
    .compactTrackListItem.clickToPlay:focus .compactTrackListItem__additional
  {
    background: none;
  }

  .l-listen-wrapper .l-about-rows
  {
    border-color: #333;
  }

  .volume__sliderWrapper,
    .volume.expanded
  {
    background-color: #111;
    border-color: #333;
  }

  .compactTrackListItem__trackTitle
  {
    color: #666;
  }

  .compactTrackListItem__additional
  {
    background: none;
  }

  .soundBadge__additional
  {
    background: transparent linear-gradient(to right, transparent,#0c0c0c 17px) repeat scroll 0% 0%;
  }

  .truncatedAudioInfo.m-overflow.m-collapsed .truncatedAudioInfo__wrapper::after
  {
    background: linear-gradient(transparent,#0c0c0c);
  }

  .listenEngagement
  {
    border: none;
    box-shadow: 0px 1px 0px 0px #333;
  }

  a:hover
  {
    color: #ccc;
  }

  .playbackSoundBadge__title:hover,
    a.sc-link-dark:hover
  {
    color: #f50;
  }

  .sc-toggle
  {
    background: #111;
    border-color: #333;
  }

  .sc-toggle-handle
  {
    background: #333;
  }

  .collection.m-overview .collection__section
  {
    border-color: #333;
  }

  .l-fluid-fixed .playlist > .sound__coverArt
  {
    background-image: none;
  }

  .playlist > .sound__coverArt
  {
    background-image: none;
  }

  .playbackTimeline__duration
  {
    color: #ccc;
  }

  a.sc-link-light
  {
    color: #666;
  }

  .truncatedUserDescription.m-overflow.m-collapsed .truncatedUserDescription__wrapper::after
  {
    background: linear-gradient(transparent,#0c0c0c);
  }

  .sc-tag
  {
    color: #ccc;
    background-color: #111;
    border-width: 2px;
    border-color: #333;
  }

  .volume.expanded .volume__sliderWrapper
  {
    border-color: #333;
  }

  .playbackTimeline__progressBackground
  {
    background-color: #333;
  }

  .commentPopover__username,
    a.commentPopover__username:hover,
    a.commentPopover__username:visited
  {
    color: #f50;
  }

  a.sc-ministats:hover
  {
    color: #ccc;
  }

  .compactTrackListItem.active .compactTrackListItem__content
  {
    background: #0c0c0c;
  }

  .compactTrackListItem.clickToPlay.active
  {
    background: none;
  }

  .gritter-item-wrapper
  {
    color: #666;
    background: #0c0c0c;
    border-color: #333;
    box-shadow: 0px 0px 0px 1px #333;
  }

  .searchTitle__content
  {
    background-color: #0c0c0c;
    border-color: #333;
  }

  a.resultCounts__link:hover
  {
    color: #f50;
  }

  a.sc-link-light:hover
  {
    color: #ccc;
  }

  .g-dark input[type="search"]
  {
    color: #ccc;
    background: #111;
  }

  .sc-input,
    .sc-select
  {
    color: #666;
    background: #111;
    border-color: #666;
  }

  .g-dark input[type="search"]:focus
  {
    background: #333;
  }

  .sc-border-light-top
  {
    border-color: #333;
  }
}
