css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org"){
  
  #wrapper-carousel{
    margin: 0 auto!important;
  }
  .oval {
    background-color: #b4b4b4;
  }
  body{
    background-color: #ddd!important;
  }
  #view,
  #view_inner,
  author-styles{
    background-color: #ddd!important;
    min-width: 900px;
    margin: 0px;
  }
  #iframe{
    min-height: 900px!important;
  }
  .author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(1){
    width: 290px!important;
  }
  .author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(2){
    width: 100px!important;
  }
  .author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(3),
  .author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(4),
  .author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(5),
  .author-styles > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(6){
    width: 70px!important;
  }
  .author-styles tbody tr td a{
    padding-left: 0px!important;
  }
  .numeric-value {
    text-align: center;
  }
  .author-styles > tbody:nth-child(2) > tr > td:nth-child(7) > a:nth-child(1){
    color: blue!important;
  }
  .author-styles > tbody:nth-child(2) > tr > td:nth-child(7) > a:nth-child(2){
    color: red!important;
  }
  .author-styles > tbody:nth-child(2) > tr > td:nth-child(7) > a:nth-child(3){
    color: green!important;
  }
  .statistic table, th, td {
    border: 1px solid #9f9f9f;
  }

  #css, .CodeMirror {
    height: 25em;
  }
  
  #navigation {
    padding: 0px 0px 0px 0px!important;
    position: fixed!important;
    margin-left: -300px!important;
  }
  .navigation_link {
    min-width: 220px;
    padding: 3px 0 3px 0;
    display: flex;
    align-items: center;
  }
  .navigation_category {
    padding-left: 5px!important;
  }
  
  div.styles > div.style_card_long > div.adContainer{
    display: none!important;
  }
  #installBox{
    display: none!important;
  }
  .adContainer,
  .adLong{
    display: none!important;
  }
  #footer {
    display: none!important;
    padding-top: 3px;
    padding-bottom: 3px;
  }
  #bottomLinks {
    margin-top: 0px!important;
  }
}
