css:
@-moz-document domain("freebitco.in"), domain("freedoge.co.in") {
  body {
      background: #f0f0f0 !important;
  }
  .new_border_shadow {
      box-shadow: none;
      background: #f7f7f7;
      padding: 25px 40px;
      width: 80%;
      border-left: 1px solid #e0e0e0;
      border-right: 1px solid #e0e0e0;
  }
  .contain-to-grid, .top-bar, .top-bar-section li a:not(.button) {
      background: #333;
  }
  .top-bar-section ul li.active > a {
      background: #222;
      color: #fff;
  }
  .top-bar-section > ul > .divider, .top-bar-section > ul > [role="separator"] {
      border-left: none;
  }
  .top-bar-section ul li > a {
      font-weight: normal;
      color: #e0e0e0;
      font-size: 12px;
  }
  .top-bar-section li a:not(.button):hover {
      background: #262626;
      color: #fff;
  }
  .top-bar-section ul li.balanceli {
      position: absolute;
      left: 160px;
      background: #222;
      padding-top: 11px;
      font-weight: normal;
      color: #b1ff5c;
      cursor: pointer;
      font-size: 13px;
  }
  .br_5, .br_0_5, .br_0_0_0_5, .br_0_0_5_0 {
      border-radius: 0px;
  }
  .table_header_background {
      text-shadow: 0 0 2px #022602 !important;
      background: #3192f3 !important;
      border-color: #2275c8 !important;
      font-weight: normal !important;
      font-size: 14px !important;
  }
  .lottery_winner_table_box {
      font-size: 14px;
      font-weight: normal;
      background: #f0f0f0;
  }
  .free_play_digits, h1.counter {
      margin-top: 15px !important;
      margin-bottom: 15px !important;
  }
  #free_play_digits span, h1.counter span {
      font-size: 45px;
      color: #fff;
      border-radius: 0px;
      text-shadow: 1px 1px 2px #170101;
  }
  #main_content_ad_left {
      display: none;
  }
  #double_your_btc_bet_hi_button, #double_your_btc_bet_lo_button, #double_your_doge_bet_hi_button, #double_your_doge_bet_lo_button {
      width: 75px;
      box-sizing: border-box;
      border: 0;
      box-shadow: none;
      background: #feba00;
      color: #483704 !important;
      height: 35px;
      padding: 0;
      font-size: 17px;
      font-family: Arial;
      line-height: 35px;
      outline: none;
      font-weight: bold;
      text-shadow: none;
  }
  #double_your_btc_bet_hi_button:disabled, #double_your_btc_bet_lo_button:disabled, #double_your_doge_bet_hi_button:disabled, #double_your_doge_bet_lo_button:disabled {
      background: #aaa;
      color: #444 !important;
  }
  #double_your_btc_bet_hi_button:focus, #double_your_btc_bet_lo_button:focus, #double_your_doge_bet_hi_button:focus, #double_your_doge_bet_lo_button:focus {
      outline: none;
  }

  #double_your_btc_half, #double_your_btc_min, #double_your_btc_2x, #double_your_doge_half, #double_your_doge_min, #double_your_doge_2x {
      font-weight: bold;
      text-shadow: none;
      display: block;
      width: 43px;
      box-sizing: border-box;
      font-size: 11px;
      margin-top: 5px;
      height: 25px;
      line-height: 25px;
      background: #feba00;
      color: #483704 !important;
      border-left: 1px solid #2d7664;
  }
  .double_your_btc_bet_lose_message, .double_your_doge_bet_lose_message, .double_your_btc_bet_win_message, .double_your_doge_bet_win_message {
      font-size: 12px;
      font-family: monospace;
  }
  #double_your_doge_max, #double_your_btc_max, .bold.manual_bet_element.keyboard_container, .manual_auto_bet_button_container {
      display: none !important;
  }
  .alert-box {
      background-color: #fbfbc0 !important;
      color: #333 !important;
      border-color: #c2c297 !important;
      font-weight: normal !important;
  }
  .alert-box .bold {
      font-weight: normal !important;
  }
  .new_button_style {
      font-weight: normal;
      border-radius: 0px;
  }
  .deposit_withdraw_button_style {
      background: #1ba722;
      box-shadow: none;
  }
  .deposit_withdraw_button_style:hover {
      background: #0fbc18;
      box-shadow: none;
  }
  .profile_page_button_style {
      background: #0e67c0;
      box-shadow: none;
  }
  .profile_page_button_style:hover {
      background: #2b85df;
      box-shadow: none;
  }
  .fp_multiplier_bonus_box {
      background: #ffcc77;;
      border: 1px solid #e5b42c;
      border-radius: 0;
      padding: 10px;
      font-size: 14px;
  }
  .free_play_bonus_box_span_large {
      border: 1px solid #7e7e7e;
      background: #d8d8d8;
      font-weight: normal;
  }
  .bold.double_your_btc_link.double_your_btc_link2, .bold.lottery_link {
      font-weight: normal;
  }
  div.row p[style="padding:0 20px;"] {
      font-size: 14px;
      margin-top: 1.8em;
      margin-bottom: 2em;
  }
  .multiplier_header_background {
      background: #142F4D;
      border-radius: 0;
  }
  .max_profit_per_bet_value, .common_background_border, .payout_win_values, .jackpot_first_column, .jackpot_second_column, .jackpot_third_column, .bet_amount_box {
      border: none !important;
      border-radius: 0 !important;
      box-shadow: none;
  }
  .bet_amount_options {
      padding: 0;
      background: transparent;
  }
  .bet_amount_box.bold.bet_amount_options:not(.bet_amount_options_margin) {
      display: none;
  }
  .bet_amount_options_margin {
      margin-right: 0;
  }
  .betting_left_right_section, #double_your_btc_middle_section, .betting_mode_on {
      background: #2d7664 !important;
      border-radius: 0;
  }
  .max_profit_per_bet_header, .free_play_digits, h1.counter, #double_your_btc_right_section center p {
      margin-top: 20px !important;
      font-size: 13px;
  }
  .play_jackpot.jackpot_input_margin {
      margin-bottom: 0px !important;
      margin-top: 9px;
  }
  .jackpot_first_column, .jackpot_second_column, .jackpot_third_column, .jackpot_header_first_column, .jackpot_header_second_column, .jackpot_header_third_column {
      line-height: 2;
      border: none;
      border-bottom: 1px solid rgba(0, 0, 0, 0.1) !important;
      font-weight: normal !important;
      font-size: 13px;
  }
  .jackpot_first_column, .jackpot_second_column, .jackpot_header_first_column, .jackpot_header_second_column {
      border-right: 1px solid rgba(0, 0, 0, 0.1) !important;
  }
  .multiplier_header_background, .max_profit_per_bet_value, .bet_amount_value_input, .win_profit_box_value, .payout_value_input, .win_chance_value_input, #multiplier_enable_sound_div, .manual_bet_gt_lt_p .bold {
      font-weight: normal !important;
  }
  .manual_bet_gt_lt_p {
      text-shadow: 0 1px #000;
      font-size: 13px;
      padding: 10px;
      margin: 10px 0;
      background: rgba(1, 1, 1, 0.15);
  }
  #double_your_btc_right_section, #double_your_btc_middle_section, #double_your_btc_left_section {
      height: 400px !important;
  }
  .row.jackpot_row .jackpot_table_ul {
      margin-bottom: 0 !important;
  }
  .win_profit_box_value {
      width: 149px;
      line-height: 2;
  }
  .max_profit_per_bet_value, .win_profit_value_li, .bet_amount_value_input, .win_chance_value_input, .payout_value_input, .payout_win_values, .payout_win_values {
      border: none !important;
      border-radius: 0 !important;
      font-size: 14px !important;
      text-shadow: 0 1px #000 !important;
      background: rgba(0,0,0,0.3) !important;
      box-shadow: none !important;
      height: 35px !important;
  }
  .payout_win_values {
      padding: 0 !important;
      background: transparent !important;
  }
  .win_profit_box, .payout_win_box, .bet_amount_box.bold.multiplier_header_background, .bold.multiplier_header_background.max_profit_per_bet_header {
      height: 35px;
      font-size: 13px;
      line-height: 1.4;
      border: none;
  }
  .win_profit_box_value {
      line-height: 1.7;
      width: 130px;
  }
  .win_profit_box {
      width: 120px;
  }
  .bet_amount_box.bold.multiplier_header_background {
      line-height: 1.9;
      width: 120px;
  }
  .bold.multiplier_header_background.max_profit_per_bet_header {
      line-height: 1.8;
  }
  .counter span {
      margin: 0px;
  }
}
